css:
@-moz-document domain("adventurelookup.com") {

body {
  background-color: #222;
  color: #ccc;
}

.bg-faded, .footer {
  background-color: #333;
}

a {
  color: #4a83b3;
}

a:focus, a:hover {
  color: #3b6990;
}

.text-muted {
  color: #8f9396 !important;
}


hr {
  border-top: 1px solid rgba(255,255,255,.1);
}


code {
  color: #c39a9c;
  background-color: #444;
}


.badge {
  color: #ccc;
}


.card {
  background-color: #333; 
  box-shadow: 5px 10px 25px rgba(0,0,0,0.2), 0px 0px 3px 0px rgba(0,0,0,0.2);
}

.card-block {
  background-color: #333;
}

.card-header {
  background-color: #333;
}

.list-group-item, .list-group-item.disabled, .list-group-item:disabled {
  background-color: #444;
}

.list-group-item-action {
  color: #ccc;
}

.list-group-item-action:focus, .list-group-item-action:hover {
  color: #ddd;
  background-color: #777;
}

/* 
 * Navbar.
 * 
 * Ideally we'd just change the class to use Bootstrap's navbar-inverse, but 
 * we don't have that luxury with custom stylesheets.
 */
 .navbar-light, .navbar.bg-faded {
  background-color: #333;
}

.navbar-light .navbar-brand,
.navbar-light .navbar-brand:focus,
.navbar-light .navbar-brand:hover,
.navbar-light .navbar-toggler,
.navbar-light .navbar-toggler:focus,
.navbar-light .navbar-toggler:hover {
  color: #ccc;
}

/* The currentactive nav entry */
.navbar-light .navbar-nav .active>.nav-link,
.navbar-light .navbar-nav .nav-link.active,
.navbar-light .navbar-nav .nav-link.open,
.navbar-light .navbar-nav .open>.nav-link {
  color: #ccc;
  background-color: #444;
  border-radius: 4px;
  box-shadow: inset 0px 0px 20px 5px rgba(255, 255, 255, 0.05), 0px 5px 20px -5px rgba(0,0,0,0.2);
}

/* Hover state */
.navbar-light .navbar-nav .nav-link:focus,
.navbar-light .navbar-nav .nav-link:hover {
  color: #ddd;
}

/* Regular nav links */
.navbar-light .navbar-nav .nav-link {
  color: #888;
}


/* 
 * Forms.
 */
.form-control, .selectize-input, .selectize-input.full {
  background-color: #ddd;
}

.form-control {
  transition: background-color 0.5s;
}

.form-control:focus {
  background-color: #eee;
}

.selectize-control.multi .selectize-input>div {
  background-color: #bbb;
}

/*
 * Buttons
 */
 .btn-secondary {
  background-color: #bbb;
}

.btn-secondary:hover, .btn-secondary:focus {
  background-color: #ddd;
}

.btn-primary, .btn-success, .btn-danger, .btn-warning {
  color: #ddd;
}

.btn-primary {
  background-color: #3c75a7;
  border-color: #0057a2;
}

.btn-success {
  background-color: #529452;
  border-color: #169c16;
}


/*
 * Put a slight shadow on the thumbnails in the search results
 */
#search-results .card-block img {
    box-shadow: 2px 5px 30px 0px rgba(0,0,0,0.25);
    border-radius: 5px;
}
}
