@-moz-document domain(1chan.pl) {

html {
    margin: 0;
    padding: 0;
}
body {
    background: white;
    margin: 0;
    padding: 0;
    min-width: 1030px;
    font-family: "Open Sans", "Verdana", sans-serif;
    font-size: 17px;
    color: black;
}
a:link,
a:visited {
    transition: 0.2s;
    color: black;
    text-decoration: none;
}
a:hover {
    transition: 0.2s;
    color: #080880;
    text-decoration: none;
}
a img {
    border: none;
    text-decoration: none;
    max-width: 250px;
}
form {
    margin: 0;
    padding: 0;
}
p {
    margin: 12px 0;
    line-height: 16px;
}
/**
 * g:
 */

a.g-dynamic:link,
a.g-dynamic:visited {
    text-decoration: none;
    border-bottom: 1px dashed #3366cc;
}
a.g-dynamic:hover {
    text-decoration: none;
    border-bottom: 1px solid transparent;
}
a.g-dynamic.m-current:link,
a.g-dynamic.m-current:visited {
    border-bottom: 1px dashed #fff;
    text-decoration: none;
    background: #3366cc;
    color: #fff;
}
a.g-dynamic.m-current:hover {
    text-decoration: none;
    border-bottom: 1px solid transparent;
}
.g-disabled {
    color: #aaa !important;
}
.g-disabled:link,
.g-disabled:visited {
    color: #aaa !important;
}
.g-disabled:hover {
    color: #aaa !important;
}
.g-clearfix:after {
    content: ".";
    display: block;
    clear: both;
    visibility: hidden;
    line-height: 0;
    height: 0;
}
.g-clearfix {
    display: inline-block;
}
html[xmlns] .g-clearfix {
    display: block;
}
* html .g-clearfix {
    height: 1%;
}
.g-green {
    color: #009900;
}
.g-red {
    color: #990000;
}
.g-bold {
    font-weight: bold;
}
.g-hidden {
    display: none;
}
.g-input-error {
    background: #fc7;
    color: #fff;
}
.g-center {
    text-align: center;
}
.g-strike,
a.g-strike {
    text-decoration: line-through;
}
/**
 * ui:
 */

.ui-autocomplete {
    max-height: 250px;
    overflow-y: auto;
    overflow-x: hidden;
    padding-right: 20px;
}
* html .ui-autocomplete {
    height: 100px;
}
.ui-dialog {
    font-size: 0.7em !important;
}
.b-notifiers {
    position: fixed;
    bottom: 5px;
    left: 5px;
    z-index: 255;
}
.b-notifier {
    width: 340px;
    margin-top: 5px;
    background: rgba(0, 0, 0, .75);
    padding: 5px 10px;
    color: #fff;
    box-shadow: 1px 1px 4px #ddd;
    -moz-box-shadow: 1px 1px 4px #ddd;
    border-radius: 0;
    -moz-border-radius: 0;
    text-shadow: 1px 1px 1px #000;
    -moz-text-shadow: 1px 1px 1px #000;
    font-size: 12px;
    cursor: pointer;
    cursor: hand;
}
.b-notifier a {
    color: #ADDFFF;
}
.b-notifier p {
    margin: 0;
    padding: 2px 0;
}
.b-notifier img {
    float: left;
    margin: 2px 5px;
}
/**
 * l:
 */

.l-gesture-wrap {
    z-index: 0;
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
}
.l-wrap {
    z-index: 1;
    position: relative;
    width: 678px;
    margin: 0 auto;
}
.l-content-wrap {
    width: 100%;
    min-height: 500px;
    padding-bottom: 30px;
    border: 1px solid #ddd;
    -webkit-border-radius: 0;
    -moz-border-radius: 0;
    border-radius: 0;
    background-color: white;
}
.l-comments-wrap {
    width: 600px;
    margin: 0 auto;
    border-top: 1px dotted #ccc;
    padding-top: 10px;
}
.l-static-wrap {
    margin: 50px 0 0 0;
}
.l-left-panel-wrap {
    position: absolute;
    top: 150px;
    left: -170px;
    width: 160px;
    padding: 10px 0px;
}
.l-right-panel-wrap {
    position: absolute;
    top: 150px;
    right: -170px;
    width: 160px;
    padding: 10px 0px;
}
.l-footer-wrap {
    color: black;
    position: relative;
    height: 265px;
    padding-top: 10px;
}
.l-footer-wrap.m-mascot-news {
    background: url(https://1chan.pl/img/mascot_end.jpg) top center no-repeat;
}
.l-footer-wrap.m-mascot-alone {
    background: url(https://1chan.pl/img/mascot_keany_end.png) top center no-repeat;
}
.l-footer-wrap.m-mascot-fav {
    background: url(https://1chan.pl/img/mascot_end.jpg) top center no-repeat;
}
/**
 * b, m:
 */

.m-right {
    text-align: right;
}
.m-justify {
    text-align: justify;
}
.b-ajax-loader {
    display: none;
    position: fixed;
    top: 3px;
    left: 3px;
    padding: 0 10px;
    background: #ddd;
    -webkit-border-radius: 0;
    -moz-border-radius: 0;
    border-radius: 0;
    line-height: 2em;
    font-size: 0.7em;
    color: #fff;
}
.b-ajax-loader-error {
    display: none;
    position: fixed;
    top: 3px;
    left: 3px;
    padding: 0 10px;
    background: #FFB975;
    -webkit-border-radius: 0;
    -moz-border-radius: 0;
    border-radius: 0;
    line-height: 2em;
    font-size: 0.7em;
    color: #fff;
}
.b-mobile-button {
    display: none;
}
.b-image-link img {
    float: left;
    margin: 3px;
    max-width: 200px;
    padding-right: 5px;
    vertical-align: top;
}
.b-chat_b-message .b-image-link img {
    float: none;
}
.b-spoiler-text {
    background: #ddd;
    color: #ddd !important;
}
.b-spoiler-text:hover {
    color: #40454b !important;
}
.b-global-message-panel {
    width: 100%;
    line-height: 25px;
    font-size: 0.7em;
    background: #fefefe;
    border-bottom: 1px solid #ddd;
}
.b-global-message-panel img {
    vertical-align: middle;
    margin: 0 3px 0 8px;
}
.b-top-panel {
    color: white;
    width: 100%;
    height: 25px;
    font-size: 0.7em;
    background:
    /*url(/img/mes_bg1.gif)*/
    
    #DC143C;
    /*background: #FFFFFF;*/
    
    text-shadow: none;
    border-left: 1px solid #ddd;
    border-right: 1px solid #ddd;
    border-bottom: 1px solid #ddd;
    -webkit-border-radius: 0;
    -moz-border-radius: 0;
    border-radius: 0;
}
.b-top-panel a{
    color: white;
}
.b-top-panel ul {
    list-style: none;
    padding: 4px 0 0 10px;
    margin: 0;
}
.b-top-panel ul li {
    float: left;
    padding: 0 5px 0 0;
    margin: 0;
}
.b-top-panel ul li a.b-top-panel_b-online-link.m-disactive:link,
.b-top-panel ul li a.b-top-panel_b-online-link.m-disactive:visited,
.b-top-panel ul li a.b-top-panel_b-online-link.m-disactive:hover {
    opacity: .7;
}
.b-top-panel ul li.b-top-panel_m-right {
    float: right;
}
.b-header-block {
    position: relative;
    width: 100%;
    padding: 15px 0;
}
.b-header-block.m-mascot-news {
    background: url(https://1chan.pl/img/img/mascot.jpg) bottom center no-repeat;
}
.b-header-block.m-mascot-fav {
    background: url(https://1chan.pl/img/img/mascot.jpg) top center no-repeat;
}
.b-header-block.m-mascot-alone {
    background: url(https://1chan.pl/img/mascot_keany.png) top center no-repeat;
}
.b-header-block.m-mascot-operate {
    background: url(https://1chan.pl/img/mascot_ougi.png) top center no-repeat;
}
.b-header-block .b-header-block_b-logotype {
    width: 250px;
    height: 80px;
}
.b-header-block .b-header-block_b-sharelink:link,
.b-header-block .b-header-block_b-sharelink:visited,
.b-header-block .b-header-block_b-sharelink:hover {
    background: #FFB975;
    height: 20px;
    border-radius: 0;
    -moz-border-radius: 0;
    -o-border-radius: 0;
    position: absolute;
    bottom: 3px;
    right: 18px;
    color: white;
    font-weight: bold;
    font-size: 11px;
    line-height: 20px;
    padding: 0 6px;
    text-decoration: none;
}
.b-header-block .b-header-block_b-stats {
    display: none;
    position: absolute;
    bottom: 30px;
    left: 395px;
    width: 285px;
    height: 50px;
    background: #fff;
    border: 1px solid #ddd;
    -webkit-border-radius: 0;
    -moz-border-radius: 0;
    border-radius: 0;
}
.b-header-block .b-header-block_b-stats p {
    white-space: nowrap;
    font-size: 0.65em;
    padding: 0 8px;
    color: #aaa;
}
.b-menu-panel {
    min-height: 70px;
    font-size: 0.7em;
    text-align: center;
}
.b-menu-panel .b-menu-panel_b-title {
    clear: both;
    border-bottom: 1px solid #bbb;
}
.b-menu-panel .b-menu-panel_b-title h2 {
    text-align: center;
    font-weight: normal;
    font-size: 1.5em;
    padding: 10px 0 0 0;
    color: black;
}
.b-menu-panel .b-menu-panel_b-links {
    margin: 15px 0;
}
.b-menu-panel .b-menu-panel_b-links ul {
    width: 170px;
    margin: 0;
    padding: 0;
}
.b-menu-panel .b-menu-panel_b-links ul li {
    list-style: none;
    padding: 8px 0;
    border-radius: 0;
    -moz-border-radius: 0;
}
.b-menu-panel .b-menu-panel_b-links ul li img {
    vertical-align: middle;
}
.b-menu-panel .b-menu-panel_b-links ul li:hover {
    background: #f2f2f2;
}
.b-menu-panel .b-menu-panel_b-links ul li.m-active {
    background: none;
}
.b-menu-panel_b-footer {
    display: none;
    text-align: center;
    font-size: 0.8em;
}
.b-links-panel {
    transition: 0.2s;
    min-height: 150px;
    font-size: 0.7em;
    text-align: center;
}
.b-links-panel:hover {
    transition: 0.2s;
    background: #F2F2F2;
    border-radius: 0;
    -moz-border-radius: 0;
}
.b-links-panel .b-links-panel_b-title_b-close {
    font-size: 0.8em;
}
.b-links-panel .b-links-panel_b-title {
    clear: both;
    border-bottom: 1px solid #bbb;
}
.b-links-panel .b-links-panel_b-title h2 {
    text-align: center;
    font-weight: normal;
    font-size: 1.5em;
    padding: 10px 0 0 0;
    color: black;
}
.b-links-panel .b-links-panel_b-links {
    margin: 15px 0;
}
.b-links-panel .b-links-panel_b-links .b-live-entry {
    width: 155px;
    min-height: 0;
    margin: 5px 0;
    padding: 0 5px;
    text-align: left;
}
.b-links-panel .b-links-panel_b-footer {
    font-size: 0.8em;
}
.b-links-panel .b-links-panel_b-hide {
    padding: 10px 0 20px 0;
}
.b-blog-panel {
    width: 100%;
    margin-bottom: 15px;
}
.b-blog-panel ul {
    width: 100%;
    list-style: none;
    padding: 0 8px;
    margin: 10px 0 0 0;
}
.b-blog-panel ul li {
    float: left;
    margin: 0 1px 0 0;
    padding: 0;
    line-height: 40px;
    text-align: center;
    font-size: 0.7em;
    text-shadow: none;
}
.b-blog-panel ul li img {
    vertical-align: middle;
    margin-right: 3px;
}
.b-blog-panel ul li a:link,
.b-blog-panel ul li a:visited,
.b-blog-panel ul li a:hover {
    color: inherit;
}
.b-blog-panel ul li a.g-dynamic:link,
.b-blog-panel ul li a.g-dynamic:visited {
    border-bottom: 1px dashed #999;
}
.b-blog-panel ul li.b-blog-panel_b-add-entry {
    color: white;
    width: 150px;
    background: #DC143C;
    -webkit-border-radius: 0;
    -moz-border-radius: 0;
    border-radius: 0;
}
.b-blog-panel ul li.b-blog-panel_b-favorites {
    width: 180px;
    background:
    /*url(/img/mes_bg1.gif)*/
    
    #FFFFFF;
    /*background: #FFFFFF;*/
    border-right: 1px solid #DDDDDD;
}
.b-blog-panel ul li.b-blog-panel_b-approved {
    width: 180px;
    background:
    /*url(/img/mes_bg1.gif)*/
    
    #FFFFFF;
    /*background: #FFFFFF;*/
}
.b-blog-panel ul li.b-blog-panel_b-all {
    color: white;
    width: 150px;
    background:
    /*url(/img/mes_bg1.gif)*/
    
    #DC143C;
    /*background: #FFFFFF;*/
    
    -webkit-border-radius: 0;
    -moz-border-radius: 0;
    border-radius: 0;
}
.b-blog-panel ul li.b-blog-panel_m-active {
    color: white;
    background: #DC143C;
    text-shadow: none;
}
.b-blog-panel ul li.b-blog-panel_m-active a:link,
.b-blog-panel ul li.b-blog-panel_m-active a:visited,
.b-blog-panel ul li.b-blog-panel_m-active a:hover {
    color: inherit;
    text-decoration: none;
}
.b-blog-panel ul li.b-blog-panel_m-active a.g-dynamic:link,
.b-blog-panel ul li.b-blog-panel_m-active a.g-dynamic:visited {
    border-bottom: 1px solid transparent;
}
.b-blog-panel ul li.b-blog-panel_b-add-entry.b-blog-panel_m-active {
    background: #DC143C;
}
.b-blog-panel .b-blog-panel_b-searchmenu {
    background: #DC143C;
    color: white;
    clear: left;
    float: left;
    padding: 0 15px 0 12px;
    margin: 1px 0 0 8px;
    line-height: 23px;
    font-size: 0.65em;
    text-align: center;
}
.b-blog-panel .b-blog-panel_b-searchmenu img {
    vertical-align: middle;
}
.b-blog-panel .b-blog-panel_b-searchmenu a:link,
.b-blog-panel .b-blog-panel_b-searchmenu a:visited,
.b-blog-panel .b-blog-panel_b-searchmenu a:hover {
    color: inherit;
}
.b-blog-panel .b-blog-panel_b-submenu {
    clear: right;
    float: right;
    width: 220px;
    padding: 0 20px;
    margin: 1px 6px 0 0;
    line-height: 23px;
    font-size: 0.65em;
    text-align: center;
    background: #DC143C;
    color: white;
    -webkit-border-radius: 0;
    -moz-border-radius: 0;
    border-radius: 0;
}
.b-blog-panel .b-blog-panel_b-submenu a{
    color: white;
    text-decoration-line: none;
}
.b-blog-panel .b-blog-panel_b-submenu img {
    vertical-align: middle;
    margin-right: 3px;
}
.b-blog-entry {
    width: 600px;
    margin: 0 auto 10px auto;
    padding: 2px 1px 10px 1px;
}
.b-blog-entry_b-body.fold {
    width: 600px;
    max-height: 400px;
    overflow: auto;
}
.b-blog-entry .b-blog-entry_b-header {
    font-size: 0.9em;
    /*text-indent: -22px;*/
    
    overflow: hidden;
    position: relative;
    left: -22px
}
.b-blog-entry .b-blog-entry_b-header.m-floating {
    z-index: 99;
    font-size: 0.9em;
    text-indent: 490px;
    /*text-indent: -22px;*/
    
    background: #ffffff;
    padding: 7px;
    -webkit-border-radius: 0;
    -moz-border-radius: 0;
    border-radius: 0;
}
.b-blog-entry .b-blog-entry_b-header img {
    vertical-align: middle;
}
.b-blog-entry .b-blog-entry_b-header a:link,
.b-blog-entry .b-blog-entry_b-header a:visited {
    color: #DC143C;
    text-decoration: none;
    vertical-align: middle;
}
.b-blog-entry .b-blog-entry_b-header a:hover {
    text-decoration: none;
}
.b-blog-entry .b-blog-entry_b-header a.b-blog-entry_b-header_m-category {
    color: #3366cc;
}
.b-blog-entry .b-blog-entry_b-body {
    font-size: 0.75em;
    line-height: 1.3em;
    max-height: 300px;
    min-height: 80px;
    min-width: 300px;
    overflow: auto;
    width: 630px;
}
.b-blog-entry .b-blog-entry_b-body h1 {
    font-size: 1.3em;
    font-weight: normal;
}
.b-blog-entry .b-blog-entry_b-body h2 {
    font-size: 1.25em;
    font-weight: normal;
}
.b-blog-entry .b-blog-entry_b-body h3 {
    font-size: 1.2em;
    font-weight: normal;
}
.b-blog-entry .b-blog-entry_b-body h4 {
    font-size: 1.15em;
    font-weight: normal;
}
.b-blog-entry .b-blog-entry_b-body h5 {
    font-size: 1.1em;
    font-weight: normal;
}
.b-blog-entry .b-blog-entry_b-body blockquote,
.b-blog-entry .b-blog-entry_b-body q {
    font-style: italic;
    color: #789922;
}
.b-blog-entry .b-blog-entry_b-info {
    padding: 3px 10px 4px 10px;
    border: 1px solid #ddd;
    -webkit-border-radius: 0;
    -moz-border-radius: 0px;
    border-radius: 0px;
    font-size: 0.65em;
    color: #aaa;
}
.b-blog-entry .b-blog-entry_b-info .b-blog-entry_b-info_b-control {} .b-blog-entry .b-blog-entry_b-info .b-blog-entry_b-info_b-control .js-favorite-button,
.b-blog-entry .b-blog-entry_b-info .b-blog-entry_b-info_b-control .js-rate-up-button,
.b-blog-entry .b-blog-entry_b-info .b-blog-entry_b-info_b-control .js-rate-down-button {
    text-decoration: none;
}
.b-blog-entry .b-blog-entry_b-info .b-blog-entry_b-info_b-control img,
.b-blog-entry .b-blog-entry_b-info .b-blog-entry_b-info_b-control span,
.b-blog-entry .b-blog-entry_b-info .b-blog-entry_b-info_b-control strong {
    vertical-align: middle;
}
.b-blog-entry .b-blog-entry_b-info .b-blog-entry_b-info_b-link {
    float: right;
    vertical-align: middle;
}
.b-blog-entry.m-hide {} .b-blog-entry.m-hide .b-blog-entry_b-header {} .b-blog-entry.m-hide .b-blog-entry_b-header img {
    opacity: .6;
}
.b-blog-entry.m-hide .b-blog-entry_b-header a:link,
.b-blog-entry.m-hide .b-blog-entry_b-header a:visited {
    color: #aaa;
}
.b-blog-entry.m-hide .b-blog-entry_b-header a:hover {
    color: #aaa;
}
.b-blog-entry.m-hide .b-blog-entry_b-body {
    display: none;
}
.b-blog-entry.m-hide .b-blog-entry_b-info {
    display: none;
}
