.b-post-added-notify {
    display: none;
    width: 600px;
    margin: 0 auto 16px auto;
    padding: 0 10px;
    background: #FFB975;
    -webkit-border-radius: 0;
    -moz-border-radius: 0;
    border-radius: 0;
    line-height: 2em;
    font-size: 0.7em;
    color: #fff;
}
.b-post-added-notify a:link,
.b-post-added-notify a:visited,
.b-post-added-notify a:hover {
    color: #fff;
}
.b-comment {
    width: 560px;
    margin: 2px auto;
    padding: 5px 10px 0 10px;
    background: #FFFFFF;
    border: 1px sold black;
    font-size: 0.7em;
}
.b-comment.m-selected {
    border: 1px sold black;
}
.b-comment.m-new {
    background:
    /*url(/img/mes_bg2.gif)*/
    
    #F2F2F2;
    /*background: #e3edfe;*/
}
.b-comment.m-tip {
    z-index: 3;
    background: #F2F2F2;
    border: 1px sold black;
}
.b-comment.m-post-preview {
    background: #fff;
}
.b-comment .b-comment_b-info {} .b-comment .b-comment_b-homeboard {
    float: right;
}
.b-comment .b-comment_b-homeboard img {
    vertical-align: middle;
    opacity: 0.7;
}
.b-comment .b-comment_b-body {
    font-size: 1.1em;
    line-height: 1.3em;
    padding: 0.4em 20px 0.7em 0;
    overflow: auto;
    max-height: 520px;
    border: 1px sold black;
}
.b-comment .b-comment_b-body h1 {
    font-size: 1.3em;
    font-weight: normal;
}
.b-comment .b-comment_b-body h2 {
    font-size: 1.25em;
    font-weight: normal;
}
.b-comment .b-comment_b-body h3 {
    font-size: 1.2em;
