    float: left;
    font-size: 1em;
    line-height: 24px;
}
.b-chat_b-header_b-statistics {
    width: 300px;
    float: right;
    text-align: right;
    font-style: italic;
    font-size: 0.7em;
    line-height: 24px;
}
.b-chat_b-messages {
    width: 100%;
    overflow: auto;
    border: 1px solid #ddd;
    -webkit-border-radius: 0;
    -moz-border-radius: 0;
    border-radius: 0;
    height: 500px;
    margin: 0 0 10px 0;
    padding: 5px 0;
}
.b-chat_b-message {
    padding: 2px 15px;
    font-size: 0.7em;
}
.b-chat_b-message_b-link {} .b-chat_b-message_b-date {
    float: right;
    color: #aaa;
}
.b-chat_b-message_b-body {
    clear: both;
}
.b-chat_b-message_b-body p {
    padding: 2px 0;
    margin: 0;
}
.b-chat_b-message_b-body h1 {
    font-size: 1.3em;
    font-weight: normal;
}
.b-chat_b-message_b-body h2 {
    font-size: 1.25em;
    font-weight: normal;
}
.b-chat_b-message_b-body h3 {
    font-size: 1.2em;
    font-weight: normal;
}
.b-chat_b-message_b-body h4 {
    font-size: 1.15em;
    font-weight: normal;
}
.b-chat_b-message_b-body h5 {
    font-size: 1.1em;
    font-weight: normal;
}
.b-chat_b-message_b-body blockquote {
    padding: 2px 0;
    margin: 0 0 0 10px;
}
.b-chat_b-message_b-body blockquote,
.b-chat_b-message_b-body q {
    font-style: italic;
    color: #789922;
}
.b-chat_b-message.m-info {
    margin: 5px 10px;
    padding: 5px 15px;
    background: #eee;
    -webkit-border-radius: 0;
    -moz-border-radius: 0;
    border-radius: 0;
    border: 1px solid #ddd;
}
.b-chat_b-message.m-error {
    margin: 5px 10px;
    padding: 5px 15px;
    background: #DC143C;
    -webkit-border-radius: 0;
    -moz-border-radius: 0;
    border-radius: 0;
    border: 1px solid #ddd;
}
.b-chat_b-messages.m-log {
    max-height: 2000px;
    overflow: auto;
    height: auto;
}
.b-chat_b-form {} .b-chat_b-form textarea {
    width: 625px;
    clear: both;
    -webkit-border-radius: 0;
    -moz-border-radius: 0;
    border-radius: 0;
    border: 1px solid #ddd;
}
.b-static {
    width: 600px;
    margin: 10px auto 5px auto;
    font-size: 0.75em;
}
.b-static h1 {
    font-weight: normal;
    font-size: 1.7em;
}
.b-static h2 {
    font-weight: normal;
    font-size: 1.5em;
}
.b-static ul {
    list-style: none;
    padding: 0;
    margin: 0 0 20px 15px;
}
.b-static ul li {
    padding: 0;
    margin: 0 0 5px 0;
}
.b-underlinks {
    position: absolute;
    right: 20px;
    font-size: 0.6em;
    padding: 12px 0 0 0;
}
.b-underlinks a:link,
.b-underlinks a:visited,
.b-underlinks a:hover {
    vertical-align: middle;
    color: black;
}
.b-underlinks img {
    vertical-align: middle;
}
.b-footer-imgboards {
    float: left;
    width: 200px;
    padding: 0 0 0 20px;
}
.b-footer-imgboards h2 {
    padding-top: 7px;
    margin-top: 0;
    font-weight: normal;
    font-size: 1.1em;
    color: black;
}
.b-footer-imgboards ul {
    list-style: none;
    margin: 0;
    padding: 0 0 0 15px;
    font-size: 0.7em;
}
.b-footer-imgboards ul li {
    margin-bottom: 7px;
}
.b-footer-imgboards ul img {
    vertical-align: middle;
}
.b-footer-imgboards ul a:link,
.b-footer-imgboards ul a:visited,
.b-footer-imgboards ul a:hover {
    color: black;
}
.b-footer-services {
    float: left;
    width: 200px;
}
.b-footer-services h2 {
    padding-top: 7px;
    margin-top: 0;
    font-weight: normal;
    font-size: 1.1em;
    color: black;
}
.b-footer-services ul {
    list-style: none;
    margin: 0;
    padding: 0 0 0 15px;
    font-size: 0.7em;
}
.b-footer-services ul li {
    margin-bottom: 7px;
}
.b-footer-services ul img {
    vertical-align: middle;
}
.b-footer-services ul a:link,
.b-footer-services ul a:visited,
.b-footer-services ul a:hover {
    color: black;
}
.b-footer-copyrights {
    padding: 37px 0 0 0;
    font-size: 0.65em;
    text-align: center;
    color: black;
}
.b-footer-copyrights a:link,
.b-footer-copyrights a:visited,
.b-footer-copyrights a:hover {
    color: black;
}
.b-mod-toolbar {
    position: absolute;
    z-index: 999;
    background: #fff;
    border: 1px solid #ddd;
    -webkit-border-radius: 0;
    -moz-border-radius: 0;
    border-radius: 0;
    -moz-box-shadow: 0 0 10px #666;
    -webkit-box-shadow: 0 0 10px #666;
    -o-box-shadow: 0 0 10px #666;
    box-shadow: 0 0 10px #666;
    height: 20px;
    padding: 2px;
}


  
}
