css:
@-moz-document domain("io.hsoub.com")
{

.hsoub-header {
   background-color: #E45151;

}


.hsoub-dropdown li{
    background-color: #eeeeee;
    
}
.hsoub-bars {
    background-color: #E45151;
     display: none;
}

.hsoub-logo {
    
    background-color: #E45151;
}

.active {
    
   background-color: #eeeeee;
}

.post-title {
    border-bottom: 1px solid #eeeeee;
}
.post-title h2 {
    line-height: 1.8;
    margin-top: 0;
    font-size: 18px;
    margin-bottom: 10px;
}
.itemsList .listItem {
    background-color: #eeeeee;
    margin: 0 0 5px;
    padding: 1px;
}
#posts_container {
   margin-right: -50px;
    
}


.hsoub-list-item  {
  background-color: #eeeeee;  
}
.hsoub-list-item-content * {
   background-color: #eeeeee;

                                            
}

.hsoub-menu-item {
    background-color: #E45151;
    
}



.hsoub-box {
   background-color: #eeeeee;
}

.hsoub-dropdown-item * {
    background-color: #eeeeee;
    
}


.hsoub-list * {
   background-color: #eeeeee;
}


.hsoub-search {
    display: block;
    height:50px;
    width:700px;
    margin-top:5px;
    background-color: #eeeeee;
    
}

.hsoub-menu-item-link.active .hsoub-sidebar-wrapper {
    display: block;
}

.hsoub-searchbar {
    
    background-color: #E45151;
    height:13%;
    
}

.profileBreif, .singleAsk {
    background-color: #ebebeb;
    margin-top: 20px;
    overflow: hidden;
    box-sizing: border-box;
    float: none;
    width: auto;
}

.profileImg {
    width: 160px;
    background-color: #ebebeb;
    text-align: center;
}

.communityCard {
    background-color: #ebebeb;
    margin: 0px 7px 15px 7px;
    padding: 20px;
    overflow: hidden;
    box-sizing: border-box;
    float: right;
    width: 32.1%;
}

.white-bg {
    background: #eeeeee;
}
.articleCont h1, .articleCont h1 a {
    color: #E45151;
    font-size: 22px;
    line-height: 1.5;
}

.lightBoxCommunity, .community-card {
    background-color: #ebebeb;
    
    
}
.lvl-0, .lvl-4, .lvl-8, .lvl-12, .lvl-16, .lvl-20, .lvl-24 {
    background: #ebebeb;
}

.blockW {
    background-color: #EBEBEB;
    padding: 20px;
    overflow: hidden;
    box-sizing: border-box;
}

.underline {
    background-color: #EBEBEB;
    border-bottom: 1px solid #e4e4e4;
    padding-bottom: 10px;
    margin-bottom: 10px;
    margin-top: 10px;
}
.btn-primary {
    background-color: #E45151;
    border-color: #dd4814;
}
.btn-primary:hover, .btn-primary:focus, .btn-primary.focus, .btn-primary:active, .btn-primary.active, .open>.btn-primary.dropdown-toggle {
    color: #fff;
    background-color: #E45151;
    
}
.rateColumn, .post_vote  {
    display:none;
    
}
.hsoub.hsoub-sidebar-active .hsoub-sidebar {
    -webkit-transform: translateX(0);
    -ms-transform: translateX(0);
    transform: translateX(0);
    -webkit-transform: translate3d(0,0,0);
    transform: translate3d(0,0,0);
    background-color: #E45151;
    color:#FFF;
}
.hsoub-sidebar-menu-item-link {
    background-color: #eeeeee;
    position: relative;
    display: block;
    padding: 14px;
    color: #fff;
    font-size: 14px;
    line-height: 1.4285714;
    white-space: nowrap;
    text-decoration: none;
    cursor: pointer;
}

.hsoub-sidebar-menu {
    background-color: #eeeeee;
   color:#FFF;
}
.hsoub-sidebar-search {
    padding: 12px 16px;
    background-color: #eeeeee;
    border-bottom: 1px solid #e8eaed;
}
.hsoub-sidebar {
    position: fixed;
    top: 0;
    right: 0;
    bottom: 0;
    -webkit-transform: translateX(260px);
    -ms-transform: translateX(260px);
    transform: translateX(260px);
    -webkit-transform: translate3d(260px,0,0);
    transform: translate3d(260px,0,0);
    transition: -webkit-transform .25s;
    transition: transform .25s;
    transition: transform .25s,-webkit-transform .25s;
    overflow: hidden;
    z-index: 2147483647;
    background: #E45151;
    font-family: "Noto Kufi Arabic",Helvetica,Arial,sans-serif;
    font-size: 16px;
    -moz-osx-font-smoothing: auto;
    color:#FFF;
    
}

.nav-tabs {
    border-bottom: 2px solid #eeeeee;
    font-family: "Noto Kufi Arabic", Helvetica, Arial, sans-serif;
    font-size: 14px;
}

.nav-tabs>li.active>a, .nav-tabs>li.active>a:hover, .nav-tabs>li.active>a:focus, .nav-tabs>li>a:hover {
    color: #333333;
    background-color: transparent;
    border-bottom: 2px solid #eeeeee;
    cursor: pointer;
}

.contentWindow hsoub-content {
    background: #eeeeee;
}

.formBg {
    background: #ebebeb;
    padding: 20px;
    margin-bottom: 0px;
}

.modal-content {
    position: relative;
    background-color: #ebebeb;

}

.inf:first-child b{
    float: right;
    display:none;
}


}
