css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.fimfiction.net"), domain("fimfiction.net") {
  
  .story-tags > li a.tag-genre:hover {
    filter: brightness(115%) !important;
  }
  
  .story-tags > li a[data-tag="second-person"] {
    background-color: #02a1db !important;
    border: 1px solid #0289ba !important;
  }
  
  .story-tags > li a[data-tag="adventure"] {
    background-color: #45c950 !important;
    border: 1px solid #3bab44 !important;
  }
  
  .story-tags > li a[data-tag="alternate-universe"] {
    background-color: #888 !important;
    border: 1px solid #747474 !important;
  }
  
  .story-tags > li a[data-tag="anthro"] {
    background-color: #b5695a !important;
    border: 1px solid #9a594d !important;
  }
  
  .story-tags > li a[data-tag="comedy"] {
    background-color: #f59c00 !important;
    border: 1px solid #d08500 !important;
  }
  
  .story-tags > li a[data-tag="crossover"] {
    background-color: #47b8a0 !important;
    border: 1px solid #3c9c88 !important;
  }
  
  .story-tags > li a[data-tag="dark"] {
    background-color: #b93737 !important;
    border: 1px solid #9d2f2f !important;
  }
  
  .story-tags > li a[data-tag="drama"] {
    background-color: #ec50ca !important;
    border: 1px solid #c944ac !important;
  }
  
  .story-tags > li a[data-tag="equestria-girls"] {
    background-color: #4d3281 !important;
    border: 1px solid #412b6e !important;
  }
  
  .story-tags > li a[data-tag="horror"] {
    background-color: #6d232f !important;
    border: 1px solid #5d1e28 !important;
  }
  
  .story-tags > li a[data-tag="human"] {
    background-color: #b5835a !important;
    border: 1px solid #9a6f4d !important;
  }
  
  .story-tags > li a[data-tag="mystery"] {
    background-color: #444 !important;
    border: 1px solid #3a3a3a !important;
  }
  
  .story-tags > li a[data-tag="random"] {
    background-color: #3f74ce !important;
    border: 1px solid #3663af !important;
  }
  
  .story-tags > li a[data-tag="romance"] {
    background-color: #974bff !important;
    border: 1px solid #8040d9 !important;
  }
  
  .story-tags > li a[data-tag="sad"] {
    background-color: #bd42a7 !important;
    border: 1px solid #a1388e !important;
  }
  
  .story-tags > li a[data-tag="scifi"] {
    background-color: #5d63a5 !important;
    border: 1px solid #4f548c !important;
  }
  
  .story-tags > li a[data-tag="slice-of-life"] {
    background-color: #4b86ff !important;
    border: 1px solid #4072d9 !important;
  }
  
  .story-tags > li a[data-tag="thriller"] {
    background-color: #d62b2b !important;
    border: 1px solid #b62525 !important;
  }
  
  .story-tags > li a[data-tag="tragedy"] {
    background-color: #ffb54b !important;
    border: 1px solid #d99a40 !important;
  }
  
  .character_icon:hover {
    background: #adccef !important;
    border-color: #798fa7 !important;
  }
  
}
