@namespace url(http://www.w3.org/1999/xhtml);
/*
Name:Pocket Reading 
Version:0.1
Author: tumuyan
*/

@-moz-document domain("getpocket.com") {
  :root
  {
/*----------------------------- HERE U CAN CHANGE : -------------------------------*/
    --blur: 0px;
    --scales: 1.02;
    --wallpaper: url(http://webbgwall.unaux.com/randomwall.php);
  }
/*----------------------------- DON`T TOUCH BELOW : -------------------------------*/
.reader_content{background: #ffffffaa}
.reading_progress_bar{height:8px !important;background-color: #f66 !important;}
h2.toolbar_reader_title{color:#666 !important;}
.reader_head .sub li *{ color:#333 !important;}
.reader_head .sub {border-bottom: 1px solid #333 !important; color:#333 !important;}
.reader_head .tags span.tag_container  a.tag{background-color: #fff;color:#fff}
.side-nav {    background: #f4f4f6cc !important;}
  nav.nav-bottom{display: none !important;}

  nav.nav-default li {border-top:1px solid #9998;padding-top:15px}
  nav.nav-default li:hover {background-color: transparent}
   nav.nav-default li.nav-default-divider {display:none !important;}
  body:before
  {
    content: "";
    background: center var(--wallpaper);
    filter: blur(var(--blur));
    -o-filter: blur(var(--blur));
    -ms-filter: blur(var(--blur));
    -moz-filter: blur(var(--blur));
    -webkit-filter: blur(var(--blur));
    transform: translate3d(0,0,0) scale(var(--scales));
    -o-transform: translate3d(0,0,0) scale(var(--scales));
    -ms-transform: translate3d(0,0,0) scale(var(--scales));
    -moz-transform: translate3d(0,0,0) scale(var(--scales));
    -webkit-transform: translate3d(0,0,0) scale(var(--scales));
    position: fixed;
    background-size: cover;
    z-index: -1;
    bottom: -5px;
    height: 100vh;
    width: 100vw;
    left: -.5%!important;
  }
}

