css:
@-moz-document domain("www.vdr-portal.de") {
/*    div.box[data-box-identifier="com.woltlab.wcf.RecentActivity"]{
        display: none;
    }*/
    section.box[data-box-identifier="com.woltlab.wbb.BoardTagCloud"] {
        display: none;
    }
    section.box[data-box-identifier="com.woltlab.gallery.TagCloud"] {
        display: none;
    }
    section.box[data-box-identifier="com.woltlab.wcf.Statistics"] {
        display: none;
    }

@media(min-width: 1025px) {
    .pageHeaderContainer {
        padding-top: 32px !important;
        font-size: 12px;
    }
    .pageHeaderPanel > .layoutBoundary {
        padding-bottom: 0;
        padding-top: 0;
    }
    .pageHeaderPanel {
        height: 32px !important;
    }
    .pageHeaderFacade > .layoutBoundary {
        padding-bottom: 0;
        padding-top: 0;
    }
    .pageHeaderFacade > .layoutBoundary img {
        width: 102px !important;
        height: 72px !important;
    }    
    .pageHeaderLogo > a {
        padding: 4px 0;
    }
    .mainMenu .boxMenu > li > a {
        height: 32px !important;
        font-size: 12px;
    }
    .mainMenu .boxMenu .boxMenuDepth1 {
        font-size: 12px;
        padding: 2px 0;
    }
    .interactiveDropdown {
            top: 32px !important;
    }
    .interactiveDropdownHeader {
        padding: 4px 8px;
    }
    .interactiveDropdownHeader .interactiveDropdownTitle {
        font-size: 12px;
    }
    .interactiveDropdownItems .noItems {
        font-size: 14px;
        padding: 8px 4px;
    }
    .interactiveDropdownItems .loading {
        font-size: 12px;
        padding: 8px 4px;
    }
    .interactiveDropdownItems > li {
        padding: 6px 8px;
    }
    .interactiveDropdownItemsContainer {
        font-size: 12px;
    }
    .interactiveDropdownShowAll {
        font-size: 12px;
        padding: 4px 8px;
    }
    .userPanel > ul > li > a {
        height: 32px !important;
    }
    .pageHeaderSearchInputContainer {
        height: 32px;
    }
    .pageHeaderSearchInputContainer .pageHeaderSearchType .button {
        font-size: 12px;
    }
    .pageHeaderContainer .pageHeaderSearch .pageHeaderSearchType > .button {
        height: 32px;
        padding: 4px 8px;
    }
    .pageHeaderContainer .pageHeaderSearch .pageHeaderSearchInput {
        font-size: 12px;
        padding: 4px 8px;
    }
    .pageHeaderSearchInput {
        height: 32px !important;
        font-size: 12px;
    }
    .dropdownMenuContainer {
	    font-size: 12px;
    }
    .dropdownMenu li > a {
        padding: 4px 8px;
    }
    .pageNavigation {
        padding-bottom: 0;
        padding-top: 0;
	}
    .boxesSidebarLeft .box:not(.boxBorderless) {
        padding: 8px;
    }
    .boxesSidebarLeft .box:not(:first-child) {
        margin-top: 16px;
    }
    .boxesSidebarRight .box:not(.boxBorderless) {
        padding: 8px;
    }
    .boxesSidebarRight .box:not(:first-child) {
        margin-top: 16px;
    }
    .boxesContentTop .box:not(:first-child) {
        margin-top: 16px;
    }
    .boxesContentBottom .box:not(:first-child) {
        margin-top: 16px;
    }
    .boxesContentTop:not(:first-child) {
        margin-top: 16px;
    }
    .boxesContentBottom {
        margin-top: 16px;
    }
    .embeddedAttachmentLink, .embeddedImageLink {
        max-width: 62%;
    }
    .htmlContent img, .messageBody > .messageText img, .messageSignature > div img, .redactor-layer img {
        max-width: 62%;
    }
    .codeBox {
        padding: 4px;
    }
    .quoteBox {
        min-height: 88px;
        padding-top: 16px;
        padding-bottom: 8px;
    }
    .spoilerBox .spoilerBoxContent {
        padding: 4px 8px;
    }
    .redactor-toolbar {
        height: 32px !important;
    }
    .redactor-toolbar > li > a {
        height: 32px !important;
        padding: 6px;
    }
    .redactor-layer {
        padding: 4px;
    }
    .messageTabMenuNavigation > ul > li > a {
        font-size: 12px;
        padding: 4px 8px;
    }
    .messageTabMenu:not(.messageTabMenuContent) > .messageTabMenuContent.active {
        font-size: 13px;
    }
    .messageTabMenu .messageTabMenuContent > nav > ul > li:not(:last-child) {
        margin-right: 16px;
    }
    .messageTabMenu .messageTabMenuContent > nav > ul > li > a {
        font-size: 11px;
    }
    .messageTabMenu .messageTabMenuContent > nav {
        margin: 0;
        padding: 0px;
        padding-bottom: 8px;
    }
    .messageTabMenu:not(.messageTabMenuContent) > .messageTabMenuContent.active {
        padding: 8px;
    }
    .messageReduced .messageBody, .messageReduced .messageFooter {
        padding: 0 8px;
    }
    .messageHeader + .messageBody {
        margin-top: 8px;
    }
    .messageReduced .messageHeader {
        padding: 4px 8px;
    }
    .messageList.messageReducedList > li:not(:last-child) {
        padding-bottom: 16px;
    }
    .userProfileUser .contentHeaderDescription {
        margin-top: 8px;
    }
    .paginationBottom {
        margin-top: 16px;
    }
    .paginationTop {
        margin-top: 24px;
    }
    .boxesFooterBoxes .boxContainer {
        padding: 24px 0;
    }
    .boxesFooterBoxes .box {
        padding-left: 6px;
        padding-right: 6px;
    }
    .boxesFooter {
        padding: 8px 0;
    }
    .pageFooterCopyright {
        padding: 8px 0;
    }
    .tabularBoxTitle > header {
       padding: 8px 0;
    }
/* layout */
    .main {
        padding: 24px;
    }
    .content:not(:last-child) {
        flex-basis: calc(100% - 256px);
        max-width: calc(100% - 256px); /* IE fix */
    }
    .content + .sidebar {
        margin-left: 16px;
    }
    .sidebar {
        flex: 0 0 240px;
    }
    .sidebar:first-child {
        margin-right: 16px;
    }
    .sidebar + .content {
        flex-basis: calc(100% - 256px);
        max-width: calc(100% - 256px); /* IE fix */
    }
    .section {
        margin-top: 16px;
    }
    .section:not(.sectionContainerList) > .sectionTitle {
        padding-bottom: 4px;
    }
    .section > .sectionHeader, .section > .sectionTitle {
        margin-bottom: 8px;
    }
    .containerList > li {
        padding: 8px;
    }
    .messageSidebar {
        padding: 8px;
    }
    .messageSidebar + .messageContent {
        flex-basis: calc(100% - 256px);
        margin-left: 16px;
        max-width: calc(100% - 256px);
    }
    .wbbBoardList .wbbBoard {
        padding: 4px 0;
    }
    .wbbBoardList > li.wbbCategory:not(:last-child) {
        margin-bottom: 8px;
    }
/* global */
    .layoutBoundary {
        padding: 0 8px;
        min-width: 1024px;
        max-width: 1856px;
    }
    .tabularListRow {
       padding: 0;
    }
    .userAvatarImage {
        max-width: 64px !important; max-height:64px !important;
        border-radius: 0;
    }
/* colors */
    .pageHeaderContainer {
        background-color:rgba(152, 152, 152, 1);
    }
    .pageNavigation {
        background-color:rgba(228, 228, 228, 1);
    }
    .pageHeaderPanel {
        background-color:rgba(136, 136, 136, 1);
    }
    .mainMenu .boxMenu > li > a {
        background:rgba(112, 112, 112, 1);
    }
    .mainMenu .boxMenu > li.active > a,.mainMenu .boxMenu > li:hover > a {
        background:rgba(84, 84, 84, 1);
    }
    .mainMenu .boxMenu .boxMenuDepth1 {
        background-color:rgba(84, 84, 84, 1);
    }
    .mainMenu .boxMenu .boxMenuDepth1 > li.active > a,.mainMenu .boxMenu .boxMenuDepth1 > li > a:hover {
        background-color:rgba(164, 164, 164, 1);
    }
    .userPanel > ul > li > a {
        background:rgba(112, 112, 112, 1);
    }
    .userPanel > ul > li.dropdownOpen > a,.userPanel > ul > li.open > a,.userPanel > ul > li:hover > a {
        background:rgba(84, 84, 84, 1);
    }
    .pageHeaderSearchInputContainer .pageHeaderSearchType > .button {
        background-color:rgba(112, 112, 112, 1);
    }
    .pageHeaderSearchInputContainer .pageHeaderSearchType > .button:hover,.pageHeaderSearchInputContainer .pageHeaderSearchType.dropdownOpen > .button {
        background:rgba(84, 84, 84, 1);
    }
    .pageHeaderSearchInputContainer .pageHeaderSearchInput {
        background-color:rgba(136, 136, 136, 1);
    }
    .pageHeaderSearchInputContainer .pageHeaderSearchInput:focus,.pageHeaderSearchInputContainer .pageHeaderSearchInput:hover {
        background-color:rgba(128, 128, 128, 1);
    }
    .pageHeaderSearchInputContainer .pageHeaderSearchInputButton {
        background-color:rgba(112, 112, 112, 1);
    }
    .pageHeaderSearchInputContainer .pageHeaderSearchInputButton:hover {
        background:rgba(84, 84, 84, 1);
    }
    .boxesSidebarLeft .box:not(.boxBorderless), .boxesSidebarRight .box:not(.boxBorderless) {
        background-color: rgba(240, 240, 240, 1);
    }
    .messageSidebar {
        background-color:rgba(240, 240, 240, 1);
    }
    .boxesFooterBoxes {
        background-color: rgba(232, 232, 232, 1);
    }
    .boxesFooter {
        background-color: rgba(152, 152, 152, 1);
    }
    .pageFooterCopyright {
        background-color: rgba(128, 128, 128, 1);
    }
    .info {
        background-color: rgba(240, 240, 240, 1);
        border-color: rgba(228, 228, 228, 1);
    }
    .interactiveDropdownHeader {
        background-color: rgba(240, 240, 240, 1);
    }
    .interactiveDropdownShowAll {
        background-color: rgba(240, 240, 240, 1);
    }
    .redactor-toolbar {
        background-color: rgba(136, 136, 136, 1);
    }
    .redactor-toolbar > li > a:hover {
        background-color:rgba(84, 84, 84, 1);
    }
    .pagination > ul > li.active > a,.pagination > ul > li.active > span,.pagination > ul > li > a:hover {
        background-color:rgba(144, 144, 144, 1);
    }
    .dialogContainer > header {
        background-color: rgba(152, 152, 152, 1);
    }
    .dialogContainer > .dialogContent .dialogFormSubmit {
        background-color: rgba(240, 240, 240, 1);
    }
    .messageReduced .messageHeader {
        background-color: rgba(228, 228, 228, 1);
    }
    .tabularBoxTitle > header {
        background-color: rgba(228, 228, 228, 1);
    }
    .badge, a.badge {
        background-color: rgb(112, 112, 112);
        color: rgb(128, 255, 255);
    }
    .balloonTooltip, .redactor-toolbar-tooltip {
        background-color: rgba(84, 84, 84, .8);
        color: rgb(128, 255, 255);
    }
/* fonts */
    .icon16 {
        font-size: 12px;
    }
    .icon24 {
        font-size: 14px;
    }
    .icon32 {
        font-size: 20px;
    }
    .icon48 {
        font-size: 32px;
    }
    .contentHeader .contentTitle,.boxHeadline .contentTitle,.contentHeader > h1,.boxHeadline > h1 {
        font-size: 24px;
    }
    .tabularBoxTitle > header > h1,.tabularBoxTitle > header > h2,.tabularBoxTitle > header > h3 {
        font-size: 15px;
    }
    .tabularListRowHead .tabularListColumns > li {
        font-size: 15px;
    }
    .boxesContentBottom .boxTitle {
        font-size: 20px;
    }
    .containerHeadline > h3 {
        font-size: 15px;
    }
    .messageGroupList .columnSubject > h3 > .messageGroupLink {
        font-size: 15px;
    }
    .messageSidebar .username {
        font-size: 15px;
    }
    .messageList:not(.messageReducedList) > li {
        padding: 16px;
    }
    .messageTitle {
        font-size: 15px;
    }
    .messageBody .messageText {
        font-size: 13px;
    }
    .boxTitle {
        font-size: 15px;
    }
    .boxesFooterBoxes {
        font-size: 12px;
    }
    .boxesFooter {
        font-size: 12px;
    }
    .pageFooterCopyright {
        font-size: 12px;
    }
    .quoteBoxTitle {
        font-size: 15px;
    }
    .redactor-layer {
       font-size: 13px;
    }
    .codeBox > div > .codeBoxHeader > .codeBoxHeadline {
       font-size:15px
    }
    .tabMenu > ul > li > a {
       font-size: 20px;
    }
    .pagination {
        font-size: 12px;
    }
    .recentActivityList {
        font-size: 13px;
    }
}
@media (min-width:769px) {
    .section .sectionTitle {
        font-size: 20px;
    }
}
@media (max-width:768px) {
    .section .sectionTitle {
        font-size: 18px;
    }
}
}
