css:
@-moz-document domain("mountainofpigeons.wordpress.com") {
/* updated 2018-02-01 */

  /* header and body */
  body.custom-background{
    background-color: #222 !important;
  }
  a, a:visited{
    color: #79e;
  }
  a:hover, a:active{
    color: #fff;
  }
  .site-title a, .site-title a:visited, .site-title a:hover, .site-title a:active{
    color: #fff;
  }
  .menu a, .menu a:visited, .menu a:hover, .menu a:active{
    color: #fff;
  }
  .menu li li a, .menu li li a:visited, .menu li li a:hover, .menu li li a:active{
    color: #000;
  }

  /* post area */
  #wrapper{
    background-color: #234;
    color: #9ab;
    border: none;
  }
  h1, h2, h3{
    color: #eee;
  }
  h1{
    font-size: 2.5em;
    margin-bottom: .2em;
  }
  div.post{
    border-bottom: 3px solid #9ab;
  }
  .post-title a, .post-title a:visited{
    color: #79e;
  }
  .post-title a:hover, .post-title a:active{
    color: #fff;
  }
  .post-data a, .post-data a:visited{
    color: #eee;
  }
  .post-data a:hover, .post-data a:active{
    color: #fff;
  }
  .wp-image-2820, .wp-image-2821, .wp-image-2822{
    background-color: #9ab;
  }

  /* widget area */
  .widget-title{
    color: #cadaea;
  }
  .widget-wrapper{
    background-color: #345;
    border: none;
  }
  .widget-wrapper a, .widget-wrapper a:visited{
    color: #8cf;
  }
  .widget-wrapper a:hover, .widget-wrapper a:active{
    color: #fff;
  }

  /* miscellaneous elements */
  del{
    color: #789;
  }
  hr{
    background: #9ab;
  }
/*
  div#actionbar.actnbr-hidden {
    -webkit-transform: translate3d(0,0,0);
    -moz-transform: translate3d(0,0,0);
    -o-transform: translate3d(0,0,0);
    -ms-transform: translate3d(0,0,0);
    transform: translate3d(0,0,0);
  }
  div#actionbar{
    opacity: 0.5;
  }
  div#actionbar:hover{
    opacity: 1.0;
  }
*/
  
  /* footer */
  #footer{
    color: #9ab;
  }
  #footer-wrapper{
    background-color: #234;
  }
  #footer a, #footer a:visited{
    color: #dadada;
  }
  #footer a:hover, #footer a:active{
    color: #fff;
  }

  /* floating footer */
  #infinite-footer .container {
    background: rgba(0, 10, 20, 0.7);
    color: #9ab;
  }
  #infinite-footer .blog-info a, #infinite-footer .blog-info a:visited{
    color: #9ab;
  }
  #infinite-footer .blog-info a:hover, #infinite-footer .blog-info a:active{
    color: #fff;
  }
  #infinite-footer .blog-credits a, #infinite-footer .blog-credits a:visited{
    color: #9ab;
  }
  #infinite-footer .blog-credits a:hover, #infinite-footer .blog-credits a:active{
    color: #fff;
  }

  /* comments */
  .commentlist{
    border-bottom: 1px solid #9ab;
  }
  .commentlist li{
    background-color: #345;
    color: #9ab;
  }
  .commentlist li.alt{
    background-color: #456;
    color: #abc;
  }
  .commentlist li cite {
    color: #fff;
  }
  
  /* blockquotes */
  blockquote{
    background: #456;
    border-left: 4px solid #789;
    color: #cde;
  }
}
