css:
@namespace url(http://www.w3.org/1999/xhtml);

/* General settings applied to all pages in the domain */
@-moz-document domain(chartpark.com), 
               domain(chartpark.com.nyud.net)
{
  /* Globexã€€S&P500å…ˆç‰© */
  iframe[src="http://chartpark.net/ac/sp500.html"] { height: 210px !important; }
  /* Globexã€€ãƒŠã‚¹ãƒ€ãƒƒã‚¯100å…ˆç‰© */
  iframe[src="http://chartpark.net/ac/nq100.html"] { height: 210px !important; }
  
  /* Header Removal */
  div#body > div:first-child > a,
  div#body > div:first-child > h1, 
  div#gsearch
    {display:none !important;}
}

/* Top Page */
@-moz-document url(http://chartpark.com/),
               url(http://chartpark.com.nyud.net/) 
{
  iframe[src="http://chartpark.net/ac/cme-2.html"] { height: 320px !important; }
  iframe[src="http://chartpark.net/ac/sgx.html"]   { height: 150px !important;}
  iframe[src="http://chartpark.net/in/sox.html"]   { height: 275px !important; }
  iframe[src="http://chartpark.net/in/rut.html"]   { height: 275px !important; }
  iframe[src="http://chartpark.net/in/tnx.html"]   { height: 275px !important; }
}
/* iframe in the Top Page */
@-moz-document url(http://chartpark.net/ac/cme-2.html),
               url(http://chartpark.net.nyud.net/ac/cme-2.html)
{
  iframe[src="http://chartpark.net/ac/cme2-2.html"] { height: 260px !important; }
}

/* Chart-Light Page */
@-moz-document url(http://chartpark.com/light.html),
               url(http://chartpark.com.nyud.net/light.html)
{
  iframe[src="http://chartpark.net/day-usd-light.html"] { height: 165px !important; }
  iframe[src="http://chartpark.net/ac/cme-light2.html"] { height: 140px !important; }
}

/* i-Chart */
@-moz-document url(http://chartpark.com/i/),
               url(http://chartpark.com.nyud.net/i/)
{
  /* Dragable Box Height Adjustment */
  div.dragableBoxContent { height:auto !important; }
  /* Dow & Nasdaq */
  iframe[src="dow.html"]  { height: 235px !important; }
  /* Asian Market */
  iframe[src="asia.html"] { height: 240px !important; }
  /* European Market */
  iframe[src="euro.html"] { height: 240px !important; }
  /* SOX index */
  iframe[src="sox.html"]  { height: 220px !important; }
}
/* iframe in the i-Chart */
@-moz-document url(http://chartpark.com/i/dow.html),
               url(http://chartpark.com.nyud.net/i/dow.html)
{
  iframe[src="http://vista-navi.com/dow.html"]    { height: 175px !important;}
  iframe[src="http://vista-navi.com/nasdaq.html"] { height: 175px !important;}
}

/* Chart for daytime */
@-moz-document url(http://chartpark.com/day.html),
               url(http://chartpark.com.nyud.net/day.html)
{
  iframe[src="http://chartpark.net/ac/sgx2.html"]  { height: 240px !important; }
  iframe[src="http://chartpark.net/in/asia2.html"] { height: 465px !important; }

  /* News Layout Adjustment */
  #webooklist * div[class="news-title"]
  {
    width : 250px !important;
    text-align: left !important;
  }
/* moved to common domain rule
  iframe[src="http://chartpark.net/ac/sp500.html"] { height: 210px !important; }
  iframe[src="http://chartpark.net/ac/nq100.html"] { height: 210px !important; }
*/
}

/* Forcused on news */
@-moz-document url(http://chartpark.com/news4.html),
               url(http://chartpark.com.nyud.net/news4.html)
{
  iframe[src="http://chartpark.net/day-usd.html"] { height: 240px !important; }
  #feedControl 
    {
      padding: 0px 20px 10px 15px !important;
      margin: 10px 0px 0px 0px !important ;
    }
  #feedControl2 
    {
      padding: 0px 0px 10px 0px !important;
      margin: 10px 0px 0px 0px !important ;
    }
}

/* FX */
@-moz-document url(http://chartpark.com/forex.html),
               url(http://chartpark.com.nyud.net/forex.html)
{
  /* Graphs of each currency */
  iframe[src="http://chartpark.net/fx/fx16-2.html"] { height: 625px !important; }
  /* Poll */
  iframe[src="http://chartpark.net/poll/fx/2/"] { height: 280px !important; }
  /* Interest Rates */
  iframe[src="http://freeserv.dukascopy.com/interest_rates/?c=1&t_width=180&title_bgcolor=E8F1F0&head_color=000000&head_fsize=11&body_fsize=11&body_bgcolor1=f0f0f0&body_bgcolor2=ffffff&body_color=000000&border_color=d6d6d6&border_size=1&"] { height: 195px !important; }
}

/* Deteiled FX */
@-moz-document url(http://chartpark.com/fx.html),
               url(http://chartpark.com.nyud.net/fx.html)
{
  iframe[src="http://chartpark.net/fx/fx16.html"] { height: 630px !important; }
  img[name="area1"] { margin-bottom: 0px !important; }
}

/* Korean won */
@-moz-document url(http://chartpark.com/fx-krw.html),
               url(http://chartpark.com.nyud.net/fx-krw.html)
{
  img[style*="margin-bottom: -100px;"][src^="http://data.moneycentral.msn.com/scripts/"]
    { margin-bottom:0px !important;}
}

/* World */
@-moz-document url(http://chartpark.com/world.html),
               url(http://chartpark.com.nyud.net/world.html)
{
  iframe[src="http://chartpark.com/world-in4.html"] { height: 1120px !important; }
}

@-moz-document url(http://chartpark.com/world-in4.html),
               url(http://chartpark.com.nyud.net/world-in4.html)
{
  iframe[src="http://chartpark.net/ac/wti-world.html"] { height: 165px !important; }
}

/* Comparison of World Indexes */
@-moz-document url(http://chartpark.com/world-hikaku.html),
               url(http://chartpark.com.nyud.net/world-hikaku.html)
{
  /* Fix first chart overflows when any term except 5d is selected */
  img#idImg { margin-bottom:auto !important; }
  img#idImg[src*="N225&t=5d"] { margin-bottom:-140px !important; }
}

/* Chicago */
@-moz-document url(http://chartpark.com/cme.html),
               url(http://chartpark.com.nyud.net/cme.html)
{
  iframe[src="http://chartpark.net/ac/cme.html"] { height: 340px !important; }
  iframe[src="http://chartpark.net/ac/cme3.html"] { height: 310px !important; }

  /* News Layout Adjustment */
  #webooklist * div[class="news-title"]
  {
    width : 250px !important;
    text-align: left !important;
  }

  /* Questionnaire */
  iframe[src*="/poll/"] { width:175px !important; height:280px !important; }

/* moved to common domain rule
  iframe[src="http://chartpark.net/ac/sp500.html"] { height: 210px !important; }
  iframe[src="http://chartpark.net/ac/nq100.html"] { height: 210px !important; }
*/
}

/* DOW */
@-moz-document url(http://chartpark.com/dow.html),
               url(http://chartpark.com.nyud.net/dow.html)
{
  iframe[src="http://chartpark.net/ac/dj.html"] { height: 210px !important; }
  iframe[src="http://chartpark.net/in/dow-in.html"] { height: 1335px !important; }
}

/* VIX */
@-moz-document url(http://chartpark.com/vix.html),
               url(http://chartpark.com.nyud.net/vix.html)
{
  /* Fix first chart overflows when 5y or more term is selected */
  img#idImg { margin-bottom: -125px !important; }
  img#idImg[src^="http://ichart.finance.yahoo.com/z?s=^VIX&t=5y"],
  img#idImg[src^="http://ichart.finance.yahoo.com/z?s=^VIX&t=my"] 
    { margin-bottom:auto !important; }
}

/* WTI */
@-moz-document url(http://chartpark.com/wti.html),
               url(http://chartpark.com.nyud.net/wti.html)
{
  iframe[src="http://chartpark.net/ac/wti-10m.html"] { height: 190px !important; }
  iframe[src="http://chartpark.net/ac/wti-3m2.html"] { height: 580px !important; }

  /* Questionnaire */
  iframe[src*="/poll/"] { width:200px !important; height:190px !important; }
}
