css:
@-moz-document url-prefix("https://www.facebook.com/plugins/page.php"), regexp(".*myanimelist\\.net/(?!animelist|mangalist|ownlist/style/theme).*") {
  body
  {
    margin: 0;
    text-align: center;
    font-family: Segoe UI !important;
    font-size: 11px;
    background: #222 repeat !important;
  }

  body:before
  {
    background: #333 !important;
    content: "" !important;
    width: 100% !important;
    height: 50px !important;
    top: 0px !important;
    left: 0 !important;
    position: absolute !important;
    z-index: -1 !important;
    border-bottom: 2px solid #222 !important;
  }

  body:after
  {
    background: url("http://i.imgur.com/fGWN1mS.jpg")!important;
    background-color: #222 !important;
    background-repeat: no-repeat !important;
    background-position: center 25% !important;
    background-size: cover !important;
    -moz-background-size: cover !important;
    -webkit-background-size: cover !important;
    content: "" !important;
    width: 100% !important;
    height: 100% !important;
    top: 0 !important;
    left: 0 !important;
    position: fixed !important;
    z-index: -2 !important;
  }

  body.ownlist_anime_update
  {
    padding-top: 165px !important;
  }

  a, a:hover,
.auto-recommendations .item .title,
#widget-seasonal-video .widget-slide .link .title
  {
    text-decoration: none !important;
    -webkit-transition: color 0.3s;
    -moz-transition: color 0.3s;
    transition: color 0.3s;
  }

  a
  {
    color: #ccc !important;
  }

  a:hover,
.auto-recommendations .item .title:hover,
#widget-seasonal-video .widget-slide .link .title:hover
  {
    color: #658FF3 !important;
  }

  .inputtext, select
  {
    color: #ccc !important;
    background-color: rgba(0,0,0,.5) !important;
    border-color: #777 !important;
    border-radius: 3px !important;
    -moz-border-radius: 3px !important;
    -webkit-border-radius: 3px !important;
  }

  .page-common .lightLink, .page-common .lightLink:link, .page-common .lightLink:visited, .page-common .lightLink:active
  {
    color: #777 !important;
  }

  .page-common .lightLink:hover
  {
    color: #708dd3 !important;   
  }
  .user-status-block .btn-user-status-add-list
  {
    background: #333 !important;
    padding: 6px 8px 7px 19px !important;
    border: 1px solid #777 !important;
    color: #ccc !important;
    font-size: 12px !important;
    text-align: center !important;
    cursor: pointer !important;
    border-radius: 3px !important;
    -moz-border-radius: 3px !important;
    -webkit-border-radius: 3px !important;
    -webkit-transition: color 0.5s, background 0.5s;
    -moz-transition: color 0.5s, background 0.5s;
    transition: color 0.5s, background 0.5s;
  }

  .user-status-block .btn-user-status-add-list:hover
  {
    color: #333 !important;
    background: #ccc !important;
    -webkit-transition: color 0.5s, background 0.5s;
    -moz-transition: color 0.5s, background 0.5s;
    transition: color 0.5s, background 0.5s;
  }

  .forum .forum-search-side input[type="submit"],
.ownlist_manga_update #advanced-button .toggle-button
  {
    background: #333 !important;
    padding: 0px 0px 0px 0px !important;
    border: 1px solid #777 !important;
    color: #ccc !important;
    text-transform: lowercase !important;
    font-size: 12px !important;
    text-align: center !important;
    cursor: pointer !important;
    border-radius: 3px !important;
    -moz-border-radius: 3px !important;
    -webkit-border-radius: 3px !important;
    -webkit-transition: color 0.5s, background 0.5s;
    -moz-transition: color 0.5s, background 0.5s;
    transition: color 0.5s, background 0.5s;
  }

  input[type="button"],
input[type="submit"],
input[type="reset"],
.page-common .inputButton.btn-middle,
.page-common .button_form,
.page-common a.button_add.button_add,
.page-common a.button_edit.button_edit,
.btn-rect-grey1,
.page-forum .btn-forum,
.club-list .btn-club-join,
.page-common .pagination .link-blue-box
  {
    background: #333 !important;
    padding: 1px 7px 2px 7px !important;
    border: 1px solid #777 !important;
    color: #ccc !important;
    text-transform: lowercase !important;
    font-size: 12px !important;
    text-align: center !important;
    cursor: pointer !important;
    border-radius: 3px !important;
    -moz-border-radius: 3px !important;
    -webkit-border-radius: 3px !important;
    -webkit-transition: color 0.5s, background 0.5s;
    -moz-transition: color 0.5s, background 0.5s;
    transition: color 0.5s, background 0.5s;
  }

  input[type="button"]:hover,
input[type="submit"]:hover,
input[type="reset"]:hover,
.page-common .inputButton.btn-middle:hover,
.page-common .button_form:hover,
.page-common a.button_add.button_add:hover,
.page-common a.button_edit.button_edit:hover,
.btn-rect-grey1:hover,
.page-forum .btn-forum:hover,
.forum .forum-search-side input[type="submit"]:hover,
.ownlist_manga_update #advanced-button .toggle-button:hover,
.club-list .btn-club-join:hover,
.page-common .pagination .link-blue-box:hover
  {
    color: #333 !important;
    background: #ccc !important;
    -webkit-transition: color 0.5s, background 0.5s;
    -moz-transition: color 0.5s, background 0.5s;
    transition: color 0.5s, background 0.5s;
  }

  .page-common .textarea
  {
    background-color: rgba(0,0,0,.5) !important;
    width: 100% !important;
    padding: 6px !important;
    border-color: #777 !important;
    box-sizing: border-box !important;
    -moz-box-sizing: border-box !important;
    -webkit-box-sizing: border-box !important;
    border-radius: 3px !important;
    -moz-border-radius: 3px !important;
    -webkit-border-radius: 3px !important;
  }
/*-------Button---------*/
/*-------Complete-------*/

  .top-ranking-table .status .btn-addEdit-large.btn-anime-watch-status.completed,
.user-status-block .form-user-status[data-class="completed"]
  {
    color: #ccc !important;
    background: #2e51a2 !important;      
    padding: 1px 7px 2px 7px !important;
    border: 1px solid #2e51a2 !important;
    text-transform: uppercase !important;
    font-size: 12px !important; 
    text-align: center !important;
    cursor: pointer !important;
    border-radius: 3px !important;
    -moz-border-radius: 3px !important;
    -webkit-border-radius: 3px !important;
    -webkit-transition: color 0.5s, background 0.5s;
    -moz-transition: color 0.5s, background 0.5s;
    transition: color 0.5s, background 0.5s;    
  }

    .top-ranking-table .status .btn-addEdit-large.btn-anime-watch-status.completed:hover,
.user-status-block .form-user-status[data-class="completed"]:hover
  {
    color: #2e51a2 !important;
    background: #ccc !important;
    -webkit-transition: color 0.5s, background 0.5s;
    -moz-transition: color 0.5s, background 0.5s;
    transition: color 0.5s, background 0.5s;
  }

/*---------Reading-------*/
 
  .top-ranking-table .status .btn-addEdit-large.btn-anime-watch-status.reading, 
.top-ranking-table .status .btn-addEdit-large.btn-anime-watch-status.watching,
.user-status-block .form-user-status[data-class="watching"],
.user-status-block .form-user-status[data-class="reading"]
  {
    background: #333 !important;
    padding: 1px 7px 2px 7px !important;
    border: 1px solid #2db039 !important;
    color: #2db039 !important;
    text-transform: uppercase !important;
    font-size: 12px !important;
    cursor: pointer !important;
    text-align: center !important;
    border-radius: 3px !important;
    -moz-border-radius: 3px !important;
    -webkit-border-radius: 3px !important;
    -webkit-transition: color 0.5s, background 0.5s;
    -moz-transition: color 0.5s, background 0.5s;
    transition: color 0.5s, background 0.5s;    
  }

 .top-ranking-table .status .btn-addEdit-large.btn-anime-watch-status.reading:hover, 
.top-ranking-table .status .btn-addEdit-large.btn-anime-watch-status.watching:hover,
.user-status-block .form-user-status[data-class="watching"]:hover,
.user-status-block .form-user-status[data-class="reading"]:hover
  {
    color: #ccc !important;
    background: #2db039 !important;
    -webkit-transition: color 0.5s, background 0.5s;
    -moz-transition: color 0.5s, background 0.5s;
    transition: color 0.5s, background 0.5s;
  }

/*---------Add to List-----------*/

  .top-ranking-table .status .btn-addEdit-large.btn-anime-watch-status.notinmylist,
.top-ranking-table .status .btn-addEdit-large.btn-anime-watch-status.plantoread, 
.top-ranking-table .status .btn-addEdit-large.btn-anime-watch-status.plantowatch
  {
    background: #333 !important;
    padding: 1px 7px 2px 7px !important;
    border: 1px solid #777 !important;
    color: #ccc !important;
    text-transform: uppercase !important;
    font-size: 12px !important;
    cursor: pointer !important;
    text-align: center !important;
    border-radius: 3px !important;
    -moz-border-radius: 3px !important;
    -webkit-border-radius: 3px !important;
    -webkit-transition: color 0.5s, background 0.5s;
    -moz-transition: color 0.5s, background 0.5s;
    transition: color 0.5s, background 0.5s;    
  }

    .top-ranking-table .status .btn-addEdit-large.btn-anime-watch-status.notinmylist:hover,
.top-ranking-table .status .btn-addEdit-large.btn-anime-watch-status.plantoread:hover, 
.top-ranking-table .status .btn-addEdit-large.btn-anime-watch-status.plantowatch:hover
  {
    color: #333 !important;
    background: #ccc !important;
    -webkit-transition: color 0.5s, background 0.5s;
    -moz-transition: color 0.5s, background 0.5s;
    transition: color 0.5s, background 0.5s;
  }

/*---------On Hold--------*/

  .top-ranking-table .status .btn-addEdit-large.btn-anime-watch-status.on-hold,
.user-status-block .form-user-status[data-class="on-hold"]
  {
    background: #333 !important;
    padding: 1px 7px 2px 7px !important;
    border: 1px solid #e7b715 !important;
    color: #e7b715 !important;
    text-transform: uppercase !important;
    font-size: 12px !important;
    cursor: pointer !important;
    text-align: center !important;
    border-radius: 3px !important;
    -moz-border-radius: 3px !important;
    -webkit-border-radius: 3px !important;
    -webkit-transition: color 0.5s, background 0.5s;
    -moz-transition: color 0.5s, background 0.5s;
    transition: color 0.5s, background 0.5s;    
  }

  .top-ranking-table .status .btn-addEdit-large.btn-anime-watch-status.on-hold:hover,
.user-status-block .form-user-status[data-class="on-hold"]:hover
  {
    color: #333 !important;
    background: #e7b715 !important;
    -webkit-transition: color 0.5s, background 0.5s;
    -moz-transition: color 0.5s, background 0.5s;
    transition: color 0.5s, background 0.5s;
  }

/*----------Dropped----------*/

  .top-ranking-table .status .btn-addEdit-large.btn-anime-watch-status.dropped,
.user-status-block .form-user-status[data-class="dropped"]
  {
    background: #333 !important;
    padding: 1px 7px 2px 7px !important;
    border: 1px solid #a12f31 !important;
    color: #a12f31 !important;
    text-transform: uppercase !important;
    font-size: 12px !important;
    cursor: pointer !important;
    text-align: center !important;
    border-radius: 3px !important;
    -moz-border-radius: 3px !important;
    -webkit-border-radius: 3px !important;
    -webkit-transition: color 0.5s, background 0.5s;
    -moz-transition: color 0.5s, background 0.5s;
    transition: color 0.5s, background 0.5s;    
  }
  .top-ranking-table .status .btn-addEdit-large.btn-anime-watch-status.dropped:hover,
.user-status-block .form-user-status[data-class="dropped"]:hover
  {
    color: #ccc !important;
    background: #a12f31 !important;
    -webkit-transition: color 0.5s, background 0.5s;
    -moz-transition: color 0.5s, background 0.5s;
    transition: color 0.5s, background 0.5s;
  }

/* Ads ------------------------------------------*/

  .left_bottom_ad, .page-common .side-ad, .side-ad,
#content ~ .mauto.clearfix.pt24[style="width:760px;"] .fl-l,
#content ~ .mauto.clearfix.pt24[style="width:760px;"] .fl-r,
#content .container-right ._unit[style="width:300px; height:250px; display: block !important;"],
#content .js-scrollfix-bottom-rel div[style="padding: 20px 0 20px 40px; float: left; position: relative; z-index: 1;"] ._unit,
#content .js-scrollfix-bottom-rel div[style="padding: 20px 40px 20px 0; float: right; position: relative;"] ._unit,
#content .js-scrollfix-bottom-rel div[style="padding: 20px 0 20px 40px; float: left; position: relative; z-index: 1;"],
#content .js-scrollfix-bottom-rel div[style="padding: 20px 40px 20px 0; float: right; position: relative;"],
#content .js-scrollfix-bottom-rel div[style="padding: 20px 40px;display: inline-block;"] ._unit,
#content .js-scrollfix-bottom-rel div[style="padding: 20px 0px;display: inline-block;"] ._unit,
#content .js-scrollfix-bottom-rel div[style="padding: 20px 40px;display: inline-block;"],
#content .js-scrollfix-bottom-rel div[style="padding: 20px 0px;display: inline-block;"],
#content .right-column .widget .widget.right_middle_ad.right,
#ad-skin-bg-left, #myanimelist ._unit table,
#myanimelist ._unit
  {
    display: none !important;
    height: 0px !important;
    width: 0px !important;
  }

/* Iframe Quick Add ------------------------------------------*/

  .quickadd-content #searchResults
  {
    background: #333 !important;
    color: #ccc !important;
    padding: 10px !important;
    border-radius: 3px !important;
    -moz-border-radius: 3px !important;
    -webkit-border-radius: 3px !important;
    box-sizing: border-box !important;
    -moz-box-sizing: border-box !important;
    -webkit-box-sizing: border-box !important;
  }

/* Header --------------------------------------------*/

  #headerSmall
  {
    background: url("http://i.imgur.com/j3h08DC.png") no-repeat !important;
    padding-top: 0px !important;
    padding-bottom: 214px !important;
    background-position: 0px 15px !important;
    z-index: 1 !important;
  }

  .page-common #headerSmall>a.link-mal-logo
  {
    padding-top: 10px !important;
    width: 60px !important;
    height: 30px !important;
  }

  #headerSmall a.banner-header-anime-straming
  {
    display: none !important;
  }

  #header-menu
  {
    top: 0px !important;
    height: 48px !important;
    background: #2B2B2B !important;
  }

  .header-menu-unit
  {
    padding-top: 12px !important;
  }

  .header-profile
  {
    background: none !important;
    padding-top: 0px !important;
  }

  a.profile-image
  {
    border-right: 1px solid #649292 !important;
    border-radius: 3px 0px 0px 3px !important;
    -moz-border-radius: 3px 0px 0px 3px !important;
    -webkit-border-radius: 3px 0px 0px 3px !important;
  }

  .header-profile .profile-name
  {
    color: #ccc !important;
    font-weight: normal !important;
    padding: 0px 16px 0px 10px !important;
  }

  .page-common .header-profile-button
  {
    height: 50px !important;
    width: 50px !important;
    background-color: #000 !important;
    box-shadow: 0px 0px 0px !important;
  }

  .page-common .header-list-button, .page-common .header-message-button,
.page-common .header-notification-button
  {
    color: #ccc !important;
  }

  .page-common .header-list-button:hover, .page-common .header-message-button:hover,
.page-common .header-notification-button:hover,
.page-common .on .header-list-button, .page-common .on .header-message-button,
.page-common .header-notification-button[aria-expanded="true"]
  {
    color: #ccc !important;
  }

  .page-common .header-list-button .fa, .page-common .header-message-button .fa,
.page-common .header-notification-button .fa
  {
    font-size: 19px !important;
  }

  .header-notification
  {
    padding-right: 10px !important;
  }

  .page-common .header-notification-button.has-unread
  {
    color: #ccc !important;
  }

  .page-common .header-notification-button.new:after
  {
    background-color: #777 !important;
  }

  .page-common .header-notification-button.has-unread:after
  {
    background-color: #777 !important;
    border: none !important;
    padding: 2px 4px 2px 3px !important;
    text-align: center !important;
  }

  .page-common #header-menu>.border
  {
    display: none !important;
  }

  .page-common .header-profile-link
  {
    color: #CCC !important;
    font-weight: normal !important;
  }

  .page-common .header-profile-link:hover,
.page-common div.on .header-profile-link
  {
    color: #ccc !important;
  }

  .page-common .header-profile-link i.fa-caret-down
  {
    margin-left: 8px !important;
  }

  .page-common .button_edit,
.page-common .button_add
  {
    border-bottom: none !important;
  }

/* Menu --------------------------------------------*/

  #menu
  {
    margin-top: 0px !important;
    height: 50px !important;
    background: #333 !important;
    position: absolute !important;
    top: 0 !important;
    border-radius: 3px 3px 0px 0px !important;
    -moz-border-radius: 3px 3px 0px 0px !important;
    -webkit-border-radius: 3px 3px 0px 0px !important;
  }

  #menu_left
  {
    background: #2B2B2B !important;
    height: 50px !important;
    margin-left: 70px !important;
  }

  #menu_right
  {
    padding: 6px 0px 2px 6px !important;
    position: absolute !important;
    background: rgba(0, 0, 0, 0.6) !important;
    top: 208px !important;
    left: 0px !important;
    z-index: 1 !important;
    border-radius: 3px 3px 0px 0px !important;
    -moz-border-radius: 3px 3px 0px 0px !important;
    -webkit-border-radius: 3px 3px 0px 0px !important;
  }

/* Search --------------------------------------------*/

  #searchBar
  {
    padding: 0px !important;
  }

  #searchBar .inputtext
  {
    background: #373737 !important;
    border: none !important;
    height: 20px !important;
    text-indent: 2px !important;
    color: #999 !important;
    box-sizing: border-box;
    -moz-box-sizing: border-box;
    -webkit-box-sizing: border-box;
    border-radius: 3px !important;
    -moz-border-radius: 3px !important;
    -webkit-border-radius: 3px !important;
  }

  #topSearchButon
  {
    display: none !important;
  }

  #topSearchText
  {
    margin-right: 5px !important;
  }


  #top-search-bar #searchBar .inputtext
  {
    background: #222 !important;
  }


/* Search Dropdown ----------------------------------------*/
 
  .page-common .header-notification-item,
.page-common .header-notification-view-all
  {
    background-color: #333 !important;
  }

  .page-common .header-notification-item:hover,
.page-common .header-notification-view-all:hover

  {
    background-color: #777 !important;
  }

  .page-common .incrementalSearchResultList
  {
    position: absolute;
    background-color: #333 !important;
    color: #ccc !important;
    top: 42px !important;
    left: 0px !important;
    width: 349px !important;
    border: #777 1px solid !important;
    z-index: 100;
    -moz-border-radius: 0px 0px 6px 6px;
    -webkit-border-radius: 0px;
    border-radius: 0px 0px 6px 6px;
    -moz-box-shadow: 0px 0px 10px 2px rgba(0,0,0,0.2);
    -webkit-box-shadow: 0px 0px 10px 2px rgba(0,0,0,0.2);
    box-shadow: 0px 0px 10px 2px rgba(0,0,0,0.2);
    transition: background-color 0.5s !important;
    -moz-transition: background-color 0.5s !important;
    -webkit-transition: background-color 0.5s !important;
  }

  .page-common .incrementalSearchResultList .list.separator:first-of-type
  {
    border-top: 0px !important;
  }

  .page-common .incrementalSearchResultList .list.separator
  {
    text-transform: capitalize;
    height: 14px !important;
    line-height: 14px;
    padding: 5px 8px 10px 8px;
    color: #ccc;
    font-weight: normal !important;
    background-color: #333 !important;
    font-size: 12px !important;
    border-bottom: 1px solid #777 !important;
    border-top: 1px solid #777 !important;
  }

  .page-common .incrementalSearchResultList .list
  {
    background-color: #333 !important;
    color: #ccc !important;
    border-bottom: 1px solid #777 !important;
  }

  .page-common .incrementalSearchResultList .list.focus
  {
    background-color: #222 !important;
    transition: background-color 0.5s !important;
    -moz-transition: background-color 0.5s !important;
    -webkit-transition: background-color 0.5s !important;
  }

  .page-common .incrementalSearchResultList .list .off .image
  {
    border-radius: 3px !important;
    -moz-border-radius: 3px !important;
    -webkit-border-radius: 3px !important;
  }

  .page-common .incrementalSearchResultList .list .off .info.anime .name, 
.page-common .incrementalSearchResultList .list .off .info.manga .name,
.page-common .incrementalSearchResultList .list .on .info.anime .name, 
.page-common .incrementalSearchResultList .list .on .info.manga .name
  {
    color: #ccc !important;
    font-weight: bold !important;
  }

  .page-common .incrementalSearchResultList .list .off .info.anime .media-type,
.page-common .incrementalSearchResultList .list .off .info.manga .media-type,
.page-common .incrementalSearchResultList .list .on .info.anime .media-type,
.page-common .incrementalSearchResultList .list .on .info.manga .media-type,
.page-common .incrementalSearchResultList .list .on .info .media-type,
.page-common .incrementalSearchResultList .list .on .info .extra-info
  {
    color: #ccc !important;
  }

  .page-common .incrementalSearchResultList .list.list-bottom
  {
    background-color: #373737 !important;
    color: #ccc !important;
  }

  .page-common .incrementalSearchResultList .list.list-bottom.focus a
  {
    color: #ccc !important;
  }

  .page-common .incrementalSearchResultList .list.list-bottom.focus a:hover
  {
    color: #CCC !important;
  }

/* Nav --------------------------------------------*/

  #nav li
  {
    background: none !important;
    padding-top: 0px !important;
    padding-right: 0px !important;
    height: 50px !important;
  }

  #nav li.small,#nav li.small2
  {
    width: auto !important;
  }

  #nav li a
  {
    background: none !important;
    font-weight: normal !important;
    color: #CCC !important;
    font-size: 14px !important;
    height: 50px !important;
    text-align: center !important;
    cursor: pointer !important;
