    color: #466a94;
    display: block;
    font-size: 11px;
    font-weight: 700;
    height: 15px;
    margin-left: 19px;
    padding: 0 24px;
}
/* Изменения шрифта Обновление */
.header_right_link {
    color: #939393;
    font-size: 11px;
    height: 32px;
    line-height: 40px;
    margin-right: 15px;
}
/* Изменения шрифта заголовок онлайн */
.fc_tab_title {
    color: #fff;
    font-size: 11px;
    font-weight: 700;
    height: 30px;
    line-height: 30px;
    overflow: hidden;
    padding: 0 3px;
    text-overflow: ellipsis;
    white-space: nowrap;
}

/* Изменение группы при открытии на основной страничке */

.fans_idol_row {
    font-size: 11px;
    padding: 16px 10px;
    width: 284px;
}

/* Изменение размера времени видео на основной страничке */
.video_module .video .page_video_duration {
    background: rgba(0, 0, 0, 0.5) none repeat scroll 0 0;
    border-radius: 2px;
    bottom: 0;
    color: #fff;
    font-size: 11px;
    margin: 8px;
    padding: 3px 6px;
    position: absolute;
    right: 0;
}
/*Изменение размера  номера альбома на основной страничке */
.page_album_size {
    float: right;
    font-size: 11px;
    margin: -1px 0 0 5px;
    opacity: 0.9;
}
/* Изменение размера точки онлайн в диалоге */
.online::after {
    border: 2px solid #fff;
    bottom: 2%;
    height: 5px;
    right: 2%;
    width: 5px;
}
/* Изменение размера в поисках */
.fakeinput, div[contenteditable="true"], input.big_text, input.file, input.search, input.text, input[type="button"], input[type="password"], input[type="search"], input[type="submit"], input[type="text"], input[type~="email"], input[type~="password"], input[type~="search"], input[type~="text"], textarea {
    box-shadow: none;
    font-family: -apple-system,BlinkMacSystemFont,Roboto,Open Sans,Helvetica Neue,sans-serif;
    font-size: 11px;
    outline: medium none;
}
/* Изменение размера в ответах поиска  */

.no_rows {
    color: #939393;
    font-size: 11px;
    line-height: 22px;
    overflow: hidden;
    padding: 100px 50px;
    text-align: center;
}

.im-page .im-page--lastact {
    color: #a9abad;
    font-size: 11px;
}.im-page .im-page--history-new-bar {
    color: #828282;
    font-size: 11px;
    font-weight: 400;
    text-align: center;
}
.fc_contact_name
{
	line-height: 34px;
	padding-left: 9px;
	white-space: nowrap;
	display: block;
	text-overflow: ellipsis;
	overflow: hidden;
	font-weight: 400;
	-webkit-font-smoothing: antialiased;
	-moz-osx-font-smoothing: grayscale;
	color: #42648B;
	font-size: 11px;
}

/* Изменение даты в диалоге */
.nim-dialog .nim-dialog--date {
    color: #777e8c;
    font-size: 11px;
}

.replies_open {
       font-size: 11px;
 }

#fc_clist_filter, #fc_clist_filter ~ .placeholder .ph_input {
    border: 0 none;
    font-size: 11px;
    padding: 5px 7px 7px;
}



/* Изменение размера в поиске Город */
.ts_contact_info {
    color: #939393;
    font-size: 11px;
    font-weight: 400;
    line-height: 14px;
    overflow: hidden;
    padding-top: 1px;
    text-overflow: ellipsis;
}

.fc_tab_wrap {
    border-radius: 3px;
    box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.1);
    font-size: 11px;
}

