css:
@-moz-document domain("marketplace.secondlife.com") {
/*General background*/
body { 
background: url('https://c2.staticflickr.com/9/8647/30219334841_014d10476f_o.jpg') 100% 100% !important; 
background-repeat: no-repeat !important; 
background-attachment: fixed !important; 
background-position: 25% !important; 
-webkit-background-size: cover !important;
-o-background-size: cover !important; 
background-size: cover !important;  
overflow-y: auto !important; 
overflow-x: hidden !important; 
}


#header { 
position: fixed !important; 
width: 100% !important; 
left: 0px !important; 
z-index: 1000 !important; 
}

#sl-toolbar { 
background-image: none !important; 
background: #333 !important;
background: -moz-linear-gradient(top, #333333 0%, #2e2e2e 50%, #232323 51%, #191919 98%, #242424 99%, #242424 100%) !important;
background: -webkit-linear-gradient(top, #333333 0%,#2e2e2e 50%,#232323 51%,#191919 98%,#242424 99%,#242424 100%) !important; background: linear-gradient(to bottom, #333333 0%,#2e2e2e 50%,#232323 51%,#191919 98%,#242424 99%,#242424 100%) !important;
padding-top: 10px !important; 
padding-bottom: 10px !important; 
border-bottom: 1px solid #101010 !important; 
box-shadow: 0 3px 6px rgba(26,26,26,.6);
height: 35px !important;
    }

.no-js #body-shadow-repeating, .no-boxshadow #body-shadow-repeating,
.no-js.logged-in body #body-shadow-fade, .no-boxshadow.logged-in body #body-shadow-fade { 
background: none;
}

.cssgradients #sl-toolbar::before {
content: '';
position: absolute;
bottom: 0;
left: 50%;
right: 50%;
width: 0%;
height: 1px;
background: #508E77;
transition: all .15s cubic-bezier(.79, .14, .15, .86);
}

.cssgradients #sl-toolbar:hover::before {
left: 0 !important;
right: 0 !important;
width: 100% !important;
}

#language-picker { 
padding-left: 70px !important; 
}

#sl-toolbar #language-picker.selected { 
background: none !important; 
}

#sl-toolbar #language-picker ul { 
background: rgba(25,25,25,.9);
margin-top: 10px !important;
background-clip: padding-box !important;
border: 1px solid rgba(0, 0, 0, 0) !important;
box-shadow: 0 0 10px 4px rgba(0, 0, 0, 0.8) !important;
z-index: -1 !important; 
padding-left: 0px;
padding-right: 0px;
}

#sl-toolbar #language-picker ul li { 
padding-left: 12px;
padding-right: 12px;
}

#sl-toolbar #language-picker ul li:hover { 
background-color: rgba(124,124,124,.4);
-webkit-transition: all .35s linear;
-moz-transition: all .35s linear;
transition: all .35s linear;
padding-left: 12px;
padding-right: 12px;
}

#my-marketplace.selected a#my-marketplace-link, 
#my-marketplace.selected a#my-marketplace-link:active { 
background: none !important; 
border: none !important; 
}

#my-marketplace ul { 
background: rgba(25,25,25,.9) !important;
margin-top: 10px !important;
background-clip: padding-box !important;
border: 1px solid rgba(0, 0, 0, 0) !important;
box-shadow: 0 0 10px 4px rgba(0, 0, 0, 0.8) !important;
z-index: -1 !important; 
padding-left: 10px;
padding-right: 10px;
}

#my-marketplace ul li:hover { 
background-color: rgba(124,124,124,.4);
-webkit-transition: all .35s linear;
-moz-transition: all .35s linear;
transition: all .35s linear;
}


#marketplace-toolbar a.logo img { 
background: url('http://i.imgur.com/YBKfyRU.png') no-repeat;
background-size: 100% auto;
padding-left: 65px; 
padding-top: 36px;
height: 0px;
width: 0px;
}

#marketplace-toolbar .logo { 
position: fixed !important;
top: -4px !important;
left: 0px !important; 
z-index: 1001 !important; 
padding-left: 5px !important; 
}

#marketplace-toolbar { 
padding-top: 30px !important; 
}

#marketplace-toolbar.container div.span-2 { 
position: fixed !important;
top: 0px !important;
left: 0px !important; 
z-index: 1001 !important; 
width: 75px !important; 
height: 55px !important; 
}

#search-container input.search { 
background: #5cb192 !important;
border: 1px solid rgba(0,0,0,.8) !important;
border-radius: 3px !important;
box-shadow: 0 1px 2px #111, 0 1px 0px #91C6B2 inset !important;
}

#search-container input.search:hover { 
background: #315E4D !important;
border-bottom-color: #333 !important;
border-top-color: #000 !important;
border-right-color: #222 !important;
border-left-color: #202020 !important;
border-radius: 3px !important;
box-shadow: inset 0px 2px 4px 0px #000 !important;
transition: background .2s, box-shadow .4s, border-color .2s !important;
color: #303030 !important; 
}

#join-sl { 
background: none !important; 
text-shadow: none !important; 
}

#language-drop, 
#marketplace-home, 
#marketplace-help, 
#login-bar { 
margin-top: 2px;
}

#my-marketplace ul,
#my-marketplace.selected a#my-marketplace-link { 
background: #161616;
border: 1px solid #333;
color: #BBB !important;
}

#my-marketplace.selected li a { 
color: #BBB  !important;
}

#my-marketplace.selected li a:hover { 
background: #444;
color: #FFF  !important;
}

#search { 
margin-top: -35px !important;
}

#marketplace-toolbar .logo { 
position: fixed;
top: -14px;
z-index: 2;
}

#centered-page a, 
#search-menu div.filter-options p a, 
#search-menu div.filter-options li a, 
#merchant-search-category div.filter-options p a, 
#merchant-search-category div.filter-options li a { 
color: #AAA;
}

#centered-page a:hover:not(.revise-listing) { 
color: #FFF !important;
text-decoration: none !important; 
-webkit-transition: all 0.25s ease-in-out;
-moz-transition: all 0.25s ease-in-out;
-o-transition: all 0.25s ease-in-out;
transition: all 0.25s ease-in-out; 
}

/*Main Column*/
#centered-page { 
background: #222 !important; /*← Just in case.*/
background-clip: padding-box !important;
border-left: 1px solid rgba(0, 0, 0, 0);
border-right: 1px solid rgba(0, 0, 0, 0);
box-shadow: 0 0 10px 4px rgba(0, 0, 0, 0.8);
}

/*Main Column*/
#canvas-container { 
background: #222 !important;
padding-top: 70px;
}

#rating-distribution-link, 
h1, h2, h3, h4, h5, h6, .avatar-compatibility, 
#product-reviews .ratings span.average-rating-label { 
color: #CCC !important;
}

.avatar-compatibility strong { 
color: #5CB192;
}

/*Removed contents*/
.cssgradients, 
.cssgradients.logged-in,
.cssgradients #footer-with-shadow { 
background: none; 
}

/*Home page*/
#featured-items .featured-carousel, 
#featured-items-category .featured-carousel { 
background: #000;
border-color: #333; 
border-radius: 0 0 2px 2px;
}

#canvas, 
#search-permissions.filter-options p label, 
.gift-form input#cart_item_giftee, .gift-form textarea { 
color: #999;
}

.gift-form, 
.product-listing.gallery .gallery-item {
background: #161616;
border: 1px solid #333;
transition: all 0.25s linear 0s;
border-radius: 4px;
box-shadow: 1px 2px 4px rgba(0, 0, 0, 1);
overflow: hidden;
}

.product-listing.gallery .gallery-item:hover {
border-color: #999;
transition: all 0.25s linear 0s;
}

.product-listing.gallery .gallery-item .item-price .price { 
background: #0F0F0F;
border-radius: 4px 0 0 4px;
}

input[type="text"], input.text, input.title { 
background: #161616;
border: 1px solid #333;
transition: all 0.25s linear 0s;
}

input[type="text"]:hover, input.text:hover, input.title:hover { 
border-color: #999;
transition: all 0.25s linear 0s;
}

#featured-items .featured-carousel .featured-carousel-container .gallery-item, 
#featured-items-category .featured-carousel .featured-carousel-container .gallery-item  { 
background: #161616;
border: 1px solid #333;
transition: all 0.25s linear 0s;
}

#featured-items .featured-carousel .featured-carousel-container .gallery-item:hover, 
#featured-items-category .featured-carousel .featured-carousel-container .gallery-item:hover {
border-color: #999;
transition: all 0.25s linear 0s;
}

#featured-items h2, 
#featured-items-category h2 { 
background: url("http://i.imgur.com/C1v2n1W.png") repeat-x scroll 0 0 / 100% 100% !important; 
border: 1px solid #333; 
border-bottom: none; 
}

/*Box-shadow, box-shadow everywhere!*/
#featured-items { 
box-shadow: 1px 2px 4px rgba(0, 0, 0, 1);
}

/*Box-shadow removed from the latest Marketplace update*/
#my-marketplace ul, 
.submit-button .button, 
#comments .button, 
#my-marketplace.selected a, 
#sl-toolbar #language-picker ul { 
box-shadow: none; 
}

/*Home*/
#recently-purchased.clear.span-6.last .column.span-4 { 
width: 700px;
}

#recently-purchased .recently-purchased-item { 
background: #161616;
border: 1px solid #333;
color: #999;
border-radius: 3px; 
box-shadow: 1px 2px 4px rgba(0, 0, 0, 1);
padding-right: 25px;
padding-left: 25px;
margin: 5px;
position: relative;
right: -25px;
transition: all 0.25s linear 0s;
}

#recently-purchased .recently-purchased-item:hover { 
border-color: #999;
transition: all 0.25s linear 0s;
}

#search, 
#search #search-container { 
width: 940px;
background: #222;
border: none;
border-bottom: 1px solid #505050;
}

#search a.tab.selected { 
background: #222;
border: none;
border-bottom: 3px solid #5CB192;
font-weight: 700 !important; 
color: #FFF !important;
}

#search a.tab { 
background: #222;
border: none;
font-weight: bold;
text-transform: uppercase;
text-align: center;
}

#search a.tab:hover { 
color: #FFF !important;
}

#link-item-search, 
#link-merchant-search, 
#link-store-search {
position: relative;
margin-left: 0 !important; 
margin-right: 0 !important; 
}


#link-item-search:not(.selected)::before, 
#link-merchant-search:not(.selected)::before, 
#link-store-search:not(.selected)::before { 
content: '';
position: absolute;
bottom: -3px;
left: 50%;
right: 50%;
width: 0%;
height: 3px;
background: #5CB192;
transition: all .15s cubic-bezier(.79, .14, .15, .86);
}

#link-item-search:hover:before, 
#link-merchant-search:hover:before, 
#link-store-search:hover:before { 
left: 0 !important;
right: 0 !important;
width: 100% !important;
}

#search select { 
width: 210px;
border: 1px solid transparent; 
background: #2c2c2c;
color: #999;
transition: all 0.25s linear 0s;
}

#search select:hover { 
border-color: #666;
transition: all 0.25s linear 0s;
}

#search select:active { 
border-color: #FFF;
}

#search-menu .utility-button, #merchant-search-category .utility-button { 
box-shadow: none !important; 
border-color: #333;
border-radius: 2px;
color: #AAA;
}

#search-menu .utility-button:hover, #merchant-search-category .utility-button:hover { 
border-color: #666;
color: #5CB192;
}

#search select#search_maturity_level, 
#search select#store-search-maximum-maturity { 
width: 165px;
}

#search input#keywords { 
box-shadow: none !important; 
width: 428px !important;
}

#search input#keywords:hover { 
border-color: #666;
transition: all 0.25s linear 0s;
}

#store-search-submit { 
box-shadow: none !important;
}

textarea, select, 
#search input#keywords, 
#search input#store-search-keywords, 
#search input#merchant-search-keywords, 
.gift-form input#cart_item_giftee, .gift-form textarea { 
box-shadow: none !important;
border: 1px solid transparent; 
background: #2c2c2c;
color: #999;
transition: all 0.25s linear 0s;
}

#search input#keywords, 
#search input#store-search-keywords { 
width: 427px !important; 
}

#search input#merchant-search-keywords { 
width: 832px !important; 
}

textarea:hover, select:hover, 
#search input#keywords:hover, 
#search input#store-search-keywords:hover, 
#search input#merchant-search-keywords:hover, 
.gift-form input#cart_item_giftee:hover, .gift-form textarea:hover { 
border-color: #666;
transition: all 0.25s linear 0s;
}

textarea:active, select:active, 
#search input#keywords:active, 
#search input#store-search-keywords:active, 
#search input#merchant-search-keywords:active, 
.gift-form input#cart_item_giftee:active, .gift-form textarea:active { 
border-color: #FFF;
}

#product-merchant-details { 
background: #161616;
border: 1px solid #333;
border-radius: 3px; 
box-shadow: 1px 2px 4px rgba(0, 0, 0, 1);
padding: 5px 5px 5px 10px !important;
z-index: 1010;
float: right !important; 
}

#product-main { 
width: 680px !important;
min-width: 680px !important;
max-width: 680px !important; 
}

#review-product-info .gallery-item { 
background: #161616 none repeat scroll 0 0;
border: 1px solid #333;
border-radius: 4px;
box-shadow: 1px 2px 4px rgba(0, 0, 0, 1);
transition: all 0.25s linear 0s;
}

#review-product-info .gallery-item:hover { 
border-color: #505050;
transition: all 0.25s linear 0s; 
}

#review-product-info .gallery-item .item-price .price { 
background: #0f0f0f none repeat scroll 0 0;
border-radius: 4px 0 0 4px; 
}

#product-merchant-details .cart-button, 
#product-merchant-details .cart-form a:not(.demo-link):not(.revise-listing) { 
color: #AAA !important; 
}

#product-merchant-details .cart-form a.demo-link { 
color: #FFF !important; 
}

#add-to-cart-button .marketplace-sprite { 
background: none !important; 
}

#add-to-cart-button.cart-button span { 
padding-left: 0 !important;
}

#product-tabs .slurl, 
#product-tabs .video-url { 
background: #333 !important;
border: 1px solid rgba(0,0,0,.8) !important;
border-radius: 3px !important;
box-shadow: 0 1px 2px #111, 0 1px 0px #555 inset !important;
font-size: 12px;
font-weight: bold;
outline: 0 none;
padding-bottom: 5px;
padding-top: 5px;
right: 2px;
top: -7px;
width: 200px;
text-decoration: none; 
position: relative;
text-align: center !important; 
}

#product-tabs .slurl:before { 
content: "?" !important; 
padding-right: 5px !important; 
}

#product-tabs .video-url:before { 
content: "" !important; 
background: url("https://s.ytimg.com/yts/img/favicon-vflz7uhzw.ico") !important; 
background-repeat: no-repeat !important; 
width: 16px !important; 
height: 16px !important; 
position: relative;
padding-right: 20px;
top: -1px !important;
}

.profile-detail-link[href^="http://maps.secondlife.com/secondlife/"] { 
background: #333 !important;
border: 1px solid rgba(0,0,0,.8) !important;
border-radius: 3px !important;
box-shadow: 0 1px 2px #111, 0 1px 0px #555 inset !important;
font-size: 12px !important;
font-weight: bold !important;
outline: 0 none !important;
padding-bottom: 5px !important;
padding-top: 5px !important;
right: 2px !important;
top: 0px !important;
width: 212px !important;
text-decoration: none !important; 
position: relative !important;
text-align: center !important; 
}

.profile-detail-link[href^="http://maps.secondlife.com/secondlife/"]:before { 
content: "?" !important; 
padding-right: 5px !important; 
}

/*Add to Cart Buttons, I might gonna add different colors in the future which is why I use them separately instead of the class element.*/
#add-to-cart-button.button.cart-button, 
#add-as-gift-button.button.cart-button.gift-button, 
#add-to-cart-form.add-to-cart-form a#buy-now-button.button.cart-button { 
background: #333 !important;
border: 1px solid rgba(0,0,0,.8) !important;
border-radius: 3px !important;
box-shadow: 0 1px 2px #111, 0 1px 0px #555 inset !important;
}

/*Demo Link*/
#product-merchant-details a.demo-link { 
background: #5cb192 !important;
border: 1px solid rgba(0,0,0,.8) !important;
border-radius: 3px !important;
box-shadow: 0 1px 2px #111, 0 1px 0px #91C6B2 inset !important;
font-size: 15px;
font-weight: bold;
outline: 0 none;
padding-bottom: 5px;
padding-top: 5px;
right: 2px;
top: -7px;
width: 100%;
text-decoration: none; 
position: relative;
}

.cart-form .revise-listing { 
background: #333 !important;
border: 1px solid rgba(0,0,0,.8) !important;
border-radius: 3px !important;
box-shadow: 0 1px 2px #111, 0 1px 0px #555 inset !important;
font-size: 15px;
font-weight: bold;
outline: 0 none;
padding-bottom: 5px;
padding-top: 5px;
right: 2px;
top: 0px;
width: 100%;
text-decoration: none; 
position: relative;
color: #AAA; 
}

.cart-form .revise-listing:hover { 
box-shadow: inset 0px 2px 4px 0px #000 !important;
border-bottom-color: #333 !important;
border-top-color: #000 !important;
border-right-color: #222 !important;
border-left-color: #202020 !important;
background-color: #181818 !important;
border-radius: 3px !important;
transition: background .2s, box-shadow .4s, border-color .2s !important;
color: #4C4C4C !important;
text-decoration: none !important; 
}

#product-tabs .slurl:hover, 
#product-tabs .video-url:hover, 
#add-to-cart-button.button.cart-button:hover, 
#merchant-box a[href*="/stores/"]:nth-child(2):hover, 
#add-as-gift-button.button.cart-button.gift-button:hover, 
#add-to-cart-form.add-to-cart-form a#buy-now-button.button.cart-button:hover { 
box-shadow: inset 0px 2px 4px 0px #000 !important;
border-bottom-color: #333 !important;
border-top-color: #000 !important;
border-right-color: #222 !important;
border-left-color: #202020 !important;
background-color: #181818 !important;
border-radius: 3px !important;
transition: background .2s, box-shadow .4s, border-color .2s !important;
color: #4C4C4C !important; 
}

#product-merchant-details a.demo-link:hover { 
box-shadow: inset 0px 2px 4px 0px #000 !important;
border-bottom-color: #333 !important;
border-top-color: #000 !important;
border-right-color: #222 !important;
border-left-color: #202020 !important;
background-color: #315E4D !important;
border-radius: 3px !important;
transition: background .2s, box-shadow .4s, border-color .2s !important;
color: #303030 !important; 
}

#main-feature { 
border: none;
}

#merchant-box { 
border-top: 1px solid #333;
border-bottom: 1px solid #333;
padding-top: 15px;
}

#merchant-box a[href*="/stores/"]:nth-child(2) {
background: #333 !important;
border: 1px solid rgba(0,0,0,.8) !important;
border-radius: 3px !important;
box-shadow: 0 1px 2px #111, 0 1px 0px #555 inset !important;
outline: 0 none;
padding-bottom: 2px;
padding-top: 2px;
right: 2px;
top: 7px;
width: 100%;
text-decoration: none; 
position: relative;
display: block !important; 
color: #AAA !important;
text-align: center !important; 
}

#usage-requirements { 
border-bottom: 1px solid #333;
}

#product-details .ratings { 
border-bottom: 1px solid #333;
padding-bottom: 15px;
}

#item-options { 
border-top: 1px solid #333;
padding-top: 15px;
}

#product-details .prim-count, 
#product-details .mesh-support { 
color: #999;
}

/*Prim count:*/
#product-details > ul:nth-child(1) > li:nth-child(3) {
padding-top: 25px;
}

.price, 
.prices { 
color: #5cb192 !important;
}

/*Permissions*/
.marketplace-sprite.cmt.permitted { 
color: #5CB192 !important;
font-weight: bold;
}

.marketplace-sprite.cmt.not-permitted { 
color: #D93F3B !important;
