}

#link-product-description, 
#link-product-features, 
#link-product-contents, 
#link-product-reviews { 
color: #AAA; 
font-weight: bold !important; 
text-transform: uppercase !important;
position: relative;
}

#link-product-description:hover, 
#link-product-features:hover, 
#link-product-contents:hover, 
#link-product-reviews:hover { 
color: #FFF !important; 
}

#link-product-description:not(.selected)::before, 
#link-product-features:not(.selected)::before, 
#link-product-contents:not(.selected)::before, 
#link-product-reviews:not(.selected)::before { 
content: '';
position: absolute;
bottom: -3px;
left: 50%;
right: 50%;
width: 0%;
height: 3px;
background: #5CB192;
transition: all .15s cubic-bezier(.79, .14, .15, .86);
}

#link-product-description:hover:before, 
#link-product-features:hover:before, 
#link-product-contents:hover:before, 
#link-product-reviews:hover:before { 
left: 0 !important;
right: 0 !important;
width: 100% !important;
}

.review-date a { 
text-transform: capitalize !important;
}

#product-tabs .tab.selected { 
background: none; 
border-bottom: 3px solid #5CB192;
font-weight: bold !important; 
color: #FFF !important;
}

#product-main { 
min-height: 700px !important; 
}

#product-contents .contents-header { 
background: url("http://i.imgur.com/C1v2n1W.png") repeat-x scroll 0 0 / 100% 100% !important; 
color: #999;
border-radius: 3px;
width: 670px;
border: none;
}

#inventory-content-container .inventory-contents-container .inventory-contents {
