css:
  @namespace url(http://www.w3.org/1999/xhtml);


  @-moz-document domain("tabun.everypony.ru") {


    #toolbar {
      position: fixed!important;
      right: 0!important;
      top: 30%!important;
      left: unset!important;
      bottom: unset!important;
    }

  @media (min-width: 1025px) and (max-width: 1100px) {
    #running_ponies img:nth-of-type(5) {
      display: none;
    }
  }
  @media (min-width: 1025px) {
    #wrapper {
      padding: 10px 16px 20px 26px;
    }
    #content-wrapper {
      width: 70%;
      margin-right: 5%;
    }
    #sidebar {
      width: 25%;
    }
    #comments .comment-level-40 .comment-wrapper {
      padding-left: 0;
    }
  }
  @media (min-width: 769px) and (max-width: 1024px) {
    #wrapper {
      padding: 10px 10px 20px 10px;
    }
    #content-wrapper {
      width: 70%;
      margin-right: 2%;
    }
    #sidebar {
      width: 28%;
    }
    #running_ponies img:nth-of-type(5) {
      display: none;
    }
    #reply {
      margin-left: 20px;
    }
    #comments .comment-level-30 .comment-wrapper {
      padding-left: 0;
    }
  }
  @media (min-width: 769px) {
    body.widemode #content-wrapper {
      width: 100%;
    }
    body.widemode #sidebar {
      display: none;
    }
    body.widemode #widemode .switch-label.normalmode.big-screen {
      display: inline-block;
    }
    body.widemode #widemode .switch-label.widemode.big-screen {
      display: none;
    }
    #widemode .switch-label.widemode.big-screen {
      display: inline-block;
    }
  }
  @media (max-width: 768px) {
    #reply {
      margin-left: 0;
    }
    #wrapper {
      margin: 0;
    }
    #comments {
      margin-bottom: 0;
    }
    #wrapper .table tr td,
    #wrapper .table tr .cell-tab-inner {
      padding: 3px 0;
    }
    #wrapper.no-sidebar .faq-page {
      width: 90%;
    }
    #widemode .switch-label.widemode.small-screen {
      display: inline-block;
    }
    body.widemode #widemode .switch-label.normalmode.small-screen {
      display: inline-block;
    }
    body.widemode #widemode .switch-label.widemode.small-screen {
      display: none;
    }
  }






  @media (min-width: 568px) and (max-width: 768px) {
    #c-header .main-menu {
      padding-top: 5px!important;
    }
    #c-header .main-menu li:first-of-type {
      width: 100%;
    }
    #header {
      overflow: visible;
    }
    #header .auth,
    #header #dropdown-user {
      background: #f4f4f6;
      display: none;
      right: 0;
      width: 32%;
    }
    #header .auth {
      width: 44%;
      height: 75px;
    }
    #wrapper {
      width: 140%;
      padding: 10px 5px 20px 5px;
    }
    #wrapper.no-sidebar {
      flex-flow: row;
    }
    #content-wrapper {
      width: 70% !important;
    }
    #sidebar {
      width: 30%;
      position: relative;
      top: 0;
      left: 100%;
      z-index: 5;
    }
    #sidebar .block,
    #sidebar .donate {
      border-radius: 0;
      background: #f4f4f6;
      padding-bottom: 40px;
      margin: 0;
    }
    #sidebar #running_ponies {
      background: #f4f4f6;
      margin: 0;
    }

    #comments .comment-wrapper {
      padding-left: 10px;
    }
    #comments .comment-level-15 .comment-wrapper,
    #comments .comment-wrapper .comment-wrapper .comment-wrapper .comment-wrapper .comment-wrapper .comment-wrapper .comment-wrapper .comment-wrapper .comment-wrapper .comment-wrapper .comment-wrapper .comment-wrapper .comment-wrapper .comment-wrapper .comment-wrapper .comment-wrapper {
      padding-left: 0;
    }
    .comment-info li {
      margin-right: 10px;
    }
    #modal_write,
    #window_upload_img {
      top: 10px;
    }
    .modal.react-modal,
    .profile-photo-wrapper .modal.react-modal {
      top: 10px;
      margin-left: -160px;
    }
    .modal.react-modal .modal-content,
    .profile-photo-wrapper .modal.react-modal .modal-content {
      margin: 0 10px 10px;
    }
    .modal.react-modal .modal-content canvas,
    .profile-photo-wrapper .modal.react-modal .modal-content canvas {
      width: 300px;
      height: 300px;
    }
    .modal.react-modal .modal-content input[type="range"],
    .profile-photo-wrapper .modal.react-modal .modal-content input[type="range"] {
      margin: 0 25px 0 10px;
      max-width: 150px;
    }
    body.widemode #header .auth {
      display: flex;
    }
    body.widemode #header #dropdown-user {
      display: inline-block;
    }
    body.widemode #sidebar {
      left: -30%;
    }
    body.widemode #toolbar {
      display: none !important;
    }
  }
  @media (max-width: 567px) {
    #wrapper {
      padding: 10px 5px 20px 5px;
    }
    #content-wrapper {
      width: 100%;
      display: block;
    }
    #sidebar {
      width: 100%;
      display: none;
    }
    #header {
      padding: 0 5px;
      margin: 0;
    }
    #header ul {
      width: 100%;
    }
    #header .auth,
    #header #dropdown-user,
    #header .modal.modal-login {
      display: none;
    }
    #c-header {
      height: 50px;
      padding: 5px 5px 0 5px;
    }
    #c-header .main-menu {
      padding: 0!important;
      display: flex;
      flex-flow: row;
      flex-wrap: wrap;
      justify-content: space-between;
    }
    #c-header .main-menu li {
      display: none;
      font-size: 18px;
      line-height: 18px;
      padding: 0 2px;
    }
    #c-header .main-menu #logolink {
      padding: 0;
      display: block;
      width: 100%;
      padding: 0;
      text-align: center;
      line-height: 36px;
    }
    #c-header .rss,
    #c-header .twitter {
      top: 5px;
      right: 5px;
      width: 16px;
      height: 16px;
      background-size: 16px 32px;
    }
    #c-header .rss:hover,
    #c-header .twitter:hover {
      background-position: 0 -16px;
    }
    #c-header .twitter {
      top: 21px;
    }
    #footer {
      padding-bottom: 40px;
    }
    .topic {
      margin-bottom: 20px !important;
    }
    #comments .comments-header .subscribe {
      position: static;
    }
    #comments .comment-wrapper {
      padding-left: 10px;
    }
    #comments .comment-level-5 .comment-wrapper,
    #comments .comment-wrapper .comment-wrapper .comment-wrapper .comment-wrapper .comment-wrapper .comment-wrapper .comment-wrapper {
      padding-left: 0;
    }
    #comments .comment-info li {
      margin-right: 5px;
    }
    #widemode {
      width: 100%;
      border-radius: 0;
      text-align: right;
    }
    #widemode a {
      margin-right: 5px;
    }
    #toolbar {
      position: fixed!important;
      bottom: 0!important;
      left: 0!important;
      height: 35px;
      right: unset!important;
      top: unset!important;
      z-index: 101;
    }
    #toolbar .toolbar-topic {
      border: 0 transparent solid;
      margin: 0;
      display: flex;
      flex-flow: row;
      padding: 6px 0 0 10px;
      width: auto;
      height: 25px;
    }
    #toolbar .toolbar-topic a {
      height: 19px;
      width: 32px;
      display: inline-block;
    }
    #toolbar .toolbar-topic a i {
      transform: rotate(270deg);
      position: relative;
      top: -8px;
      left: 6px;
    }
    #toolbar .toolbar-update {
      border: 0 transparent solid;
      padding: 2px 0 0 5px;
      margin: 0;
      width: auto;
      display: flex;
      flex-flow: row;
      align-items: center;
    }
    #toolbar .toolbar-update #new_comments_counter {
      padding: 0 10px;
    }
    #wrapper .profile .user-login,
    #wrapper .profile .user-name {
      display: block;
      margin-right: 100px;
    }
    #wrapper .profile .strength,
    #wrapper .profile .vote-profile,
    #wrapper .profile .button {
      margin-left: 0px;
      float: left;
    }
    #wrapper .profile-info-about {
      position: static;
      padding: 0 5px;
    }
    #wrapper .profile-info-about .avatar {
      top: -5px;
      left: inherit;
      right: 0;
    }
    #wrapper .profile-dotted-list li {
      background-image: none;
      display: flex;
      flex-flow: column;
    }
    #wrapper .profile-dotted-list li span {
      font-weight: bold;
    }
    #wrapper .profile-dotted-list li strong {
      width: 100%;
    }
    #wrapper .table.table-blogs tr .cell-join {
      width: 20px;
    }
    #wrapper .table.table-blogs tr .cell-readers,
    #wrapper .table.table-blogs tr .cell-rating {
      width: 40px;
    }
    #wrapper .table.table-users tr .cell-name+th,
    #wrapper .table.table-users tr .cell-name+td {
      width: 20px;
    }
    #wrapper .table.table-users tr .cell-skill,
    #wrapper .table.table-users tr .cell-rating {
      width: 40px;
    }
    #wrapper .table.table-talk .cell-date.ta-r {
      display: none;
    }
    #wrapper .table.table-talk .cell-title {
      width: 70%;
    }
    #wrapper .table.table-talk .wrapper-content.wrapper-content-dark {
      padding: 10px 3px 20px 3px;
    }
    #wrapper select {
      max-width: 100% !important;
    }
    #modal_write,
    #window_upload_img {
      position: fixed;
      width: 100%;
      height: auto;
      margin: 0;
      left: 0;
    }
    #modal_write .modal-content,
    #window_upload_img .modal-content {
      padding: 5px 5px 15px 5px !important;
    }
    #modal_write {
      top: 150px;
    }
    #modal_write .write-list {
      display: flex;
      flex-flow: row;
      flex-wrap: wrap;
      justify-content: space-between;
      align-items: center;
    }
    #modal_write .write-list li {
      margin: 0 5px !important;
      padding: 0 !important;
    }
    #window_upload_img {
      top: 50px;
    }
    #window_upload_img .modal-header {
      text-align: center;
    }
    #window_upload_img .modal-content .nav.nav-pills.nav-pills-tabs {
      justify-content: center;
    }
    .modal.react-modal,
    .profile-photo-wrapper .modal.react-modal {
      top: 10px;
      margin-left: -130px;
    }
    .modal.react-modal .modal-content,
    .profile-photo-wrapper .modal.react-modal .modal-content {
      margin: 0 5px 5px;
    }
    .modal.react-modal .modal-content canvas,
    .profile-photo-wrapper .modal.react-modal .modal-content canvas {
      width: 200px;
      height: 200px;
    }
    .modal.react-modal .modal-content input[type="range"],
    .profile-photo-wrapper .modal.react-modal .modal-content input[type="range"] {
      margin: 0 25px 0 5px;
      max-width: 120px;
    }
    #window_login_form {
      position: static;
      z-index: 0;
    }
    #window_login_form .login.login-modal {
      position: fixed;
      z-index: 3000;
      left: 0;
      top: 50px;
      width: 100%;
    }
    #window_login_form .login.login-modal .login-container {
      width: 96%;
      padding: 10px 5px;
    }
    body.widemode #sidebar {
      display: block;
    }
    body.widemode #content-wrapper {
      display: none!important;
    }
    body.widemode #c-header .main-menu li {
      display: inline-block;
    }
    body.widemode #c-header #logolink,
    body.widemode #c-header .rss,
    body.widemode #c-header .twitter {
      display: none;
    }
    body.widemode #header ul.auth {
      display: flex;
    }
    body.widemode #header #dropdown-user {
      display: inline-block;
      right: 10px;
    }
    body.widemode #header .nav,
    body.widemode #header #nav {
      display: none;
    }
  }
  body {
    font-size: 12px;
    font-family: Arial, sans-serif;
    line-height: 18px;
    color: #333;
    background: url("https://cdn.everypony.ru/storage/02/43/83/2017/08/22/728dd6ddde.png") repeat scroll 0 0 #fff;
  }
  br.clear {
    clear: both;
  }
  a {
    color: #09f;
  }
  a:hover {
    color: #f00;
  }
  .user,
  .ls-user {
    font-weight: bold;
    color: #333;
  }
  .user:visited,
  .ls-user:visited {
    color: #333;
  }
  .user:hover,
  .ls-user:hover {
    color: #555;
  }
  img,
  iframe {
    max-width: 100%;
  }
  .text a:visited {
    color: #1d2273;
  }
  .spoiler-body {
    display: none;
    padding: 5px;
  }
  .spoiler-title {
    background: #f7f7f7;
    border: 1px solid #e6e6e6;
    border-radius: 3px;
    padding: 3px;
    cursor: pointer;
    display: inline-block;
  }
  .spoiler-media>.spoiler-title {
    box-shadow: 0px 0px 2px 1px rgba(168,255,127,0.8);
  }
  .spoiler {
    display: block;
    padding: 5px;
  }
  .spoiler + br {
    display: none;
  }
  .spoiler-gray {
    background-color: #ccc;
    color: #ccc;
  }
  .spoiler-gray a,
  .spoiler-gray a:visited,
  .spoiler-gray a.ls-user[href] {
    color: #ccc;
  }
  .spoiler-gray .ls-user::before {
    opacity: 0;
  }
  .spoiler-gray:hover {
    background-color: transparent;
    color: #666;
  }
  .spoiler-gray:hover a {
    background-color: transparent;
    color: #09f;
  }
  .spoiler-gray:hover a.ls-user {
    color: #555;
  }
  .spoiler-gray:hover .ls-user::before {
    opacity: inherit;
  }
  .spoiler-gray:hover img {
    filter: inherit;
    -webkit-filter: inherit;
  }
  .spoiler-gray:not(:hover) img {
    filter: contrast(0%) brightness(159.375%);
    -webkit-filter: contrast(0%) brightness(159.375%);
  }
  h1,
  h2,
  h3,
  h4,
  h5,
  h6 {
    color: #444;
  }
  textarea.markItUpEditor {
    height: 300px;
  }
  div.markItUpHeader {
    padding: 5px 0;
  }
  .markItUpFooter {
    padding: 0;
    height: 100%;
  }
  .comment-content .text img {
    vertical-align: bottom;
  }
  .generator .list .list-item {
    padding-left: 0;
  }
  #popup-password {
    margin-bottom: 12px;
  }
  #running_ponies {
    max-height: 60px;
    overflow: hidden;
    width: 100%;
    margin-bottom: 10px;
    display: flex;
    flex-flow: row;
    justify-content: space-between;
    white-space: nowrap;
  }
  #widemode {
    background-color: #fff;
    border-radius: 10px 0 0 0;
    bottom: 0;
    padding: 7px 1px 7px 10px;
    position: fixed;
    right: 0;
    z-index: 100;
    border: 1px solid #ccc;
    display: flex;
    justify-content: flex-end;
    flex-flow: row;
    align-items: center;
  }
  #widemode a {
    border: 1px solid #ccc;
    border-radius: 5px;
    height: 25px;
    line-height: 25px;
    text-decoration: none !important;
    color: #7f8993;
    font-weight: bold;
    margin-right: 10px;
    cursor: pointer;
    background-color: #f0f2f5;
  }
  #widemode a {
    display: inline-block;
    width: 25px;
    height: 25px;
  }
  #widemode a#up-switch,
  #widemode a#down-switch {
    background-image: url("https://cdn.everypony.ru/storage/02/43/83/2017/08/22/1e54a61c24.png");
  }
  #down-switch {
    transform: rotate(180deg);
  }
  #widemode a:hover {
    color: #fff;
    background-color: #22b2fe;
    border-color: #27ace8;
    background-position: -25px 0;
  }
  #widemode .switch-label {
    display: none;
  }
  #widemode #despoil,
  #widemode #widemode-switch {
    padding: 0 10px;
    width: auto;
  }
  a[title="Читать дальше"] {
    background-color: #eee;
    border: 1px dashed #999;
    display: inline-block;
    padding: 6px 10px;
  }
  #up-switch,
  #down-switch {
    display: inline-block;
    width: 25px;
    height: 25px;
  }

  h2.page-header {
    color: #333;
    font-size: 27px;
    line-height: 1em;
    font-weight: normal;
    margin-bottom: 10px;
  }
  h2.page-header i {
    vertical-align: middle;
  }
  h2.header-table {
    margin-bottom: 10px;
    font-size: 22px;
    font-weight: normal;
  }
  h2.header-table a {
    color: #275ec2;
  }
  h2.header-table span {
    color: #949aa1;
  }
  #header .new-messages {
    position: relative;
    color: #fff;
    padding: 3px;
    border-radius: 50%;
    background-color: #008000;
    font-weight: bold;
    font-size: 14px;
    text-decoration: none;
  }
  #header .new-messages i {
    position: relative;
    top: -1px;
  }
  #header .auth {
    position: absolute;
    right: 20px;
    top: 15px;
    margin: auto 0;
    font-size: 18px;
    flex-wrap: nowrap;
    justify-content: flex-end;
  }
  #header .auth li {
    margin: 10px 15px 0 0;
  }
  #header .dropdown-user {
    display: inline-block;
    position: absolute;
    z-index: 200;
    right: 20px;
    top: 50%;
    margin: -35px 0 0 0;
    padding: 0 70px 0 0;
    min-height: 70px;
    text-align: right;
    line-height: 22px;
  }
  #header .dropdown-user .dropdown-user-menu {
    justify-content: flex-end;
  }
  #header .dropdown-user .dropdown-user-trigger {
    text-decoration: underline;
    cursor: pointer;
  }
  #header .dropdown-user .username {
    font-size: 15px;
    text-decoration: underline;
    white-space: nowrap;
    display: inline-block;
    font-weight: bold;
  }
  #header .dropdown-user .username:hover {
    color: #aaa;
  }
  #header .dropdown-user .avatar {
    position: absolute;
    right: 0px;
    border: 1px solid #ccc;
    border-radius: 5px;
    width: 64px;
    height: 64px;
  }
  .mozilla #header .new-messages i {
    top: 0;
  }
  .dropdown-user-menu {
    list-style-type: none;
  }
  .dropdown-user-menu li {
    display: inline-block;
    margin-left: 5px;
  }
  .dropdown-user-menu li.item-stat .rating.negative {
    color: #666;
  }
  .dropdown-user-menu li.item-stat .strength {
    color: #666;
    margin-right: 8px;
  }
  .dropdown-user-menu .dropdown-user-menu li.item-stat .rating {
    color: #666;
  }
  .dropdown-user-menu .dropdown-user-menu li.item-messages .new {
    position: absolute;
    top: 15px;
    right: 7px;
    cursor: pointer;
    color: #fff;
    border-radius: 15px;
    border: 2px solid #eabe21;
    line-height: 1em;
    padding: 2px 7px 3px;
  }
  .button-write {
    color: #da5252;
  }
  .block-type-pseudomenu {
    margin-bottom: 0 !important;
  }
  .block-type-pseudomenu ul {
    font-size: 18px;
    list-style-type: none;
  }
  .block-type-pseudomenu ul li {
    padding-right: 5px;
    display: inline-block;
  }
  .block.block-type-search {
    margin-bottom: 0;
    padding: 8px;
  }
  .search-header {
    display: inline-block;
    width: 100%;
  }
  .search-header .search-header-form {
    position: relative;
  }
  .search-header .search-header-form .input-text {
    padding: 4px 25px 4px 5px;
    width: 100%;
  }
  .search-header .search-header-form .input-submit {
    position: absolute;
    top: 5px;
    right: 5px;
    width: 16px;
    height: 16px;
    border: none;
    background: url("https://cdn.everypony.ru/storage/02/43/83/2017/08/22/d1b7550e9c.png") -115px -55px no-repeat;
  }
  .stat-performance {
    width: auto;
    margin: 0 auto 20px;
    padding: 15px 20px;
    background: #fff;
    border-top: 1px solid #eee;
    border-bottom: 1px solid #eee;
  }
  .stat-performance table td {
    width: 200px;
    vertical-align: top;
  }
  .stat-performance h4 {
    font-weight: bold;
  }
  .userfeed-get-more {
    display: block;
    width: 90%;
    margin: 0 auto;
    text-align: center;
    height: 2.5em;
    padding-top: 1em;
  }
  .userfeed-get-more:hover {
    background: #f0f7fd;
  }
  .stream-get-more {
    margin-top: 50px;
    border-radius: 5px;
    display: block;
    text-align: center;
    padding: 12px 0;
    color: #258fa7;
    text-decoration: none;
    background: #eefbfc;
    background: linear-gradient(to top, #eefbfc 0%, #d3edf8 100%);
    cursor: pointer;
  }
  .stream-get-more:hover {
    background: #eefbfc;
    color: #258fa7;
  }
  .stream-get-more.loader {
    background-image: url("https://cdn.everypony.ru/storage/02/43/83/2017/08/22/cc6a3cc028.gif");
    background-repeat: no-repeat;
    background-position: 50% 50%;
  }
  .stream_loading,
  .userfeed_loading {
    background: #f7f7f7 url("https://cdn.everypony.ru/storage/02/43/83/2017/08/22/cc6a3cc028.gif") no-repeat center;
  }
  .stream-comment-preview {
    position: relative;
    padding: 5px 10px 6px;
    background: #fefef1;
    color: #363636;
    margin-top: 13px;
    font-size: 11px;
    border-radius: 3px;
    -webkit-box-shadow: 0 2px 4px #e8e8c0 inset;
    box-shadow: 0 2px 4px #e8e8c0 inset;
  }
  .stream-comment-preview:after {
    display: block;
    content: "";
    position: absolute;
    top: -12px;
    left: 15px;
    width: 0;
    height: 0;
    border-width: 6px;
