css:
@-moz-document domain(techmeme.com)
{
body {
	max-width: 90em!important;
	background: #f2f2f2
}

.item {
	padding: 10px 30px 20px;
	margin-bottom: 10px;
	background: #fff;
	border: none;
	border-radius: 6px
}

.podcast{
	display:none;
}

.item:hover {
	box-shadow: 0 10px 40px -20px rgba(0,0,0,.3);
	margin: -5px -5px 5px;
	padding: 15px 35px 25px;
}

#topcol3 .item {
	margin: 0px -5px 10px;
	padding: 10px 20px
}

.dxcol{
	cursor: pointer;
}

.ed A:hover {
	background: 0 0;
	text-decoration: none;
	opacity: .8
}

#sponsorposts,div#hiring,div#sponsors {
	display: none
}

#topcol2 {
	width: 0!important
}

#topcol3 {
	width: 98%!important
}

#topcol23 {
	width: 35%!important
}

#topcol1 {
	width: 60%!important
}

#countercol {
	background: 0 0
}
}
