css:
@-moz-document domain("wikipedia.org") {
.mediawiki, #mw-page-base {
	background: #fff;
}
.mw-body {
	border: 0;
}
body.action-edit {
	margin-top: 15px;
}
.mw-body h1, .mw-body h2, .mw-body h3, .mw-body h4, .mw-body h5, .mw-body h6 {
	border-bottom-color: #ddd;
	border-bottom-width: 0.05rem;
}
.mw-body h1, .mw-body-content h1, .mw-body-content h2 {
	line-height: 1.5;
}
.vectorTabs, .vectorTabs a, #mw-head .vectorMenu h3 {
	background-image: none;
}
table.ambox {
	border: 0;
	box-shadow: 0 6px 24px rgba(20, 20, 20, .12);
	margin-bottom: 14px;
}
table.ambox-style {
	background: #f7eccb;
}
.infobox, .navbox, .catlinks {
	border: 0;
	background: #fff;
	box-shadow: 0 6px 24px rgba(20, 20, 20, .12);
}
#toc, .toc, .mw-warning, .toccolours, .thumbinner {
	background: #fafafa;
	border-color: #ddd;
	background: #fff;
	box-shadow: 0 3px 14px rgba(20, 20, 20, .07);
}
table.wikitable {
	background: #fff;
	box-shadow: 0 3px 14px rgba(20, 20, 20, .07);
}
table.wikitable > tr > th, table.wikitable > * > tr > th {
	background: #eee;
}
table.wikitable > tr > th, table.wikitable > tr > td, table.wikitable > * > tr > th, table.wikitable > * > tr > td {
	border-color: #ddd;
	padding: 0.3em 0.5em
}
div#footer {
	opacity: .44;
}
div#simpleSearch {
	padding: 3px 5px;
	background: #fff;
	border: 1px solid #ddd;
	line-height: 19px;
}
div.vectorTabs ul li, div#mw-head div.vectorMenu h3 {
	background-color: #fff;
	background-image: none;
	padding: 0 5px;
	text-transform: uppercase;
}
div.vectorTabs, div.vectorTabs span {
	background-image: none;
}
div.vectorTabs, #p-personal {
	opacity: .75;
}
#mw-panel .portal h3 {
	text-transform: uppercase;
	border-bottom-color: #ddd;
	border-bottom-width: 0.05rem;
	border-bottom-style: solid;
}
#mw-panel .portal .body {
	background-image: none;
}
.mw-headline {
	font-family:'Linux Libertine', 'Georgia', 'Times', serif;
}
.noprint {
	color: #888;
}
#mw-head-base {
	height: 4em;
}
#right-navigation, #left-navigation {
	margin-top: 2.2em;
}
.mw-content-ltr .mw-editsection, .mw-content-rtl .mw-content-ltr .mw-editsection {
	margin-left: 1em;
	float: right;
	opacity: .66;
}
}
