css:
@-moz-document url-prefix('https://trello.com/') {
body .list-card-labels {
  float: right;
  top: -2px;
  right: -6px;
  max-width: 450px;
  margin: 0;
  text-align: right;
}
 
body .list-card-labels .card-label {
  float: right;
  width: auto;
  height: 16px;
  line-height: 16px;
  padding: 0 3px;
  margin: 0 0 2px 2px;
  font-size: 9px;
  border-radius: 3px;
  overflow: visible;
}

body .list-card-labels .card-label.mod-card-front{
  max-width:none;
}
 
body .list-card-details {
  padding: 4px 8px;
}
 
 
/* display card id */
 
body .card-short-id.hide {
  position: absolute;
  top: 0;
  left: 0;
  display: block;
  height: 16px;
  line-height: 16px;
  padding: 0 3px;
  font-weight: bold;
  font-size: 11px;
  background: gold;
  border-radius: 3px 0;
}
 
body .has-cover .card-short-id.hide {
  border-radius: 0 0 3px 0;
}
 
body .list-card-title {
  margin: 16px 0 10px;
}
 
 
/* bottom edit link */
 
body .list-card-operation {
  top: auto;
  right: -2px;
  bottom: -2px;
  padding: 1px;
  border-radius: 3px;
  box-shadow: 1px 1px rgba(0,0,0,.3), -1px -1px white;
}
 
body .list-card {
  margin-top: 10px;
    max-width:none;
}
 
body .list-card:first-child {
  margin-top: 2px;
}
 
 
/* display total cards count */
 
body .list-header-num-cards.hide {
  display: inline-block;
  font-size: 11px;
}


body .list-wrapper{
     width:16.5%;
}
body .list-wrapper.mod-add{
	display:none;
}

body .list-header-num-cards  {
  display: inline !important
}
















/* remove logo*/
  span.header-logo-default
  {
    display: none;
    background: none;
  }

  a.list-header-menu-icon
  {
    color: #ecf0f1;
  }

  a.open-card-composer:hover
  {
    background-color: transparent !important;
  }

  div.list-card
  {
    margin-top: 10px;
  }


  div#board div.js-list.list-wrapper{
    margin: 0;
  }

  div#board div.js-list.list-wrapper div.js-list-content{
    border-radius: 0;
    border-left: 1px solid #222;
    border-right: 1px solid #222;
  }



  div#board div.js-list.list-wrapper div.list.js-list-content div.list-header div.list-header-extras span.agile_listboard_header div.agile_badge_list {
    color:black !important;
  }

 /* Colors for diferent columns*/ 
  div#board div.js-list.list-wrapper:nth-child(1) div.list.js-list-content div.list-header
  {
    background-color: #2c3e50;
    color: #ecf0f1;
    padding: 14px;
  }

  div#board div.js-list.list-wrapper:nth-child(2) div.list.js-list-content div.list-header
  {
    background-color: #3498db;
    color: #ecf0f1;
    padding: 14px;
  }

  div#board div.js-list.list-wrapper:nth-child(3) div.list.js-list-content div.list-header
  {
    background-color: #8e44ad;
    color: #ecf0f1;
    padding: 14px;
  }

  div#board div.js-list.list-wrapper:nth-child(4) div.list.js-list-content div.list-header
  {
    background-color: #e74c3c;
    color: #ecf0f1;
    padding: 14px;
  }

  div#board div.js-list.list-wrapper:nth-child(5) div.list.js-list-content div.list-header
  {
    background-color: #2ecc71;
    color: #ecf0f1;
    padding: 14px;
  }
	/* Live production*/
  div#board div.js-list.list-wrapper:nth-child(6) div.list.js-list-content div.list-header
  {
    background-color: #2ecc71;
    color: #ecf0f1;
    padding: 14px;
  }

}
