css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("steemit.com") {

/*
Author: Harshal Lele
License: CC0
*/

  /*Colours:
    #f0e3bc text
    #00a878, #00db78 active tab text
    #f0e3bc, #8BB9E0 link text
    #ff031e warnings
    #111111 header borders
    #13191C background
    #222D32 posts
  */

  body {
	  background-color: #13191C;
	  color: #f0e3bc;
  }

  a {
    color: #f0e3bc;
  }

  a:hover {
    /*color: #8BB9E0;*/
    color: #00a7e1
  }

  .help-text {
    color: #f0e3bc;
  }

  input[type=text] {
    border: 1px solid #111111;
    background-color: black;
    color: #f0e3bc;
  }

  input[type=text]:focus {
    border: 1px solid #111111;
    background-color: black;
    color: #f0e3bc;
  }

  input[type=password] {
    border: 1px solid #111111;
    background-color: black;
    color: #f0e3bc;
  }

  input[type=password]:focus {
    border: 1px solid #111111;
    background-color: black;
    color: #f0e3bc;
  }

  input[type=url] {
    border: 1px solid #111111;
    background-color: black;
    color: #f0e3bc;
  }

  input[type=url]:focus {
    border: 1px solid #111111;
    background-color: black;
    color: #f0e3bc;
  }

  textarea {
    border: 1px solid #111111;
    background-color: black;
    color: #f0e3bc;
  }

  textarea:focus {
    border: 1px solid #111111;
    background-color: black;
    color: #f0e3bc;
  }

  textarea:active {
    border: 1px solid #111111;
    background-color: black;
    color: #f0e3bc;
  }

  select {
    border: 1px solid #111111;
    background-color: black;
    color: #f0e3bc;
  }

  select:focus {
    border: 1px solid #111111;
    background-color: black;
    color: #f0e3bc;
  }

  /*Header*/

  .Header .Header__top {
    background: #13191C;
    border-bottom: 1px
  }

  .Header .menu {
    background-color: inherit;
  }

  li.Header__userpic .Userpic {
    border: 1px solid #111111;
  }

  .Header .menu .hamburger {
    background: black;
    border: 1px solid black;
  }

  .VerticalMenu > li.title {
    border-bottom: 1px solid #111111;
    color: #00a878;
  }

  .Header .Header__sub-nav {
    background-color: #13191C;
    border-bottom: 1px solid #111111;
    padding: 10px 10px 10px 10px;
  }

  .Header .HorizontalMenu {
    background-color: #13191C;
    margin: 0px;
    padding: 0px;
  }

  .Header .HorizontalMenu > li {
    color: #f0e3bc;
    background-color: #13191C;
    margin: 2px 10px -1px 10px;
    padding: 10px 10px 10px 10px;
    border: 1px;
    border-radius: 10px 10px 0px 0px;
    text-transform: capitalize;
  }

  .Header .HorizontalMenu > li > .Header__search{
    color: #f0e3bc;
  }

  .Header .HorizontalMenu > li > a {
    color: #f0e3bc;
  }

  .Header .HorizontalMenu > li > a:hover {
    background-color: #13191C;
    /*color: #7AC533; */
    color: #00a878
  }

  .Header .HorizontalMenu .active {
    background-color: #13191C;
    border-bottom: 1px solid black;
  }

  .Header .HorizontalMenu .active > a {
    border: 1px solid #111111;
    background-color: #13191C;
    color: #00a878 !important;
    outline: 0;
  }

  .Header__sub-nav li > a {
    background-color: #383635;
    border: 1px solid #111111;
  }

  .UserProfile__banner {
    border-bottom: 1px solid #13191C;
  }

  .UserProfile__banner > div > h1 {
    color: #f0e3bc;
  }

  .UserProfile__banner .UserProfile__buttons .button:first-of-type {
    border: 1px solid #13191C;
    background-color: #13191C;
    color: #00a878;
  }

  .UserProfile__banner .UserProfile__buttons .button:first-of-type:hover {
    background-color: black;
  }

  .UserProfile__banner .UserProfile__buttons .button:last-of-type {
    border: 1px solid #13191C;
    background-color: #13191C;
    color: #ff031e;
  }

  .UserProfile__banner .UserProfile__buttons .button:last-of-type:hover {
    background-color: black;
  }

  .UserProfile__banner .UserProfile__stats a {
    color: #f0e3bc;
  }

  .UserProfile__banner .UserProfile__stats > span {
    border-color: #13191C;
  }

  .UserProfile__top-nav {
    background-color: #13191C;
    margin: 0px 0px 0px 0px !important;
    padding: 0px 0px 0px 0px !important;
    border-bottom: 1px solid #13191C;
  }

  .UserProfile__top-nav .row .columns {
    overflow-x: unset !important;
  }

  .UserProfile__top-menu {
    margin: 0px 0px 0px 0px !important;
    padding: 0px 0px 0px 0px !important;
    max-width: none !important;
  }

  .UserProfile__top-menu .columns {
    max-width: none !important;
  }

  .UserProfile__top-menu .columns .menu a {
    color: #f0e3bc;
    background-color: #13191C;
    margin: 10px 10px -1px 10px;
    padding: 0px 10px 0px 10px;
    border: 1px solid #13191C;
    border-radius: 0px;
  }

  .UserProfile__top-nav .menu > li > a:hover {
    background-color: #13191C;
    color: #00a878;
  }

  .UserProfile__top-nav .menu > li > a.active {
	  background-color: #13191C;
	  color: #00a878;
    border-bottom: 1px solid #13191C;
    outline: 0;
  }

  .dropdown-pane {
    /*border: 1px solid #111111;*/
    border: 1px solid #13191C;
    border-radius: 0px;
  }

  .VerticalMenu {
    background-color: #13191C;
    border: 1px solid #13191C;
  }

  .VerticalMenu > li > a {
    border: 1px solid #13191C;
	  color: #CDCAC1;
  }

  .VerticalMenu > li > a:hover {
    background-color: #222D32;
  }

  /* Followers */

  .UserList {
    border: 1px solid #13191C;
    padding: 10px;
    background-color: #13191C;
    margin-bottom: 10px;
  }

  .pager .button {
    border: 1px solid #13191C;
    background-color: #13191C;
    color: #00a878;
  }

  .pager .button:hover {
    border: 1px solid #222D32;
    background-color: #222D32;
    color: #00db78;
  }

  .UserList > div > div > h3 {
    color: #00a878;
  }

  .UserList > div > div > table {
    border: 1px solid #13191C;
  }



  .UserList > div > div > table > tbody > tr{
    background-color: #222D32;
  }

  .UserList > div > div > table > tbody > tr .button:first-of-type {
    border: 1px solid #222D32;
    border-radius: 0px;
    background-color: #222D32;
    color: #00a878;
  }

  .UserList > div > div > table > tbody > tr .button:first-of-type:hover {
    background-color: #13191C;
    color: #00a878;
  }

  .UserList > div > div > table > tbody > tr .button:last-of-type {
    border: 1px solid #222D32;
    border-radius: 0px;
    background-color: #222D32;
    /*color: #EC5840;*/
    color: #ff031e;
  }

  .UserList > div > div > table > tbody > tr .button:last-of-type:hover {
    background-color: #13191C;
    color: #ff031e;
  }

  /* Rewards Page */

  .UserProfile__tab_content .UserWallet .row {
    background-color: #13191C !important;
    border: 1px solid #13191C !important;
    border-radius: 0px !important;
   }

  .UserProfile__tab_content .UserWallet .row .column{
    background-color: #13191C !important;
    border: 1px solid #13191C !important;
    border-radius: 0px !important;
   }

  .UserProfile__tab_content .UserWallet__balance.UserReward__row + div + div > div > table > tbody > tr:nth-child(odd) {
    background-color: #222D32;
  }

  .UserProfile__tab_content .UserWallet__balance.UserReward__row + div + div > div > table > tbody > tr:nth-child(even) {
    background-color: #222D32;
  }

  .TransferHistoryRow__text > a {
   /*color: #547CAA; */
    color: #f0e3bc;
  }

  /* Wallet - Balances */

  .reveal {
    background-color: #222D32;
    color: #f0e3bc;
    border: 1px solid #222D32;
    border-radius: 0px;
    padding: 0px;
  }

  .reveal > form {
    background-color: #222D32;
    border: 1px solid #222D32;
    border-radius: 0px;
  }

  .reveal > form > div+div > div > label {
    color: #00a878;
  }

  .reveal > form > div+div > div > input {
    border: 1px solid #111111;
    background-color: black;
    color: #f0e3bc;
  }

  .reveal > div {
    background-color: #222D32;
    margin: 0px;
    padding: 10px;
    border: 1px solid #222D32;
    border-radius: 0px;
  }

  .reveal > div > h3 {
    color: #00a878;
  }

  .reveal > div .input-group-label  {
    background-color: #13191C;
    color: #f0e3bc;
    border: 1px solid #13191C;
  }

  .reveal > div .input-group-field  {
    background-color: #13191C;
    color: #f0e3bc;
    border: 1px solid #13191C;
  }

  .reveal > div .input-group > input  {
    background-color: #13191C;
    color: #f0e3bc;
    border: 1px solid #13191C;
  }

  .reveal > div input  {
    background-color: #13191C;
    color: #f0e3bc;
    border: 1px solid #13191C;
  }


  .reveal .button {
    border: 1px solid #222D32;
    background-color: #222D32;
    color: #00a878;
    text-decoration: none !important;
  }

  .reveal .button:focus {
    border: 1px solid #13191C;
    background-color: #13191C;
    color: #00a878;
  }

  .reveal .button:hover {
    border: 1px solid #13191C;
    background-color: #13191C;
    color: #00a878;
  }



  .ConfirmTransactionForm > h4 {
    color: #00a878;
  }

  .LoginForm > center > h3 {
    color: #00a878;
  }

  .LoginForm label {
    color: #f0e3bc;
  }

  .LoginForm .info {
    color: #f0e3bc;
  }

  .UserWallet__claimbox {
    border: 1px solid #222D32;
    border-radius: 10px;
    background-color: #222D32;
    padding-left: 10px;
    padding-right: 10px;
  }

  .UserWallet__claimbox .button {
    border: 1px solid #13191C;
    border-radius: 10px;
    background-color: #13191C;
    color: #f0e3bc;
    margin-right: 0px !important;
  }

  .UserWallet__claimbox .button:hover {
    background-color: #222D32;
    color: #00a878;
    border: 1px solid #222D32;
  }

  .UserWallet__claimbox .button:focus {
    border: 1px solid #222D32;
    background-color: #222D32;
    color: #00a878;
  }

  .UserWallet > div:nth-last-child(1) {
    border: 1px solid #13191C;
    border-radius: 0px;
    background-color: #13191C;
    margin-bottom: 10px;
  }

  .UserWallet > div:nth-last-child(1) > div > h4 {
    margin-top: 20px;
    color: #00a878;
  }

  .UserWallet > div:nth-last-child(1) > div > table {
    border: 1px solid #13191C;
  }

  .UserWallet > div:nth-last-child(1) > div > table > tbody > tr{
    background-color: #222D32;
  }

  /*
  .UserWallet > div:nth-last-child(1) > div > table > tbody > tr:nth-child(even) {
    background-color: #1F1F1F;
  }
  */

  .UserWallet > div:nth-last-child(2) {
    display: none;
  }

  .UserWallet > div:nth-last-child(4) {
    border: 1px solid #13191C;
    border-top: none;
    border-radius: 0px;
    padding: 5px 10px 10px 10px;
    background-color: #222D32;
  }

  .UserWallet > div:nth-last-child(4) > div{
    border: 1px solid #222D32;
    border-radius: 0px;
    padding: 10px;
    background-color: #222D32;
    color: #00a878;
  }

  .UserWallet > div:nth-last-child(4) > div:first-of-type .secondary {
    color: #f0e3bc;
  }

  .UserWallet > div:nth-last-child(5) {
    border: 1px solid #13191C;
    padding: 5px 10px 5px 10px;
    background-color: #222D32 !important;
  }

  .UserWallet > div:nth-last-child(5) > div {
    border: 1px solid #222D32;
    border-radius: 0px;
    padding: 10px;
    background-color: #222D32;
    color: #00a878;
  }


  .UserWallet > div:nth-last-child(5) > div:first-of-type .secondary {
    color: #f0e3bc;
  }

  /*

  .UserWallet > div:nth-last-child(5) > div:last-of-type {
    border: 1px solid #111111;
    border-left: none;
    border-radius: 0px 10px 10px 0px;
    padding: 10px;
    background-color: #1F1F1F;
  }

  */

  .UserWallet > div:nth-last-child(6) {
    border: 1px solid #13191C;
    padding: 5px 10px 5px 10px;
    background-color: #222D32;
  }

  .UserWallet > div:nth-last-child(6) > div{
    border: 1px solid #222D32;
    border-radius: 0px;
    padding: 10px;
    background-color: #222D32;
    color: #00a878;
  }

  .UserWallet > div:nth-last-child(6) > div:first-of-type .secondary {
    color: #f0e3bc;
  }

  /*
  .UserWallet > div:nth-last-child(6) > div:last-of-type {
    border: 1px solid #111111;
    border-left: none;
    border-radius: 0px 10px 10px 0px;
    padding: 10px;
    background-color: #1F1F1F;
  }
  */

  .UserWallet > div:nth-last-child(7) {
    border: 1px solid #13191C;
    padding: 5px 10px 5px 10px;
    background-color: #222D32 !important;
  }

  .UserWallet > div:nth-last-child(7) > div{
    border: 1px solid #222D32;
    border-radius: 0px;
    padding: 10px;
    background-color: #222D32;
    color: #00a878;
  }

  .UserWallet > div:nth-last-child(7) > div:first-of-type .secondary {
    color: #f0e3bc;
  }

  /*
  .UserWallet > div:nth-last-child(7) > div:last-of-type {
    border: 1px solid #111111;
    border-left: none;
    border-radius: 0px 10px 10px 0px;
    padding: 10px;
    background-color: #1F1F1F;
  }
  */

  .UserWallet > div:nth-last-child(8) {
    border: 1px solid #13191C;
    border-radius: 0px;
    padding: 10px 10px 5px 10px;
    background-color: #222D32;
  }

  .UserWallet > div:nth-last-child(8) > div{
    border: 1px solid #222D32;
    border-right: none;
    border-radius: 0px;
    padding: 10px;
    background-color: #222D32;
    color: #00a878;
  }

  .UserWallet > div:nth-last-child(8) > div:first-of-type .secondary {
    color: #f0e3bc;
  }

  /*
  .UserWallet > div:nth-last-child(8) > div:last-of-type {
    border: 1px solid #111111;
    border-left: none;
    border-radius: 0px 10px 10px 0px;
    padding: 10px;
    background-color: #1F1F1F;
  }
  */

  .WalletSubMenu {
    border: none;
    margin: 10px 0px 0px 0px;
  }

  .WalletSubMenu > li > a {
    border: 1px solid #13191C;
    border-radius: 0px;
    background-color: #13191C;
    color: #CDCAC1;
    margin: 10px 10px -1px 10px;
    padding: 0px 10px 0px 10px;
  }

  .WalletSubMenu > li > a:hover {
    color: #00a878;
  }

  .WalletSubMenu > li > a.active {
    border-bottom: 1px solid #222D32;
    background-color: #222D32;
    color: #00a878;
    outline: 0;
  }

  .UserWallet__buysp.button.hollow {
    border: 1px solid #13191C;
    border-radius: 0px;
    margin: 19px 10px -15px 10px;
    padding: 5px 10px 5px 10px;
    background-color: #13191C;
    color: #f0e3bc;
  }

  .UserWallet__buysp.button.hollow:hover {
    color: #00a878;
  }

  /* Wallet - Permissions & Password */

  .UserProfile__top-nav + div > div > a.float-right {
    float: none !important;
    width: 100px;
    margin-left: auto;
    margin-right: auto;
  }

  .UserProfile > div > div > br {
    display: none;
  }

  .UserProfile > div > div > div.row:nth-of-type(2) {
    background-color: #222D32;
    border: 1px solid #222D32;
    border-radius: 0px;
    padding: 10px 10px 0px 10px;
    margin-bottom: 10px;
  }

  .UserProfile > div > div > div.row:nth-of-type(2) > div {
    background-color: #222D32;
    border: 1px solid #222D32;
    border-radius: 0px;
    padding: 10px 10px 0px 10px;
    margin-bottom: 10px;
    flex: 0 0 100% !important;
    max-width: 100% !important;
  }

  .UserProfile > div > div > div.row:nth-of-type(2) > div > span > div > div > label {
    color: #00a878;
  }

  .UserProfile > div > div > div.row:nth-of-type(2) > div .button {
    border: 1px solid #222D32;
    background-color: #222D32;
    width: 9rem;
  }

  .UserProfile > div > div > div.row:nth-of-type(2) > div .button a {
    color: #f0e3bc;
    font-size: 0.9rem;
  }

  .UserProfile > div > div > div.row:nth-of-type(2) > div .button a:hover {
    color: #00a878;
  }

  .ChangePassword__rules > hr {
    border: 1px solid #13191C;
  }

  .ChangePassword > form > h4 {
    color: #00a878;
  }

  .ChangePassword > form > label {
    color: #00a878;
  }

  .ChangePassword > form > label > span.secondary {
    color: #00a878;
  }

  .ChangePassword > form > div > label {
    color: #00a878;
  }

  .ChangePassword .float-right a {
    margin: 0px 0px 10px 0px;
    padding: 10px;
    border: 1px solid #222D32 !important;
    background-color: #222D32;
    color: #f0e3bc;
    font-size: 0.9rem;
    font-family: sans-serif;
    line-height: 1;
    display: inline-block;
    border-radius: 3px;
    vertical-align: middle;
    text-align: center;
    width: 20rem;
  }

  .ChangePassword .float-right a:hover {
    color: #00a878;
  }

  .UserProfile > div > div > div.row:nth-of-type(2) > div > span > form > center {
    width: 20rem;
    margin-left: auto;
    margin-right: 10px
  }

  .UserProfile > div > div > div.row:nth-of-type(2) > div > span > form .button {
    border: 1px solid #222D32;
    background-color: #222D32;
    color: #f0e3bc;
    margin: 10px;
    padding: 10px;
    width: 20rem;
  }

  .UserProfile > div > div > div.row:nth-of-type(2) > div > span > form .button:hover {
    color: #00a878;
  }

  .UserProfile > div > div > div.row:nth-of-type(2) > div > span > form > div:last-of-type {
    width: 20rem;
    margin-left: auto;
    margin-right: 10px;
  }

  /* Settings */

  .UserProfile__tab_content .Settings > div > form {
    background-color: #13191C;
    border: 1px solid #13191C;
    border-radius: 0px;
    padding: 10px;
    max-width: 100% !important;
    flex: 100% !important;
  }

  .UserProfile__tab_content .Settings > div > form > h3 {
    color: #00a878;
  }

  .UserProfile__tab_content .Settings > div > form > label {
    background-color: #222D32;
    color: #00a878;
    border: 1px solid #222D32;
    border-radius: 0px;
    padding: 10px;
  }

  .UserProfile__tab_content .Settings > div > form > div {
    background-color: #222D32;
    border: 1px solid #222D32;
    border-top: none;
    border-radius: 0px;
    padding: 10px;
  }

  .UserProfile__tab_content .Settings > div > form .button {
    border: 1px solid #13191C;
    background-color: #13191C;
    padding: 10px;
    color: #f0e3bc;
  }

  .UserProfile__tab_content .Settings > div > form .button:hover {
    color: #00a878;
  }

  .UserProfile__tab_content .Settings > div + div {
    background-color: #222D32;
    border: 1px solid #13191C;
    border-radius: 0px;
    margin: 10px -15px 10px -15px;
    padding: 10px 0px 10px 0px;
  }

  .UserProfile__tab_content .Settings > div + div > div > h3 {
    color: #00a878;
  }

  .UserProfile__tab_content .Settings > div + div > div > select {
    background-color: black;
    color: #f0e3bc;
    border: 1px solid #111111;
  }

  .UserProfile__tab_content .Settings > div + div .button {
    border: 1px solid #222D32;
    background-color: #222D32;
    margin-top: 10px;
    padding: 10px;
    color: #f0e3bc;
  }

  .UserProfile__tab_content .Settings > div + div .button:hover {
    color: #00a878;
  }

  .UserProfile__tab_content .Settings div:last-of-type > br {
    display: none;
  }

  /* Posts */

  .PostsList__summaries {
    background-color: #13191C;
    padding: 10px;
    border: 1px solid #13191C;
    border-radius: 0px;
  }

  .PostSummary.hentry  {
    margin: 0px 0px 10px 0px;
    padding: 10px 10px 10px 10px;
    border: 1px solid #222D32;
    border-radius: 0px;
    background-color: #222D32;
  }

  .PostSummary__image {
    background-color: #222D32;
    border: 1px solid #222D32;
  }

  .PostSummary .PostSummary__nsfw-warning {
    border: 1px solid #222D32;
