css:
@-moz-document domain("tweetdeck.twitter.com"), domain("twitter.com") {

a.js-media-image-link.block.med-link.media-item {
    background-size: contain;
    background-color: #222426;
}

.permalink-tweet .AdaptiveMedia.is-square, .AdaptiveMedia.is-square {
    max-height: none;
}

.AdaptiveMedia-singlePhoto .AdaptiveMedia-photoContainer {
    position: static;
}


.AdaptiveMedia-singlePhoto .AdaptiveMedia-photoContainer img {
    top: 0 !important;
}

.AdaptiveMedia-singlePhoto {
    padding-top: 0 !important;
}

}
