css:
@-moz-document domain(example.com)
{
/* NTV news night mode by Mihyaz v1.0*/

body.main {
     background-color: #1F1F1F;
     color: #d6d1d1;
     min-width: 320px;
}
.content_wrapper>header {
     background-color: #242323 !important
}
body.main #story div.content article {
     border: 1px solid rgba(0, 0, 0, 0);
     padding: 5px;
     background-color: rgba(39, 38, 38, 0);
     line-height: 20px;
     margin-bottom: 10px;
     -webkit-box-sizing: border-box;
     -moz-box-sizing: border-box;
     box-sizing: border-box;
}
.card.grid-wrapper .grid>.frame>ul>li>.wrap>a>figure>figcaption,
.card.grid-wrapper .grid>.frame>ul>li>a>figure>figcaption {
     color: #4d4d4d;
     padding: 0;
     background-color: rgb(31, 31, 31);
     position: relative;
     float: left;
     width: 45%;
     margin-left: 10px;
}
.card.grid-wrapper>.grid>.frame>ul>li {
     width: 100%;
     height: 125px;
     border: 1px solid rgba(16, 16, 16, 0);
     padding: 4px;
     background-color: #1f1f1f;
     margin-bottom: 10px;
}
body.main div>nav.main {
     background: repeat-x 0 0 transparent;
     padding: 1px 0;
}
.subheadline.grid-wrapper .swiper-pagination-bullets {
     background-color: rgba(227, 230, 228, 0.37);
     bottom: 0!important;
     color: rgb(4, 8, 4);
     font-size: 16px;
     height: 40px;
     left: 0!important;
     line-height: 40px;
     opacity: 1;
     text-align: center;
     width: 100%;
     border: 1px solid rgb(0, 0, 0);
     border-top: none;
     -webkit-box-sizing: border-box;
     -moz-box-sizing: border-box;
     box-sizing: border-box;
}
.subheadline.grid-wrapper .grid>.frame>ul>li {
     border: 1px solid rgba(31, 30, 30, 0.98)!important;
     height: 240px!important;
     box-sizing: border-box;
     -webkit-box-sizing: border-box;
     -moz-box-sizing: border-box;
}
.default.tile.grid-wrapper>.grid>.frame>ul>li:not(.election2015):not(.election2015Mini):not(.election2017):not(.election2017Chart),
.light.tile.grid-wrapper>.grid>.frame>ul>li:not(.election2015):not(.election2015Mini):not(.election2017):not(.election2017Chart) {
     border: 1px solid rgba(212, 30, 30, 0);
     padding: 4px;
     background-color: rgba(0, 0, 0, 0);
}
.headline.grid-wrapper>.grid>.frame>ul>li {
     border: 1px solid rgba(216, 216, 216, 0);
     padding: 4px;
     background-color: rgba(255, 255, 255, 0);
}
.grid-wrapper .grid.headline ul.previews {
     margin: 0 auto;
     padding: 0 4px 4px 4px;
     background-color: rgba(248, 248, 248, 0);
     -webkit-box-sizing: border-box;
     -moz-box-sizing: border-box;
     box-sizing: border-box;
     border: 1px solid rgba(216, 216, 216, 0);
     border-top: none;
}
.default.carousel.grid-wrapper>.grid>.frame>ul>li,
.single.carousel.grid-wrapper>.grid>.frame>ul>li {
     padding: 4px;
     background-color: rgba(255, 255, 255, 0);
     border-right: 1px solid rgba(216, 216, 216, 0);
}
.default.carousel.grid-wrapper .grid>.frame>ul>li>.wrap>a>figure>figcaption,
.default.carousel.grid-wrapper .grid>.frame>ul>li>a>figure>figcaption {
     font-size: 16px;
     color: #7d905e;
     background-color: rgba(103, 103, 103, 0.19);
     height: 50px;
     overflow: hidden;
}
.grid-wrapper .grid.headline ul.previews>li>a {
     display: block;
     float: left;
     cursor: pointer;
     border-top: 5px solid rgba(255, 255, 255, 0);
     position: relative;
}
.grid-wrapper .grid.headline ul.previews>li>a.active,
.grid-wrapper .grid.headline ul.previews>li>a:hover {
     border-top-color: #8a3838;
     border-width: 5px;
     border: 5px;
}
body.main .finance-widget .finance-data ul li a span.title {
    color: #cebebe;
    font-size: 12px;
    padding-left: 18px;
}
}
