css:
@-moz-document domain("web.whatsapp.com")
{
/*///////estilo de gloria//////////*/
.pane-header.pane-list-header {
    -webkit-flex: none;
    flex: none;
    height: 59px;
    width: 100%;
    background-color: #f10d0d;
    box-sizing: border-box;
    padding: 10px;
    position: relative;
    z-index: 1000;
    padding-right: 16px;
    padding-left: 15px;
    -webkit-justify-content: flex-end;
    justify-content: flex-end;
}
.list-search {
    -webkit-flex: none;
    flex: none;
    height: 49px;
    box-sizing: border-box;
    padding: 10px;
    position: relative;
    z-index: 100;
    transition: box-shadow 0.18s ease-out,background-color 0.18s ease-out;
    transition-delay: 0.12s;
    padding: 0px;
    background-color: #f10d0d;
}
.pane-header {
    -webkit-flex: none;
    flex: none;
    height: 59px;
    width: 100%;
    background-color: #f10d0d;
    box-sizing: border-box;
    padding: 10px;
    position: relative;
    z-index: 1000;
}
.chatlist .chat.active {
    background-color: #ea3ee7;
}
.chatlist .chat.active:after {
    border-top-color: #29b6f6;
}
.chatlist .chat-body, .chatlist .header-body {
    border-top: 2px solid #29b6f6;
    padding-right: 15px;
}
.chat {
    position: relative;
    cursor: pointer;
    background-color: rgb(190, 51, 239);
}
.block-compose {
    -webkit-align-items: flex-end;
    align-items: flex-end;
    background-color: #f10d0d;
    border-left: 2px solid rgb(41, 182, 245);
    box-sizing: border-box;
    display: -webkit-flex;
    display: flex;
    -webkit-flex-direction: row;
    flex-direction: row;
    max-width: 100%;
    padding: 10px 19px;
    position: relative;
    z-index: 2;
}
.pane-chat-body {
    display: -webkit-flex;
    display: flex;
    -webkit-flex-direction: column;
    flex-direction: column;
    overflow-x: hidden;
    overflow-y: scroll;
    z-index: 100;
    box-sizing: border-box;
    border-left: 2px solid #29b6f5;
    transition: background .3s ease-out .1s;
    background-color: rgba(190, 51, 239, 0.85);
}
.chatlist .chat:hover {
    background-color: rgba(234, 62, 231, 0.51);
}
*::-webkit-scrollbar-thumb {
    background-color: rgb(41, 182, 246);
}
.pane-chat-header:after, .list-search:after {
    position: absolute;
    content: '';
    width: 100%;
    height: 1px;
    left: 0;
    bottom: -1px;
    background-color: rgb(41, 182, 245);
    border-bottom: 1px solid rgb(41, 182, 246);
}
.pane-chat-header {
    padding-left: 16px;
    padding-right: 15px;
    display: -webkit-flex;
    display: flex;
    box-sizing: border-box;
    border-left: 2px solid rgb(41, 182, 246);
}
/* Common */
.app-wrapper-web .app {
width: 1347px;
}
.three .pane-three {
min-width: 250px;
}
.message-system {
visibility: hidden;
height: 0px;
}
/* left panel controls */
.pane-list-header {
padding-right: 200px;
}
.menu-horizontal>span>.menu-item {
padding: 0px;
}
/*side bar size change*/
.pane-one {
flex-basis: 95px;
min-width: 355px;
transition: flex-basis 0.1s, min-width 0.1s;
}
.pane-one:hover {
flex-basis: 355px;
}
.app .pane-two {
width: 100%;
}
.pane-header .chat-title {
color: #000;
}
.pane-chat .icon-user-default{
filter: blur(5px); 
-webkit-filter: blur(5px); 
-moz-filter: blur(5px);
-o-filter: blur(5px); 
-ms-filter: blur(5px);
}
.pane-chat .icon-user-default:hover{
filter:none;
-webkit-filter: none; 
-moz-filter: none;
-o-filter: none; 
-ms-filter: none;
zoom: 150%;
}
/* Compose Adjustments */
.input-placeholder {
font-size: 14px;
}
.block-compose {
padding: 9px 6px;
}
.block-compose .btn-emoji {
margin-right: 5px;
margin-bottom: 7px
}
.block-compose .input-container {
margin-right: -17px;
}
/*///////////////////////////////////////////////////////*/
/* Chatlist */
/* chat general */
.chatlist .avatar {
height: 35px !important;
width: 35px !important;
filter: blur(5px);
-webkit-filter: blur(5px); 
-moz-filter: blur(5px);
-o-filter: blur(5px); 
-ms-filter: blur(5px);
}
.chatlist .avatar:hover {
zoom: 200%;
filter: none;
	-webkit-filter: none; 
-moz-filter: none;
-o-filter: none; 
-ms-filter: none;
}
.avatar,
.avatar-image {
border-radius: 10%;
overflow: inherit;
}
/* chat normal list */
.chat-status {
color: rgba(0, 0, 0, 0.0);
}
.chat-drag-cover .chat-title {
color: rgba(0, 0, 0, 0.0);
}
.chat-title {
color:#000;
font: oblique bold 100% cursive;
}
/* chat normal list hover */
.chat-title:hover{color:#000;}
.chat-status:hover{
color:#000;
}
/* chat active */
.chat.active .chat-status {
color: rgba(0, 0, 0, 0.0);
}
.chat.active .chat-status {
color: rgba(255, 255, 255, 0.9);
}
.chat.active .chat-title {
color: #fff;
font: oblique bold 120% cursive;
}
.chat.active .chat-meta {
color: rgba(255, 255, 255, 0.9);
}
.chatlist .active .avatar {
zoom: 130%;
filter: none;
	-webkit-filter: none; 
-moz-filter: none;
-o-filter: none; 
-ms-filter: none;
}
/* left panel controls */
.pane-list-header {
padding-right: 200px;
}
.menu-horizontal>span>.menu-item {
padding: 0px;
}
/*//////////////////////////////////////////////////////////////////*/
/*side bar size change*/
.pane-one {
flex-basis: 95px;
min-width: 355px;
transition: flex-basis 0.1s, min-width 0.1s;
}
.pane-one:hover {
flex-basis: 355px;
}
.app .pane-two {
width: 100%;
}
/* Left alignment */
body {
margin: 0;
padding: 0;
background-image: -webkit-linear-gradient(top, #5b5b5b, #5b5b5b) !important;
font-family: 'Roboto Light', sans-serif !important;
}
.app {
position: absolute;
left: 10px;
width: 600px;
}
.app-wrapper::after {
    position: fixed;
    z-index: -1;
    background-color: rgba(195, 76, 235, 0.75);
    width: 100%;
    height: 127px;
    content: '';
    top: 0;
    left: 0;
}
}
