css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("mediafire.com")
{

  html, #container, #footer, #addfolder_to_account,
.signup_button a, #headerlogin_tab, #loginWrap,
#sidebar li a, fieldset, .selected_count_wrap,
.breadCrumbsOptions, .popup-content div,
table.split-cells tbody tr td, .invoices td
  {
    background: #000000 !important;
  }

  #selection-actions, #header, #breadcrumbs_filemgr, 
.row_container, #myfilesCurrentFolderNameWrap,
#myfiles_controlbar_outer, #loginFooter, .popup-btns,
.popup-title, #followingHome, .split-cells,
.invoices table, .invoices th
  {
    background: #000000 !important;
  }

  .itemStatus, .fileoptions, input,
#myfiles_sortbyname a, .columnControl a,
.folder_mainaction, #signup_summary,
#card_number, #signupHeader, #signupHeader .wrap
  {
    background: #999999 !important;
  }

  .grayTop, .spaceIndicator
  {
    background-image: none !important;
  }

  .grayFront
  {
    background: orange !important;
  }

  .row_container
  {
    border-color: #000000 !important;
  }

  #selection-actions, #breadcrumbs_filemgr,
.myfiles #header, .appView #header,
#myfilesCurrentFolderNameWrap
  {
    border-bottom: 2px #000000 solid !important;
  }

  #loginWrap, table
  {
    border: 1px #000000 solid !important;
  }

  .row_container, #selection-actions .selected_count_wrap::before,
.breadCrumbsOptions, .selected_count_wrap, table
  {
    box-shadow: none !important;
  }

  .breadCrumbsOptions, .selected_count_wrap
  {
    border-radius: 0 !important;
  }

  html, body, #owner_breadcrumb1, .breadcrumb,
.item-name, .extraInfo, .extraInfoDownloads,
.created, #folder_name, #folder_author,
.columnControl a, input, .togglePath,
#sidebar li a, .selected_count_wrap,
.folder_mainaction, .breadCrumbsOptions,
.planProFeatures li, .planBusinessFeatures li,
.priceUpdate, #edit_details_area h3, .label,
 #edit_details_area span, .cf h3, 
#form_account h3, .foldername, .contactLink,
#sharedAppInfo, #sharedAppInfo a
  {
    color: #FFFFFF !important;
  }
}
