css:
/* i really want this to be global */   
 @namespace url(http://www.w3.org/1999/xhtml);
         
        @-moz-document domain("4chan.org") {
        /*
             General
        */
         
        *{ /* kill outlines and underlines */
            outline: 0 !important;
            text-decoration: none !important;
            font-weight: normal !important;
            font-size: 10pt !important;
           
        }
     
        body { /* general stuff */
            background: #fafafa !important;
            /* font-family: 'Arial', sans-serif !important; */
            color: #1f1f1f !important;
            margin: auto !important;
            font-size: 10pt !important;
     
        }
        hr { /* ugly lines @ top of pages */
            padding-top: 5px !important;
            border: 0 !important;
        }
         
        a { /* Links */
            color: #666 !important;
        }
         
        /* Appearance of the menus. */
        #menu.dialog,
        .entry {
            background: #fafafa !important;
            border: 0 !important;
            color: #666 !important;
            font-size:10pt !important;
           
        }
         
        /* Appearance of the thread updater and thread stats. */
        #index-search.field,
        #updater.dialog,
        #thread-stats.dialog {
            background: transparent !important;
            color: #1e1e21 !important;
            border: 0 !important;
        }
         
        /* Thread updater menu */
        #index-search.field:hover,
        #updater.dialog:hover {
            background: #efefef !important;
            color: #1a1a1a !important;
        }
         
        /* Clean up. */
        .bottomCtrl.desktop,
        #absbot.absBotText,
        .navLinks.desktop,
        .navLinks.navLinksBot.desktop {
            display: none !important;
        }
         
        /* Hide ads? */
        div.topad.center,
        div.middlead.center,
        div.bottomad.center,
        div.ad-plea,
        .center {
            display: none !important;
        }
         
        /* Display board banner? */
        .boardBanner {
            display: none !important;
        }
         
        /* Relative to posts. */
        .post.reply {
            /* font-family: 'Arial', sans-serif !important; */
            font-size: 10pt !important;
            background: #fafafa !important;
            color: #1a1a1a !important;
            width: 100% !important;
            border-bottom: none !important;
            border-top: 3 !important;
            border-left: none !important;
            border-right: none !important;
            border-color: #cecece !important;
            box-shadow: #afafaf 0 0 0px !important;
            padding-left: 10px !important;
            padding-top: 15px !important;
            padding-right: 10px !important;
            padding-bottom: 15px !important;  
            border-style:solid;
            border-bottom-color: #d2d4d6 !important;
            -webkit-transition: all 0.3s ease-out;
            -moz-transition: all 0.3s ease-out;
            -o-transition: all 0.3s ease-out;
            transition: all 0.3s ease-out;
        }
         
        }
           
           
        .post.reply:hover {
           /* box-shadow: #dadada 0 0 6px !important; */
            border-left: 4px solid #87a4c7 !important;
            border-right: 4px solid #87a4c7 !important;
            -webkit-transition: all 0.3s ease-out;
            -moz-transition: all 0.3s ease-out;
            -o-transition: all 0.3s ease-out;
            transition: all 0.3s ease-out;
        }  
         
         
        /* Image thumbnails */
        .fileThumb img{
            box-shadow:0 0 2px #afafaf !important;
            border-radius: 5px !important;
        }
           
        /* Relative to the thread */
        .thread {
            margin-top: 30px !important;
            margin-bottom: 30px !important;
            margin-right: 118px !important;
            margin-left: 48px !important;
        }
        div.postContainer.opContainer {
            display: inline-block !important;
            width: 100% !important;
            margin: auto !important;
        }
        .inline {
            padding-left: 15px !important;
            padding-right: 15px !important;
            border: 0 !important;
        }
        .inline .post.reply {
            background-color: #fff !important;
            border: 0 !important;
        }
         
        /* Post subjects. */
        span.subject {
            color: #587384 !important;
        }
         
        /* Tripcodes. */
        span.postertrip {
            color: #96c3b6 !important;
        }
         
        .prettyprint.prettyprinted {
            margin-bottom: -7px !important;
            background: #efefef !important;
            font-family: 'Gohufont' !important;
        }
         
        .pln {
            font-family: "Gohufont", sans-serif !important;
        }
         
        /* Post numbers. */
        span.postNum a {
            color: #a5a5a5 !important;
        }
         
        /* Poster's name. */
        span.name {
            color: #3d3d3d !important;
            font-weight: normal !important;
        }
         
        /* Date and time of the posts. */
        span.dateTime {
            color: #6f6f6f !important;
        }
         
        /* Quote links. */
        a.quotelink {
            color: #aaaaaa !important;
        }
         
        /* Link to posts quoting the current post. */
        a.backlink {
            color: #3f3f3f !important;
        }
         
        /* Greentext. */
        span.quote {
            color: #6EA637 !important;
        }
         
        /* Post menu button. */
        .post .menu-button {
            float: right;
            position: relative;
            left: 0px;
        }
         
         
         
        /* Hide "hide reply" buttons. */
        .postInfo > input,
        a.hide-reply-button > span {
            display: none !important;
        }
         
        /* Relative to post info, i.e. Post name, tripcode, post date, etc. */
        .postInfo a,
        div.postInfo {
            color: #6f6f6f !important;
        }
         
        /* Fix post previews. */
        #qp {
            border-right: 1px !important;
            box-shadow: #afafaf 2 2 2 4px !important;
        }
         
         
        /* Relative to the quick reply. */
        #qr {
            background: #fafafa !important;
            color: #666 !important;
            border: none !important;
            border-radius: 0 !important;
            box-shadow: 0 0 5px #afafaf !important;
            transition: all 0.2s ease 0s;
        }
        #qr form {
            border-radius: 0 !important;
            bottom: 12px !important;
            right: 0% !important;
            width: 300px !important;
            padding: 4px !important;
            margin-top: 0px !important;
            padding-top: 10px !important;
            padding-bottom: 3px !important;
            background: #efefef !important;
        }
           
        input.captcha-input.field {
            width: 300px !important;
        }
           
        #dump-button {
            background: #fafafa !important;
            color: #666 !important;
            border: 0 !important;
        }
         
        /* Color of the fields. */
        #qr .field {
            background: #fefefe !important;
            border-color: #fafafa!important;
            color: #666 !important;
            font-size: 10pt !important;
        }
         
        /* Remove menu */
        #qr select,
        #qr > div {
            display: none !important;
        }
         
        /* Make the name field become invisible when there's something in it... */
        #qr input.field:nth-child(2) {
            background: #fefefe !important;
            color: #fefefe !important;
        }
         
        /* ... And then make it visible again when you hover over it. */
        #qr input.field:nth-child(2):hover {
            background: #fefefe !important;
            color: #fafafa !important;
        }
         
        /* Remove bar above the fields. */
        #qr > div {
            position: fixed !important;
        }
         
        /* Relative to 4chan X's menu */
        #fourchanx-settings.dialog,
        fieldset legend,
        a.tab-main.tab-selected {
            font-family: "Arial" !important;
            font-size: 10pt !important;
            background: #fafafa !important;
            color: #666 !important;
        }
        /* Relative to the thread watcher */
        #watched-threads a,
        #thread-watcher .fa{
            color: #666 !important;
            font-size: 10pt !important;
        }
         
        #thread-watcher .move {
            display: none !important;
        }
         
        /* Catalog stuff */
         
        #order-ctrl,
        .ctrl-wrap {
            display: none !important;
        }
         
        #ctrl,
        #settings span.button {
            font-family: "Arial" !important;
            color: #1a1a1a !important;
        }
         
        #threads .thread {
            background: #fafafa !important;
            box-shadow: #afafaf 0 0 2px !important;
            padding: 10px !important;
            max-height: 200px !important;
            margin-bottom: 20px !important;
            transition: 0.5s ease all 0s !important;
        }
        #threads .thread:hover {
            box-shadow: #afafaf 0 0 2px !important;
        }
         
        /* Transitions */
         
        /* Change backlinks to >> and animate them. */
        a.backlink {
            font-size: 0 !important;
            text-decoration:none!important;
        }
        .backlink:after {
            font-size: 13px !important;
            padding: 1px;
            content: /*"✔"*/"➤" !important;
            color:#afafaf!important;
            text-decoration:none!important;
            transition:0.5s ease all!important;
        }
         
        .backlink:hover::after,
        .backlink.inlined::after{
            color:#91b6c6!important;
            transition:0.5s ease all!important;
        }
         
        .backlink.inlined::after{
            content:"✔"!important;
            color:#000!important;
        }
         
        .backlink:not(:first-child) {
          transition: all .1s linear .2s;
        }
        .postContainer:hover .backlink {
          margin-left: 0rem;
          transition-delay: .1s;
        }
        /* QR animations. */
        #qr.dialog.has-captcha.autohide,
        #qr.dialog.autohide {
            top: -300px !important;
            height: 21px !important;
            transition: all 0.7s ease 0s;
        }
         
        /* Post deleteform things */
        .post .menu-button {
            opacity: 0 !important;
            transition: 0.5s ease all !important;
        }
        .post:hover .menu-button{
            opacity: 1 !important;
        }
           
        .hide-post-button{
                display: none !important;
            }
           
        /* Start a New Thread */
            #togglePostFormLink{
                display: none !important;
            }
           
        #navlinks a:hover{
            color:#91b6c6!important;
        }
         
        /* Header */
        #header > .dialog{
            background: #efefef !important;
            border: 0 !important;
            height: 60px !important;
            color: #aaaaaa !important;
            margin-top: -50px !important;
            transition:0.5s ease all 1s !important;
        }
        #header-bar:hover{
            margin-top: 0 !important;
        }
         
        #header-bar > #shortcuts a{
            color: #aaaaaa !important;
        }
         
        #header-bar > #shortcuts{
            position: absolute !important;
            padding: 3px !important;
            bottom: 0 !important;
            left: 0 !important;
            width: 100% !important;
            background: #1e1e21 !important;
        }
        #board-list{
            margin-bottom: 30px !important;
        }
        #custom-board-list > .current {
            color: #648ea7 !important;
            font-size: 20pt !important;
            font-family: "Raleway" !important;
            font-weight: 200 !important;
        }
        #custom-board-list{
            color: #efefef !important;
        }
        .show-board-list-button{
            display: none !important;
        }
        #thread-watcher {
            font-family: "Arial" !important;
            color: #666 !important;
            background: #fafafa !important;
            box-shadow: #afafaf 0 0 2px !important;
            position: fixed !important;
            margin: 20px !important;
        }
            .reply { box-shadow: -1px 1px 1px rgba(0,0,0,.08); }
         
        .quote {
            font-style:italic !important;
        }
         
        .move {
            background: #fafafa !important;
            color: #fafafa !important;
            -webkit-transition: all 0.3s ease-out;
            -moz-transition: all 0.3s ease-out;
            -o-transition: all 0.3s ease-out;
            transition: all 0.3s ease-out;
        }
       
        .move:hover {
            color: #333 !important;
            -webkit-transition: all 0.3s ease-out;
            -moz-transition: all 0.3s ease-out;
            -o-transition: all 0.3s ease-out;
            transition: all 0.3s ease-out;
        }
         
        .name {
            font-weight:normal !important;
        }
           
        .postInfo {
            font-weight:normal !important;
        }                
         
        .quotelink {
            font-style:italic !important;
        }
         
        .dateTime {
            color: #999 !important;
        }
         
        .navLinks.desktop {
            color: #fafafa !important;
            visibility:hidden !important;
        }
         
        .navLinks.navLinksBot.desktop {
            color: #fafafa !important;
            visibility:hidden !important;
        }
         
        .fnfull {
            padding-left: 5px !important;
        }
         
        .fileText {
            padding-left: 20px !important;  
            padding-top: 5px !important;
        }
         
        .fileThumb {
            padding-left: 20px !important;
            padding-top: 5px !important;
        }
         
        .postMessage {
            padding-top: 5px !important;
        }
         
        .brackets-wrap {
            background: #0e1112 !important;
        }
         
        .qr-shortcut.fa.fa-comment-o {
            color: #0329123 !important;  
            visibility:hidden
        }
         
        .sideArrows {
            visibility: hidden !important;
        }
