css:
@-moz-document domain("www.vexforum.com"), url-prefix("https://www.vexforum.com/") {
/*================================================================

        Vex Forum Dark theme
          by Alex Griffin

        v0.0.1
        Last updated: 2017/08/24

    ================================================================*/


/*  do not display the bottom nav bar
 *  matter of taste if you don't want it delete it.
 */

.vnav {
	display: none;
}

/*  
 *  global vars
 *  variable colors from: Andy Cormack
 *  http://ac-design.co.uk/
 *	@groundxaero
 */
:root {
    --selection-bg-color: #8ea2cb;
    --selection-text-color: #dde;    
    --bg-color: #1b1b1d;
    --main-text-color: #aaaab6;
    --card-bg-color: #222225;
    --card-hover-bg-color: #2a2a2d;
    --menu-bg-color: #2a2a2e;
    --menu-hover-bg-color: #2f2f34;
    --link-color: #ddd;
    --link-hover-color: #fff;
    --input-box-bg-color: #171719;
}



 /* Scrollbar Chrome*/
 *::-webkit-scrollbar {
     width: 10px !important;
     height: 10px !important;
     background: #1e1e1e !important;
     border: 1px solid #252525 !important;
 }
 *::-webkit-scrollbar-button {
     display: none !important;
 }
 *::-webkit-scrollbar-thumb,
 *::-webkit-scrollbar-track {
     border: 0 !important;
     box-shadow: none !important;
 }
 *::-webkit-scrollbar-thumb {
     min-height: 28px !important;
     background: #333 !important;
 }
 *::-webkit-scrollbar-corner,
 *::-webkit-scrollbar-track {
     background: #1e1e1e !important;
 }



::-moz-selection {
    background: var(--selection-bg-color);
    color: var(--selection-text-color);
}
::selection {
    background: var(--selection-bg-color);
    color: var(--selection-text-color);
}
body {
    background: var(--bg-color);
    color: var(--main-text-color);
}
#hdr, #body-content, #ftr-content, .postBody {
    background: var(--bg-color);
    color: var(--main-text-color);
}
#hdr {
	border-color: var(--main-text-color);
}
input[type=text], input[type=password], input[type=number], input[type=email], textarea, .input {
    background-color: var(--input-box-bg-color);
    color: var(--main-text-color);
}
.channel {
    background-color: var(--input-box-bg-color);
    color: var(--link-color);
}
.channel:hover, a:hover {
    color: var(--link-hover-color);
}
.list > li {
    background-color: var(--bg-color);
}
.list > li:hover, .popupMenu li > a:hover, ul.popupMenu li.selected > a {
    background-color: var(--menu-hover-bg-color) !important;
}
.sheet, .area, .scrollShadowTop, .postHeader, .postFooter {
	background: var(--bg-color);
    color: var(--main-text-color);
}
.channelList {
		background: var(--menu-bg-color);
	    color: var(--main-text-color);

}
.button, .scrubberMore, .timeMarker, .tabs li.selected a, .sheetContent>h3 {
   background: var(--menu-bg-color);
   color: var(--main-text-color);
}
.tabs .pathItem.selected a:after {
	border-left-color: var(--menu-bg-color);
}
.scrubberMore {
	border: none;
}
.hasControls .controls, [class*=control-] {
	    color: var(--main-text-color) !important;

}
.popupWrapper.active .popupButton, blockquote {
		background-color: var(--card-hover-bg-color) !important;
   	color: var(--main-text-color);

}
.popup {
	background-color: var(--bg-color);
}
}
