css:
@-moz-document domain("mp3.zing.vn") {
body, .wrapper-page, .sta-result {
    background: #000;
}

footer, footer .container, .six-color-bg, nav, ul.main-nav.group, .lyrics-container, .subcribe a, .button.btn-dark-blue, nav .main-nav>li>a, .playlist li, .page-play-album .player .box-scroll, .button-style-1, .input-txt, li .title-row, .section-search .suggestion-2, .section-search .search .input-btn .btn, .box-comment .btn-more, textarea, .wrap-select, nav .main-nav .megamenu.submenu, .zlike a {
    background: #111;
}

.container-zalo-share, div#app, .wrap-sidebar, .toggle-ads {
    display: none;
}

.base-color, a:hover, .button-style-1:hover span, .button-style-1.active span, .button-style-1.active, nav .main-nav>li:hover>a, nav .main-nav .megamenu.submenu .subcol .title-menu a:hover, nav .main-nav .megamenu.submenu .subcol li:hover a, .custom-filter a:hover, .custom-filter a.active, .box-overview .tab-nav li a.active, .box-overview .tab-nav li:hover a, .dropdown ul li a:hover, .dropdown ul li a.active, .info-score li.last span, .upload-plus:hover .text, .box-tooltip ul li a:hover, .box-tooltip ul .li a:hover, .box-tooltip .ul li a:hover, .box-tooltip .ul .li a:hover, .playlist li:hover .item-song h3 a, .playlist li:hover .item-song h4 a, .playlist li:hover .item-song span, .playlist li.playing .item-song h3 a, .playlist li.playing .item-song h4 a, .playlist li.playing .item-song span, .playlist li.pause .item-song h3 a, .playlist li.pause .item-song h4 a, .playlist li.pause .item-song span, .page-hottopic .info-content h4 a:hover, .section-search .search .input-btn .btn, .mark {
    color: #FFF;
}

.subcribe a.active, .button.btn-dark-blue:hover, .zlike a:hover, .bar-chart span, .box-comment .btn-more:hover {
    background: #333;
}

.zicon, .dropdown ul li a.checked:before, .filter-display li i, .main-nav .home a, .s50x50, .icon33 {
    filter: hue-rotate(-80deg) brightness(2);
}

.playlist li.playing .item-song>span:before {
    filter: hue-rotate(-80deg);
}

.zm-jump-lyric svg path, path.zp-svg-fill {
    fill: #008cff;
}

.zm-volume .zm-volume-available, .media-control[data-media-control] .media-control-layer[data-controls] .bar-container[data-seekbar] .bar-background[data-seekbar] .bar-fill-2[data-seekbar] {
    background: #008cff;
}


.zm-quanlity:hover .zm-quanlity-display, .input-txt, li .title-row, .section-search .suggestion-2, .txt-primary, .nhaccho-section table td h3, .title-section a, .tab-menu li.active>a, .tab-menu li:hover>a, .jumper-link li:hover a, .jumper-link li.active a, .txt-primary:hover, a.txt-info:hover, .txt-info a:hover, .txt-title-lagre:hover, .title-section .view-all, .show-time, .playlist li:hover .item-song h3 a, .playlist li:hover .item-song span, .playlist li:hover .item-song i, .wrap-2-col .sidebar .data-list li.active>a, .wrap-2-col .sidebar .data-list li a.active, .wrap-2-col .sidebar .data-list li a:hover, .alphabet a.active, .alphabet a:hover, .biography ul li a:hover, .read-more:hover, .info-primary .info-content .read-more:hover, .text-notice-user strong, .sz-title-sm strong, .box-info-artist .info-summary a:hover, .time-zone-7, .switch-chart li a:hover, .switch-chart li a.active, .info-song-top a:hover, .add-playlist-region li span, .zm-lyrics p.current span, .zm-quanlity .zm-quanlity-display, .zm-jump-mv a, .four-color, .button-style-1 span, .bull, .table-canhan tr, .top-real-time .func-title-section, .load-more, .second-color, a, .info-meta h4, .box-upload .upload-heading, .form-control label, .table-canhan tr td strong, .page-hottopic .info-content h4 a {
    color: #3792ff;
}

.zm-ico svg path {
    fill: #008cff;
}

.main-nav li a, .lyrics-container h3, .header-frm, .con-select .option-select label, .box-artist .artist-info h2, .wrap-2-col .sidebar .data-list>li>a, .wrap-2-col .sidebar .data-list>li>h1>a, .txt-title-lagre, .info-primary .info-content h2, .info-primary .info-content li span, .info-primary .info-content .read-more, .panel-filter li a:hover, .panel-filter li.selected a, .panel-filter li a:hover, .info-top-play .txt-primary, .info-top-play .txt-primary:hover, .info-song-top a, .lyrics-container, nav .main-nav .megamenu.submenu .subcol li a, nav .main-nav .megamenu.submenu .subcol .title-menu, .tab-menu li a, .title-section, body, .wrapper-page {
    color: #999;
}

.section-search .suggestion-2 li .title-row {
    border-top: 1px solid #111;
}

.tab-menu li.active, .tab-menu li:hover {
    border-bottom: 2px solid #008cff;
}

.box-comment .frm-comment .wrap-comment textarea, select, textarea, .wrap-select, .playlist li, .page-play-album .player .box-scroll, .button-style-1, nav .main-nav .megamenu.submenu {
    border: none;
}

.box-comment .frm-comment {
    border-bottom: none;
}

.wrap-content, .zcontainer.pointer-enabled, .media-control.live {
    width:950px;
}

.zm-quanlity .zm-quanlity-display, .zm-jump-mv a {
    background: none;
}

.list-item>ul>li, .page-play-song .tab-menu, .page-play-video .tab-menu, .page-play-album .tab-menu, .page-artist-genre .tab-menu, .page-artist .tab-menu, .page-album-list .tab-menu, .page-search .tab-menu {
    border-bottom: 1px solid #111;
}

.title-section .icon-arrow {
    filter: hue-rotate(-80deg) brightness(2);
}

.box-comment .btn-more, .title-section span#commentCounter, .box-comment .frm-comment .wrap-comment textarea, select {
    color: #ccc;
}
}
