css:
/* i really want this to be global */
/* Sidebar and console window alignment */
#sidebarCell.sidebarCollapsible {
    padding: 8px 4px 0 8px !important;
}
.outer td.oRight {
    padding: 0 8px 8px 12px !important;
}
.bodyDiv {
    border-top: 2px solid rgb(0, 122, 165) !important;
}

/* Header panels adjustment */
.rf-ticker-div {
    min-height: 24px !important;
    background-color: #333 !important;
    height: 24px !important;
    padding-right: 5px !important;
}
.rf-view-div {
    font-size: 15px !important;
    line-height: 17px !important;
}
.dpl-icon {
    font-size: 15px !important;
}
.broadcastIconCls {
	font-size: 20px !important;
}
.rateExperienceIconCls {
	font-size: 16px !important;
}
.x-tab-bar-top .x-tab-bar-body-default-plain {
    padding-left: 1px !important;
}
#topmenu {
    padding: 11px 0 !important;
    line-height: 0 !important;
    font-size: 0 !important;
    background: none !important;
    border: none !important;
}
.message_space_on {
	display: none !important;
}
.toplink {
	padding: 0 6px !important;
}
.x-toolbar-default {
	border: none !important;
}

/*Console window*/
#detailViewChildPanelId-body {
	border-top: 1px solid #666 !important;
}
.pbBody {
	min-width: 98% !important;
}
#rfDPLReferenceDiv .tertiaryPalette {
	display: none !important;
}
#rfDPLReferenceDiv .secondaryPalette, .srTitlecls_SR, #docBody {
	background-color: #f8f8f8 !important;
}

/*Inputs padding grid*/
#rfDPLReferenceDiv .dataCol {
    padding-left: 4px !important;
    padding-top: 8px !important;
}
.pbSubsection {
    margin-top: 0 !important;
    padding-bottom: 10px !important;
    border-bottom: 1px solid #999 !important;
}

/*Inputs labels*/
#rfDPLReferenceDiv Label {
    margin-bottom: 0 !important;
    font-size: 13px !important;
    color: #333 !important;
}
#rfDPLReferenceDiv .requiredField {
    color: #f86e00 !important;
}

/* SR details padding */
.srTitlecls_SR {
    font-size: 14px !important;
    padding-top: 10px !important;
}
#rfDPLReferenceDiv .rf-label {
	color: #333 !important;
}
.srm_clsDynamicInputTextArea {
	width: 78% !important;
}

/* all ticket window input alignment */
#rfDPLReferenceDiv .rf-single-line-field, div.rfDPL-picklist-container select {
    width: 75% !important;
    height: 24px !important;
}
#rfDPLReferenceDiv INPUT, SELECT, TEXTAREA {
    line-height: 14px !important;
    padding-left: 8px !important;
    padding-right: 8px !important;
    border: 1px solid #ccc !important;
}
.rfDPL-auto-complete-div {
	cursor: pointer !important;
}

/* Details alignment */
.bDetailBlock.bPageBlock {
    margin-bottom: 0 !important;
    padding: 2px 2px !important;
    border-top-width: 2px !important;
}
.publishercontainer .publishertextarea {
	border: none !important;
}
.bDetailBlock.bPageBlock .pbBottomButtons {
    padding: 0px 0px 0px 600px !important;
}
.bPageBlock {
    padding: 2px !important;
	margin-bottom: 0 !important;
}
.bPageBlock .pbHeader>table>tbody>tr>td {
    padding: 2px 2px !important;
}
.bRelatedList .pbTitle .relatedListIcon {
    display: none !important;
}
.pbButton > .btn {
	font-size: 1em !important;
    margin: 0px 2px !important;
    padding: 2px 4px !important;
}
.bRelatedList .bPageBlock .pbBody {
    margin: 2px !important;
}
.bRelatedList .bPageBlock {
	border-top-width: 2px !important;
}

/*Headers padding*/
.pbBody table.list tr.headerRow th {
    padding: 0px 4px !important;
}

/*Cells padding */
.list td {
    padding: 4px !important;
}

/* Radios size */
.radioBtnCls, #staffRdBtn, #queueRdBtn {
    transform: scale(1.3) !important;
    margin-top: 1px !important;
    margin-bottom: 6px !important;
}

/* Remove link to top */
.bottomLinkDiv, .backtotop {
    display: none !important;
}

/* Footer */
.bPageFooter {
    padding: 6px 0 !important;
}
