css:
@-moz-document url-prefix("http://avtovokzal.pl.ua/") {
body {
	background: white!important;
}

tr > td:last-child {
	border: none;
}

#shed-top td,
#search-top td {
	background: #eceff1;
	border-bottom: 1px solid #CFD8DC;
}

#content_all td:not(:last-child) {
	border-right: 1px solid #CFD8DC!important;
}

#content1 {
	border: 1px solid #cfd8dc;
	box-shadow: none;
	margin: 10px auto 60px;
}

#content_all td.tdr-2 {
	background-color: #FAFAFA;
}

#content_all td.tdr-1 {
	background-color: #ECEFF1;
}

.tdr-2.over,
.tdr-1.over {
	background: #CFD8DC;
	border-color: #FAFAFA;
	transition: all .15s;
}

#poisk_polya {
	margin: 20px auto;
	margin-left: 133px;
}

#fon_forma_poisk {
	display: none;
}

td.mtditem {
	background: none!important;
}

td.mtditem:hover {
	background: #546E7A!important;
	cursor: pointer;
}

td.mids {
	display: none!important;
}

td.mtditem:not(:last-child) {
	border-right: 1px solid #546E7A!important;
}

img.ImgMenu {
	height: auto!important;
}

#fon_menu {
	background-color: #607D8B;
	border-bottom: 1px solid #546E7A;
}

.TextActiveMenu,
.TextItemMenu {
	padding: 0!important;
	height: auto!important;
	line-height: 50px;
}

div#fon_forma_poisk {
	background: #ECEFF1;
	padding: 0;
	border-bottom: 1px solid #CFD8DC;
}

div#poisk_forma {
	background: none;
}

div#avtostancii_all {
	background: #FAFAFA;
	border: 1px solid #EEEEEE;
	border-top: none;
	margin: 0 auto;
	padding: 7px;
	box-shadow: none;
}

div#content_all {
	background: none;
}

div#shedule {
	height: auto!important;
	background-color: #FAFAFA;
}

div#shedule > table {
	width: 100%;
}

tr > td[width*="81"]:last-child {
	width: 111px;
}


/* hide unused */
#shapka,
#fon_poisk_reisa,
#global1,
#novosti,
#spravka_zagolovok,
#content5,
#podval_fon,
#spravka_fon,
#left_content,
#right_content {
	display: none;
}


/* sale ticket */
.reis {
	background: #607d8b;
}

#podval_doroga {
	display: none;
}
}

@-moz-document url-prefix("http://avtovokzal.pl.ua/home") {
div#poisk_polya {
	margin: 13px auto;
}

#fon_forma_poisk {
	display: block;
}
}
