css:
@-moz-document domain("forum.gamevn.com") {
:root {
    --bg-color1:#000;
    --bg-color2:#111;
    --bg-color2-2:#191919;
    --bg-color2-hover:#222;
    --bg-color2-rgba:rgba(34,34,34,0.96);
    --bg-color3:#333;
    --bg-color4:#444;
    
    --text-color1:#999;
    --text-color2:#ccc;
    --text-color2-v:#aaa;
    --text-color3:#fff;
    --text-color4:#3792ff;
    
    --border-bg-sep:1px solid #222;
    --border-menu-sep:1px solid #333;
    --border-popup:1px solid #666;
}




html, body, #header {
    background: var(--bg-color1) !important;
    color: var(--text-color1) !important;
}

#content .pageContent {
    background: var(--bg-color2) !important;
}

.navTabs {
    background: var(--bg-color2) !important;
    border: none !important;
}

.navTabs .navTab.selected .tabLinks {
    background: var(--bg-color2) !important;
    border: none !important;
}

.navTabs .navTab.selected .navLink {
    background: var(--bg-color3) !important;
    border: none !important;
    color: var(--text-color3) !important;
}

.navTabs .navTab.PopupClosed .navLink {
    color: var(--text-color2) !important;
}

.navTabs .navTab.PopupClosed .navLink:hover {
    color: var(--text-color3) !important;
    background: var(--bg-color2-hover) !important;
}

.Popup .PopupControl.PopupOpen, .Popup.PopupContainerControl.PopupOpen {
    background: var(--bg-color2-hover) !important;
}

.navTabs .navTab.PopupOpen .navLink {
    color: var(--text-color2) !important;
}

.Menu {
    border: none !important;
}

.Menu .primaryContent, .primaryContent {
    background: var(--bg-color2-hover) !important;
    border-bottom: var(--border-menu-sep) !important;
}

.Menu .secondaryContent {
    background: var(--bg-color2-rgba) !important;
    border: none !important;
}

.Menu .blockLinksList a, .navPopup .sectionFooter a, ul.links a, .menuColumns .blockLinksList a, .menuColumns .blockLinksList label, .linkGroup a {
    color: var(--text-color2) !important;
}

.Menu .sectionFooter {
    background: var(--bg-color2-hover) !important;
}

.textCtrl {
    background: var(--bg-color3) !important;
    border: none !important;
    color: var(--text-color2) !important;
}

.button.primary, .button, .topCtrl a.callToAction, .nodeListNewDiscussionButton a.callToAction, .linkGroup a.callToAction, .button.DisableOnSubmit, .button.PreviewButton, .thread_view .messageMeta a.report, .thread_view .messageMeta a.like, .thread_view .messageMeta a.unlike, .thread_view .messageMeta a.reply, .thread_view .messageMeta a.MultiQuoteControl, .thread_view .messageMeta a.edit, .thread_view .messageMeta a.history, .thread_view .messageMeta a.delete, .thread_view .messageMeta a.deleteSpam, .thread_view .messageMeta a.ip, .thread_view .messageMeta a.viewWarning, .thread_view .messageMeta a.warn {
    background: var(--bg-color3) !important;
    box-shadow: none !important;
    border: none !important;
    color: var(--text-color3) !important;
}

#QuickSearch, .formPopup, .formPopup .controlsWrapper {
    background: var(--bg-color2) !important;
}

#QuickSearch.active {
    box-shadow: 5px 5px 25px rgba(51,51,51,0.75) !important;
}

#calroot {
    background: var(--bg-color2-2) !important;
    /*border: var(--border-popup) !important;*/
}

.breadcrumb {
    background: var(--bg-color2) !important;
    border: var(--border-bg-sep) !important;
}

.breadcrumb .crust a.crumb {
    background: var(--bg-color2-hover) !important;
    color: var(--text-color2) !important;
}

.breadcrumb .crust a.crumb:hover {
    
    color: var(--text-color1) !important;
}

.breadcrumb .crust .arrow {
    /*background: var(--bg-color3) !important;*/
}

.breadcrumb .crust .arrow span {
    /*background: var(--bg-color3) !important;*/
    border-left-color: var(--bg-color2-hover) !important;
}


.sidebar .secondaryContent, .avatar img {
    background: var(--bg-color2-2) !important;
}

.avatar img, .avatar .img {
    border: var(--border-bg-sep) !important;
}

.sidebar .visitorPanel .username {
    color: var(--text-color2) !important;
}

.resourceListSidebar .secondaryContent a, .sidebar .secondaryContent a:visited {
    color: var(--text-color2) !important;
}

.sidebar .secondaryContent a, .sidebar .secondaryContent a:visited {
    color: var(--text-color2) !important;
}

.sidebar .section .primaryContent h3 a, .sidebar .section .secondaryContent h3 a, .sidebar .avatarList .userTitle, .sidebar .section .secondaryContent .footnote, .sidebar .section .secondaryContent .minorHeading, .subForumsPopup .dt, .pairs dt, .pairsInline dt, .pairsRows dt, .pairsColumns dt, .pairsJustified dt, .sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3 {
    color: var(--text-color1) !important;
}

fieldset, .submitUnit, .ctrlUnit, .section li {
    border-color: var(--bg-color3) !important;
}

.sidebar .secondaryContent {
    border:none !important;
}

.sidebar .section .primaryContent h3, .sidebar .section .secondaryContent h3, .profilePage .mast .section.infoBlock h3 {
    border-bottom: var(--border-menu-sep) !important;
}

.forum_list .sectionMain, .forum_view .sectionMain, .find_new_posts .section, .watch_threads .sectionMain, .watch_threads_all .sectionMain, .watch_forums .sectionMain, .category_view .sectionMain, .conversation_list .sectionMain, .conversation_list_yours .sectionMain, .conversation_list_starred .sectionMain {
    background: none !important;
    border: none !important;
}

.messageList .message {
    background: var(--bg-color2-2) !important;
    border-top-color: var(--bg-color2-hover) !important;
    box-shadow: none !important;
}

.message .likesSummary {
    background: var(--bg-color2-hover) !important;
    border: none !important;
}

.message .likesSummary a.username, .message .likesSummary a.OverlayTrigger {
    color: var(--text-color2) !important;
}

.extraUserInfo .pairsJustified dd a {
    color: var(--text-color1) !important;
}

.messageList h3 a {
    color: var(--text-color2) !important;
}

.messageText a {
    color: var(--text-color2) !important;
}

.textHeading, .xenForm .sectionHeader {
    color: var(--text-color1) !important;
    border-bottom: var(--border-menu-sep) !important;
}

.bbCodeBlock .type, .bbCodeQuote blockquote {
    background: var(--bg-color2-hover) !important;
    color: var(--text-color1) !important;
}

.bbCodeBlock {
    border: none !important;
}

.bbCodeQuote .quoteContainer .quoteExpand {
    color: var(--text-color3) !important;
    background: linear-gradient(to bottom,rgba(51,51,51,0) 0%,#222 80%)!important;
}







.nodeList .categoryStrip, .discussionList .sectionHeaders {
    background: var(--bg-color2-hover) !important;
    border-bottom: var(--border-menu-sep) !important;
}

.node, .node .nodeText, ol.subForumList, ol.subForumList h4.nodeTitle {
    background: var(--bg-color2-2) !important;
    border: none !important;
}



.sectionMain a, .sectionMain a:visited, #legal a, #legal a:visited {
    color: var(--text-color2) !important;
}

.nodeList .nodeDescription {
    color: var(--text-color1) !important;
}

.footer .pageContent {
    background: var(--bg-color2-2) !important;
}

.nodeList .categoryForumNodeInfo, .nodeList .forumNodeInfo, .nodeList .pageNodeInfo, .nodeList .linkNodeInfo {
    border-bottom: var(--border-bg-sep) !important;
}

.PageNav a {
    background: var(--bg-color2-2) !important;
    color: var(--text-color2) !important;
    border: none !important;
}

.PageNav a:hover {
    background: var(--bg-color3) !important;
    color: var(--text-color3) !important;
}

.PageNav a.currentPage {
    background: var(--bg-color3) !important;
    color: var(--text-color3) !important;
}

.discussionListItem {
    background: var(--bg-color2-2) !important;
}

.section .posterDate a, .section .posterDate a:visited {
    color: var(--text-color1) !important;
}

.section .posterDate a:hover {
    color: var(--text-color3) !important;
}

.discussionListItem .posterAvatar, .avatar img, .discussionListItem .stats {
    background: var(--bg-color2-2) !important;
    border: none !important;
}

.section a.dateTime, .section a:visited.dateTime, .node .nodeLastPost .DateTime {
    color: var(--text-color1) !important;
}

.navTabs .navTab.selected .tabLinks a {
    color: var(--text-color2) !important;
}

.sectionFooter {
    background: var(--bg-color2-hover) !important;
    border-bottom: var(--border-menu-sep) !important;
}

#DiscussionListOptionsHandle a, #DiscussionListOptionsHandle a:visited {
    background: var(--bg-color2-hover) !important;
    color: var(--text-color2) !important;
    border: var(--border-menu-sep) !important;
    border-top: none !important;
}

.DiscussionListOptions {
    background: var(--bg-color2-hover) !important;
    color: var(--text-color2) !important;
    border: none !important;
}

.redactor_toolbar {
    background: var(--bg-color1) !important;
    border: none !important;
    padding: 2px !important;
}

.redactor_box {
    border: none !important;
}

/*
.redactor_toolbar li.redactor_btn_group ul {
    background: var(--bg-color3) !important;
}*/

.message .signature a {
    color: var(--text-color1) !important;
}

.tabs {
    background: var(--bg-color2) !important;
    border-color: var(--bg-color3) !important;
}

.tabs li a, .tabs.noLinks li {
    background: var(--bg-color2-hover) !important;
    color: var(--text-color2) !important;
    border: var(--border-menu-sep) !important;
    border-bottom: none !important;
}

.tabs li a:hover, .tabs.noLinks li:hover {
    background: var(--bg-color3) !important;
    color: var(--text-color2) !important;
}

.tabs li.active a, .tabs.noLinks li.active {
    background: var(--bg-color3) !important;
    color: var(--text-color3) !important;
}

.memberListItem h3.username a {
    color: var(--text-color2) !important;
}

.username .style8 {
    color: var(--text-color1) !important;
}

.avatar .img {
    background-color: var(--bg-color2-hover) !important;
}

.thread_view .threadAlerts {
    background: var(--bg-color2-2) !important;
    border: none !important;
}

.thread_view .threadAlerts dd {
    color: var(--text-color2) !important;
}

.thread_view .threadAlerts dt {
    color: var(--text-color1) !important;
}

.attachedFiles .attachmentList, .attachment .boxModelFixer {
    background: var(--bg-color2-hover) !important;
}

.attachment .boxModelFixer {
    border: var(--border-menu-sep) !important;
}

.attachedFiles .attachedFilesHeader {
    background: var(--bg-color3) !important;
}

.attachedFiles {
    border: none !important;
}

.attachedFiles .attachedFilesHeader {
    color: var(--text-color1) !important;
}

h6.filename a {
    color: var(--text-color2) !important;
}

.hasJs .bbCodeSpoilerText {
    background: var(--bg-color2-hover) !important;
    border: none !important;
}

.searchResult .snippet a {
    color: var(--text-color1) !important;
}

.search_results .sectionMain {
    border: none !important;
    padding: 0px !important;
}

.messageInfo.primaryContent {
    border-bottom: none !important;
}

.messageList .messageUserInfo {
    border-right: none !important;
}
}
