css:
@-moz-document domain("hot.cnbeta.com"), domain("www.cnbeta.com") {
@media (max-width: 768px) {
  body {
    width: 100%;
    min-width: 0;
  }
  .w1200,
  .cnbeta-article {
    width: 100%;
  }
  .topSearchBox {
    right: 0;
    position: absolute;
  }
  .global-navi {
    height: auto;
  }
  .cnbeta-home-side,
  .cnbeta-update-list,
  .cnbeta-article-comments-list,
  .cnbeta-article-comments-right,
  #comment-fixed-area {
    width: 100%;
  }
  .cnbeta-article-comments-right.fixed,
  .cnbeta-article-comments-right.stopfixed,
  .cnbeta-article-comments-right.fixed #comment-fixed-area,
  .cnbeta-article-comments-right.stopfixed #comment-fixed-area {
    position: relative;
    width: 100%;
  }
}
}
