css:
@-moz-document regexp("^http(s?):\\/\\/(www\\.)?youtube\\.com\\/watch\\?(v=.{11}&((list=WL&index=\\d+)|(index=\\d+&list=WL$)))&?") {
@media
	screen and (min-width: 2500px) and (max-width: 2560px) and (min-height: 950px) and (max-height: 1070px),
	screen and (min-width: 3380px) and (max-width: 3440px) and (min-height: 1310px) and (max-height: 1430px)
{
	*, html {
		font-size: 10pt !important;
	}
	body {
		zoom: 200%;
	}
	div#masthead-container.ytd-app {
		z-index: -10;
	}
	div#content.ytd-app {
		margin-top: -80px;
	}
	div.header.ytd-playlist-panel-renderer {
		display: none;
	}
}
}
