css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("m.aftershock.news") {

  #block-block-16,
  .aft-postcountryicon,
  .aft-postcounter,
  .aft-commentauthorage,
  .field.field-name-field-image.field-type-image.field-label-hidden,
  .tags-printingpic,
  .aft-commentcountryicon,
  .permalink_img,
  .parent_img,
  .aft-commentacc{
    display: none!important;
  }

  #aft-main {
    color: #fff!important;
    background-color: #333!important;
  }

  h1,
  h2,
  h3,
  h4,
  h5,
  h6,
  .aft-comment-content,
  .aft-postfootericons,
  a.aft-button,
  a.aft-button:link,
  a.aft-button:link:link,
  body a.aft-button:link,
  a.aft-button:visited,
  body a.aft-button:visited,
  input.aft-button,
  button.aft-button,
  .aft-postcontent li,
  .aft-post li,
  .aft-textblock li,
  div.spoiler div.spoiler-title,
  div.spoiler div.spoiler-content,
  .field-name-field-author-comment .field-label,
  .editor-title,
  .editor-title a {
    color: #fff!important;
  }

  .aft-postheader a,
  .aft-postcontent a,
  .aft-postcontent a:link,
  blockquote a,
  .aft-postcontent blockquote a,
  .aft-blockcontent blockquote a,
  .aft-footer blockquote a,
  .blockquote a:link,
  .aft-postcontent blockquote a:link,
  .aft-blockcontent blockquote a:link,
  .aft-footer blockquote a:link,
  .aft-comment-header a,
  .aft-comment-header a:link,
  .aft-pulse-a,
  .aft-menu-show,
  .aft-blockcontent table,
  .aft-blockcontent li,
  .aft-blockcontent a,
  .aft-blockcontent a:link,
  .aft-blockcontent a:visited,
  .aft-blockcontent a:hover {
    color: #79afde!important;
  }

  .publication-tags-item a:hover,
  .publication-tags-item a:focus,
  .publication-tags-category-item a:hover,
  .publication-tags-category-item a:focus,
  ul.menu li a.active {
    color: #ea7575 !important;
  }

  .aft-postheadericons,
  .aft-postheadericons a,
  .aft-postheadericons a:link,
  .aft-postheadericons a:visited,
  .aft-postheadericons a:hover,
  .aft-postfootericons a,
  .aft-postfootericons a:link,
  .comment .comment a.username,
  a.username,
  a#wysiwyg-toggle-edit-comment-body-und-0-value,
  .ctools-collapsible-handle,
  .ctools-collapsible-handle a,
  .aft-blockheader .t,
  .aft-blockheader .t a,
  .aft-blockheader .t a:link,
  .aft-blockheader .t a:visited,
  .aft-blockheader .t a:hover,
  .aft-blockcontent {
    color: #eee!important;
  }
  .comment .aft-commentauthorage,
  .comment .comment_date,
  .field-label,
  label.aft-checkbox,
  label.aft-radiobutton,
  .aft-commentsform {
    color: #ccc!important;
  }

  blockquote {
    color: #ccc!important;
    background-color: #222!important;
  }

  .opengraph-filter {
    background-color: #333!important;
  }

  .editor-comments,
  .field-name-field-author-comment,
  .ctools-collapsible-container,
  div.spoiler div.spoiler-title,
  div.spoiler div.spoiler-content {
   background-color: #555!important;
  }

  .recommended-comment {
    background-color: #444!important;
  }
  
  .new {
    color: #62d759!important;
  }
  div.spoiler div.spoiler-title {
    background-image: none!important;
  }
}
