css:
@-moz-document domain("www.rodiaki.gr") {

.img_ad, [class='row hidden-xs hidden-sm'], .col-md-4:not(.article-box)   {
display: none !important; 
position: absolute !important;
z-index: -999 !important;
min-height: 0px !important;  
height: 0px !important;  
}

.navbar-nav {
position: sticky;
float: right !important;
}  
  
.navbar-header {
position: absolute;
float: right;
padding-left: 60px;
transform: scale(4.3);
text-shadow: -4px 2px 6px black;
}  
  
[alt="Η ΡΟΔΙΑΚΗ"]{
  position: absolute !important;
transform: scale(0.2);
left: 35px;
top: 12px;
display: block;
-moz-box-sizing: border-box;
box-sizing: border-box;
background: url(data:image/png;base64,) no-repeat;
width: 37px;
height: 56px;
padding-left: 58px;
  
}

body, .main-container, .list-group-item, .media-body, .media-list, .panel-body, [data-toggle="tab"] {
background: -moz-linear-gradient(rgb(120, 120, 115) 0.2%, rgb(150, 150, 155) 15%, rgba(110, 110, 120, 1)) 50%;
background-attachment: fixed !important;
}
  
.flow li span {
background: rgba(140, 140, 145, 0.3) !important;
}
  
[style*="border-bottom: 7px solid #3F51AF"] {
border-bottom: 5px solid #DDDDDD !important;
}  
  
.panel-info, .alert-info {
border-color: #DDDDDD;
}
  
.page-header.main, .alert-info, .alert-info > * {
background: none !important;
text-align: center !important;
color: rgb(180,180,185) !important;
}  

a:not(.carousel-caption), body:not(.carousel-caption) {
color: rgb(60,60,54);
}
  
h5 > small, small {
color: rgb(180,180,185) !important;
}
  
.navbar-default {
background: rgba(200,200,200, 0.85)  !important;
border-top: none !important;
border: none !important;
border-radius: 0 0 150px 150px !important;
}
  
.col-md-8 {
width: -moz-available !important;
}

HTML > body > div {
height: -moz-fit-content !important;
}  
  
}
