css:
@-moz-document regexp(".*craigslist.*") {
/*  I started with this style and modified it
Dark Craigslist - http://userstyles.org/styles/104332
*/

* {
background-color: transparent !important;
color: #999 !important;
border-color: #444 !important;
text-decoration: none !important;
outline: none !important;
}

*:focus {
outline: none !important
}

h1, h2, h3, h4, h5, h6, h1 *, h2 *, h3 *, .med, em, #rg_hta, .qbtt, .ss-link.ss-selected, td > font > b, #qbut b, #qbft b, a.spell, a.spell *, a.spell_orig, #gen-query .operator, .threadCountNumber, #popup_dialog_title, .settings-title, .thread-subject, .leftTitle, #conditions #temp, #conditions #condition, .mapBalloon .b {color: #CCC !important}
#hplogo div, #ftby a, .page-title, .jhp input[type="submit"], body > div[style*="fixed"] > div > b, .rhsvw span > .fl, .rhsvw span > .fl b, .rhsvw span > b, .rhsvw fieldset > legend {
color: #EEE !important
}

.returnmail, .reply-flap {
	background-color: #303030 !important;
}

.titleBox, .titleBox *, .tablib_selected .tab_content {
color: #444 !important; text-shadow: 0 1px 0 #000 !important
}

.msel, .msel .kls, .tbos,  .tbos b, .tbots, .tbotu, #leftnav > h2, .selectedTab .kdSidebarContent > div, .cb .G-G- {
color: #FF2600 !important
}

.a, .cite, cite, cite a, cite b, #ans > i, .f .bc, cite .bc, #rg_hr, #rg_hr a {
color: #ECEA00 !important
}

.gac_b *, .gssb_i *, .esc.kb, .ac-row.active, .ac-row.active *, .dropdown-box > div:hover {
color: #E8287E !important
}

.xsm, .psgiprice, .psliprice b, .selectionTools span, .pagination-div a, .f span[style*="background:#aaa;border:#999 1px solid;border-radius:2px;"] {
color: #1698B5 !important
}

#GMmessages a, .bubble .item-body a, .mapBalloon a, .item-body a, #weather .on a, #weather a.active, #weather .active a, #conditions #obsTable .b, #conditions #obsTable .b *, #stars table .b, #conditions .b {
color: #08ADCF !important
}

#leftnav a, #leftnav a *, .cb > div, .kdSidebarContent > div, .kdSidebar > div, #talk_title, a.ss-link, .fl > span {
color: #7A7A7A !important
}

.f, .f span, .osl, .osl a, .bc a, .gl a, .esc > a, #knavm, .pslipricecol cite > a, .goog-date-picker-other-month, .RssSummary div, .RssEntryOuterContent, #bp-bd form span.small, .thread_table .read .thread, .read .subject_and_snippet, #views .Nk.Pk *,
.tn-snippet, .tn-time, .tn-source, #container .source, #container .source-link {
color: #666 !important
}

.msel, #leftnav > h2 {
font-weight: bold !important
}

.msel, #leftnav > h2, .tbos, .selectedTab div.kdTabTitle, .selectedGadgetTitle {
text-shadow: 0 1px 0 rgba(0,0,0,0.5) !important
}

.gbts, #gbgs4dn {
text-shadow: 0 1px 0 #000 !important
}

#knavm {
text-shadow: 0 0 2px #000 !important
}

#content .uim .title-cell strong, #content .uim .callout strong, .thread_table .unread b, .thread_table .selected b, #views .Nk.Ok b {color: #FFF !important; text-shadow: 0 0 6px rgba(255,255,200,0.8) !important}
span.bd, .controls > b > a, td.goog-date-picker-month, td.goog-date-picker-year, th.goog-date-picker-wday {border-bottom: 1px dotted #444 !important}
.f span[style*="background:#aaa;border:#999 1px solid;border-radius:2px;"] {
border-style: dashed !important
}

#res a:hover img, .videobox a:hover img, .vstb, .vsbb, .vstbb, .vstbt, a:hover .newsimg {
border-color: #FA0 !important
}

.f span[style*="background:#aaa;border:#999 1px solid;border-radius:2px;"] {
border-color: #F80 !important
}

a:hover, a:hover *, .link:hover, #gbgs4dn:hover, .cb > div:hover, .kdSidebarContent > div:hover, #sharesLink:hover, #updatesLink:hover, #friendsLink:hover, #talk_title:hover, .as-tip-span:hover, .g a:visited:hover, .g a:visited:hover *, #footer a:hover, #leftnav a:hover, #leftnav a:hover *, .chip_x:hover, .gsib_b:hover, #rg_hta:hover, .goog-date-picker-date:hover, #add_gadget:hover, #change_theme:hover, #themes_keep_theme_link:hover, .selectionTools span:hover, #GMmessages a:hover, .bubble .item-body a:hover {
color: #FFF !important;
}

a.kd-button:hover *, a.nolink:hover {
text-shadow: none !important; color: #999 !important
}

td.menulist:hover {
color: #BBB !important;
background: -moz-linear-gradient(left, #333, #111) !important; 
}

.toc .toc_legend {
top: -2.1em;
}

.vst {
border-bottom: 1px dotted #666 !important
}
.g a:visited, .g a:visited * {
color: #777 !important
}

.RssHeadlinePreview a:visited, .RssSummary a:visited, .tn-headline a:visited {
color: #666 !important
}
  
#res a img, .videobox a img, a .newsimg {
-o-transition-duration: 0.4s;
-o-transition-property: border-color, opacity;
-moz-transition-duration: 0.4s;
-moz-transition-property: border-color, opacity;
-webkit-transition-duration: 0.4s;
-webkit-transition-property: border-color, opacity
}

.tsf-p .lsbb, .lsb, .vspii, .kpbb, .ksb, .btc .lsb, span.ds .lsb, .jhp input[type="submit"] {
background-image: none !important
}

.goog-button-base-outer-box, .scrollBarInner, .gssb_d, .lnsep, .lst-td, #sftab.lst-td > div:not(#qbq), .lst-b, .lsbb, .kpbb, .btc, .ds, #misspell, #rhs, #sc-block .sc a, .mslg .vsc, #footer > div, .gecko #container, table[style*="border-bottom:1px solid transparent"] {
border: none !important
}

.msel .kls, .rhsvw, .rhsvw > div, div[style="width:1px"] div.threads-extra-row {
border-color: transparent !important
}

div.bubble > .i:not(.point), #container #up, #container #down, .thread-top, .thread-bottom, .thread-left, .thread-l, .thread-r, #forecast #dayRow, #forecast .on {
background-image: none !important
}

#gbpr, #pmocntr2, #bb_pp_tos_ack {
display: none !important
}

a {
color:#117990 !important;
text-decoration: none !important;
font-weight: 800 !important;
}

a:hover {
color:#1698b5 !important;
text-decoration: underline !important;
}

a:visited {
color:#46528D !important;
font-weight: 400 !important;
text-decoration: underline !important;
}

a:active {
color: #ccccdd !important;
}

body {
background:#131313 !important; 
color:#CCCCCC !important;
}

#mainLogo {
display: none !important;
}

.clogin a {
color: #000000 !important;
text-decoration: none !important;
}

#header {
background-color: #000000 !important;
height: 24px !important;
}

#headerBottom {
position: absolute !important;  
width: auto !important;
height: 20px !important;
top: 0px !important;
right: 0px !important;
z-index: 9999 !important; 
}

header img, #logo {
display: none !important;
}


td {
color: #000000 !important;
font-family: tahoma !important;
}

button, input, textarea, select {
-moz-appearance: none !important; -webkit-appearance: none !important
}

input[type="submit"], input[type="button"], input[type="checkbox"], input[type="radio"], button:not(.esw):not(.vspib):not(.wsa):not(.csb):not(.goog-date-picker-btn), a[role="button"],
.buttonsAndLinks .composeLink, .fakecheckbox, .goog-button-base-inner-box.goog-inline-block, .jfk-button-standard {
background: rgba(35,35,35,.7) !important;
border: 1px solid #292929 !important;
box-shadow: 0 0 5px #000 !important;
border-radius: 0 !important;
-o-transition-duration: 0.4s;
-o-transition-timing-function: ease-in;
-moz-transition-duration: 0.4s;
-moz-transition-timing-function: ease-in;
-webkit-transition-duration: 0.4s;
-webkit-transition-timing-function: ease-in}
.buttonsAndLinks .composeLink {padding: 0 3px !important; margin-top: 1px !important;
}

.buttonsAndLinks button {
margin-right: 4px !important
}

span#gbgs4 {
margin-top: 0px !important
}

input[type="submit"]:hover, input[type="button"]:hover, input[type="checkbox"]:hover, input[type="radio"]:hover, button:not(.esw):not(.vspib):not(.wsa):not(.csb):not(.goog-date-picker-btn):hover, .fakecheckbox:hover {
box-shadow: 0 0 5px #666 !important;
color: #CCC !important
}

.buttonsAndLinks .composeLink:hover {
box-shadow: 0 0 5px #666 !important
}

select button:not(.esw):not(.vspib):not(.wsa):not(.csb):not(.goog-date-picker-btn) {
box-shadow: 0 0 12px #000 !important;
background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAcAAAAFCAYAAACJmvbYAAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAADsMAAA7DAcdvqGQAAAAadEVYdFNvZnR3YXJlAFBhaW50Lk5FVCB2My41LjEwMPRyoQAAADRJREFUGFdjmDlz5n8gYEDHIHEGdnb2/+gKQHyQOFgHsgKYBEgcbhxMAYiGWYFiF7IESAEA1TZZ1AUcPVYAAAAASUVORK5CYII=) rgba(35,35,35,.7) no-repeat center !important;
padding-right: 5px !important
}
  
input[type="checkbox"]:active:hover, input[type="radio"]:active:hover, .fakecheckbox:active:hover {
background-color: rgba(35,35,35,.7) !important;
border: 1px solid #292929 !important}
input[type="checkbox"]:checked, input[type="radio"]:checked {color: #F80 !important}
input[type="checkbox"], input[type="radio"] {min-width: 6px; min-height: 6px}
.fakecheckbox.checked {border-color: #F90 !important;
}
  
@media screen {
input[type="checkbox"] {margin: 3px 3px 2px 4px !important}
input[type="radio"] {margin: 3px 6px 2px 4px !important}
input[type="checkbox"]:checked, input[type="radio"]:checked {border-color: #F80 !important
}

select {
padding-right: 12px !important;
background: url(http://www.abload.de/img/dropkupg.png) rgba(35,35,35,.7) no-repeat !important;
background-position: right center !important;
}}

input:disabled, button:disabled, select:disabled, #container .navigation-disabled {
color: #666 !important;
opacity: 0.6 !important;
cursor: default !important
}

textarea, select, input, #sftab, #lst-ib.lst.qbhi, #gbqfqw, .kd-button, .dropdown-menu, .goog-flat-menu-button {
background: rgba(35,35,35,.7) !important;
border: 2px solid !important;
border-color: rgba(200,200,200,.1) rgba(200,200,200,.11) !important;
border-radius: 0 !important;
box-shadow: inset 0 0 3px #000 !important;
-o-transition-duration: 0.4s;
-moz-transition-duration: 0.4s;
-webkit-transition-duration: 0.4s
}

.dropdown-menu {
padding-top: 0 !important
}

.dropdown-menu small {
padding-top: 2px !important
}

.goog-flat-menu-button-dropdown, .goog-menu-button-dropdown {
border-color: #777777 transparent !important
}
  
textarea:hover, select:hover, input:hover, #qbfile:hover *, .input:hover *, #sftab:hover, #lst-ib.lst.qbhi:hover, .kd-button:hover, .dropdown-menu:hover {
border-color: rgba(200,200,200,.2) rgba(200,200,200,.21) !important
}
  
textarea:focus, select:focus, input:focus, #sftab.lst-d-f, #lst-ib.lst.qbhi:focus, .kd-button:focus, .dropdown-menu:focus {
border-color: rgba(200,200,200,.3) rgba(200,200,200,.31) !important
}
  
#gsea_table input:hover {
background-color: rgba(35,35,35,.8) !important
}
  
#gsea_table input:focus {
background-color: rgba(35,35,35,.9) !important
}
  
#lst-ib, .gsib_a .gsfi, #gbqfqw .gbqfif {
border: none !important; background: none !important; box-shadow: none !important
}
  
#btnG, input[name="btnG"].lsb, #ytg-search .search-button {
background: url("http://www.abload.de/img/searchsdb0z.png") no-repeat center center rgba(35,35,35,.7) !important
}
  
option, optgroup {
  background: #222 !important;
  color: #666 !important
}

#addGadgets {
margin-bottom: 15px !important
}
  
.nyc_open #nycx {
  width: 9px !important; height: 9px !important; 
}
  
.vspii {
border: none !important;
border-color: transparent !important;
border-radius: 0 !important;
-o-transition-duration: 0.4s;
-o-transition-property: border-color;
-moz-transition-duration: 0.4s;
-moz-transition-property: border-color;
-webkit-transition-duration: 0.4s;
-webkit-transition-property: border-color
}
  
.vsc:hover .vspii {
  box-shadow: 0 0 4px #121212 !important;
  height: 100% !important
}
  
.vso .vspii {
border: 1px solid #333 !important; border-right: none !important
}
  
#nycp {
border-color: #333 !important
}
  
div.querybox {
background: black !important; 
}
  
footer {
Display: none !important; 
}
  
.gmnoprint > div[style*="background-color: white;"] > div, div.lyrc_dd_inner, #hmtctl div {
background: -moz-linear-gradient(#222, #444) !important; border-color: #333 !important; color: #999 !important; box-shadow: inset 0 0 6px #000 !important
}

.gmnoprint > div[style*="background-color: white;"]:hover > div, .gmnoprint > div[style*="background-color: white;"] > div[style*="font-weight: bold;"] {
background: -moz-linear-gradient(#444, #111) !important; color: #EEE !important
}
}
