css:
@-moz-document domain("mpgh.net") {
html {
    color: #000 !important;
    background: #232323 !important;
}
table {
    color: #FFFFFF !important;
    background: #232323 !important;
}
p {
    color: #FFFFFF !important;
    background: #232323 !important;
}
.body_wrapper {
    color: #000 !important;
    background: #131313 !important;
}
.blocksubhead {
    color: #FFFFFF !important;
    background: #131313 !important;
}
body_wrapper {
    color: #333333 !important;
    background: #191919 !important;
}
div {
    color: #4388e8 !important;
}
.forumbit_nopost .forumbit_nopost .forumrow,
.forumbit_post .forumrow {
    background: rgb(35, 35, 35) url(http://www.mpgh.net/forum/images/gradients/grey-up.png) repeat-x left bottom !important;
}
ol ol ul,
ol ul ul,
ul ol ul,
ul ul ul {
    color: #FFFFFF !important;
    background: #232323 !important;
}
div.logo_container {
    color: #FFFFFF !important;
    background: #353535 !important;
}
.forum_info .blockbody {
    background: rgb(35, 35, 35) url(http://imgur.com/a/NDcf9) repeat-x !important;
    _background-image: none;
}
.formcontrols .blockrow {
    background: #232323 bottom repeat-x !important;
    border-top: solid 1px #cedfeb;
}
.blockrow a {
    color: #FFFFFF !important;
}
.blockfoot,
.blocksubfoot {
    background: #232323 none !important;
}
.blockfoot a {
    color: #232323 !important;
}
.textcontrols a,
a.textcontrol {
    color: #FFFFFF !important;
    background: #232323 !important;
    -moz-border-radius: 0px;
    -webkit-border-radius: 0px;
}
.memberaction_body.popupbody {
    background: #232323 !important;
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
    -moz-box-shadow: 0px 4px 7px #000000;
    -webkit-box-shadow: 0px 4px 7px #000000;
}
.popupbody li a,
.popupbody li label {
    display: block;
    color: #FFFFFF !important;
    background: #FFFFFF none !important;
}
body a {
    color: #4388e8 !important;
}
.pagination span a.popupctrl {
    background: #232323 url(http://www.mpgh.net/forum/mpghv2/misc/black_downward_arrow.png) no-repeat center left !important;
}
.pagination span a {
    background: #232323 none !important;
    color: #FFFFFF !important;
}
.threadbit .sticky {
    background: #232323 url(http://imgur.com/HrBbi66) repeat-x left bottom !important;
    border: 1px solid #131313 !important;
}
.threadbit .alt {
    background: #232323 url(http://imgur.com/HrBbi66) repeat-x left bottom !important;
}
.threadbit .nonsticky,
.threadbit .deleted,
.threadbit .discussionrow,
.threadbit .ignored {
    border: 1px solid #131313 !important;
}
.threadbit .nonsticky,
.threadbit .discussionrow {
    background: #232323 !important;
}
.breadcrumb .active span.current span.holder {
    background-color: #232323 !important;
    color: #FFFFFF !important;
}
.breadcrumb .navbit a span {
    background-color: #232323 !important;
    color: #FFFFFF !important;
}
.breadcrumb .navbithome {
    background-color: #232323 !important;
    color: #FFFFFF !important;
}
.newcontent_textcontrol {
    background: #232323 url(http://www.mpgh.net/forum/images/buttons/newbtn_middle.png) repeat-x left top !important;
    -moz-border-radius: 2px;
    -webkit-border-radius: 2px;
    border: 1px solid #4388e8 !important;
}
.floatcontainer,
.formcontrols,
dl.stats {
    background: #232323 none !important;
    background-color: #232323 !important;
}
.doc_header {
    border: 20px solid #232323 !important;
    border-right: 5px solid #232323 !important;
    border-left: 5px solid #232323 !important;
    color: #232323 !important;
}
.above_body {
    -moz-border-radius-bottomleft: 5px;
    -moz-border-radius-bottomright: 5px;
    -webkit-border-bottom-left-radius: 5px;
    -webkit-border-bottom-right-radius: 5px;
    border-bottom-left-radius: 5px !important;
    border-bottom-right-radius: 5px !important;
}
ul.navi1,
ul.navi3 {
    border-bottom: 2px solid #232323 !important;
}
.toolsmenu div {
    background: #232323 !important;
    outline: 1px solid #4388e8 !important;
}
.footer_copyright,
.footer_morecopyright {
    color: rgb(66, 134, 244) !important;
    margin-top: 0px !important;
    background: #232323 !important;
}
.below_body {
    border: 1px solid #4286f4 !important;
}
.textbox,
textarea,
select {
    background: rgb(255, 255, 255) none !important;
    border: solid 1px #6b91ab !important;
}
.forum_info_form .options_input_block .button {
    color: #FFFFFF !important;
    background: #191919 !important;
    border: 1px solid #4388e8 !important;
}
.popupbody li a,
.popupbody li label {
    display: block;
    color: #FFFFFF !important;
    background: #232323 none !important;
}
#usercp_nav .blockrow * a {
    background: #131313 !important;
}
.pagination span a {
    border: 1px solid #4388e8 !important;
    background: #232323 none !important;
    color: #FFFFFF !important;
}
.floatcontainer,
.formcontrols .blockrow,
dl.stats {
    outline: 5px solid #343434 !important;
}
.blockrow {
    background: #232323 none !important;
}
.blockbody {
    background: rgb(35, 35, 35) none no-repeat !important;
}
.postcontent {
    background: #232323 none !important;
}
.userprofile {
    border-bottom: 3px #232323 !important;
    background-color: #353535 !important;
}
.postbitlegacy .postrow.has_after_content {
    background-color: #232323 !important;
}
.postbitlegacy .postdetails,
.eventbit .eventdetails {
    background: #f2f6f8 none;
    background-color: #232323 !important;
}
.postbit .posttitle,
.postbitlegacy .title {
    background-color: #232323 !important;
}
.postbitlegacy .postrow,
.eventbit .eventrow,
.postbitdeleted .postrow,
.postbitignored .postrow {
    background-color: #232323 !important;
}
.postbitlegacy .postbody,
.eventbit .eventdetails .eventbody {
    background-color: #232323 !important;
    border-left: 1px solid #232323 !important;
}
.postbitlegacy .userinfo {
    background-color: #353535 !important;
}
.bbcode_quote div.quote_container {
    color: #333333;
    background-color: #353535 !important;
    border: 3px solid #232323 !important;
}
li.sgicon .maincol {
    background-color: #232323 !important;
}
.member_summary dl.stats dt {
    padding-bottom: 5px !important;
    color: #FFFFFF !important;
    background-color: #232323 !important;
}
.member_summary dl.stats dd {
    padding-bottom: 15px !important;
    color: #4388e8 !important;
}
dl.stats dt,
#sidebar_container dl.stats,
#sidebar_container .blockbody.userprof_content,
#sidebar_container .userprof_content .time,
.userprof_content .time {
    outline: 0 !important;
}
#sidebar_container .underblock {
    background: #232323 !important;
}
#sidebar_container.member_summary {
    background-color: #232323 !important;
}
.member_content #sidebar_container div.block {
    background-color: #232323 !important;
}
.attachments {
    background-color: #232323 !important;
    color: #FFFFFF !important;
}
.postbitlegacy .signature,
.postbitlegacy .lastedited {
    color: #FFFFFF !important;
}
li.activitybit {
    background-color: #232323 !important;
}
#activity_tab_container > div {
    background-color: #232323 !important;
}
.userprof_content .blockrow {
    padding: 5px 2px 7px 0px !important;
    outline: 3px solid #232323 !important;
}
#view-aboutme .blockrow {
    padding-right: 5px !important;
    padding-bottom: 15px !important;
}
.profile_content .subsubsectionhead {
    padding: 0px 0px 0px 0px !important;
}
.profile_content .friends_list li {
    background-color: #232323 !important;
}
#profile_tabs a,
#profile_tabs a:hover {
    background-color: #232323 !important;
}
.activitystream_block dd,
dd.userprof_moduleinactive {
    background-image: url(http://imgur.com/0RY2gcI);
    background-color: #232323 !important;
}
.activitystream_block dd.selected,
dd.userprof_module {
    color: #ffffff;
    background-color: #232323 !important;
    border: 2px solid #4388e8 !important;
    background-image: url(http://imgur.com/0RY2gcI);
}
.userprof_headers_border {
    background-color: #232323 !important;
    border: 2px solid #4388e8 !important;
}
}
