css:
/* i really want this to be global */
/* probably.. */
@-moz-document domain("kissmanga.com") {
/* -----------Main Container--------------- */

#container {
	width: 1600px;
    margin-top : 150px;
}
body {
/* 	background-color: #999; */
/*     background: linear-gradient(to top, #222, #333, #999, #fff) ;  */
    height: auto !important;
}

/* -------------All Header----------------- */
#navcontainer ul {
    width: inherit !important;
    margin: 0;
    padding: 0;
    list-style-type: none;
    text-align: center;
    display: inline-flex;
    flex-flow: row;
    flex-wrap: wrap;
    flex-direction: row;
}
#navcontainer {
/*     border-radius: 10px; */
/*  	box-shadow: 8px 10px 20px 0px rgba(46,61,73,0.55); */
 	box-shadow: 0px 10px 25px 2px rgba(98,188,188,0.5);
}
#navcontainer li {
    width: 11.05% !important;
}
#navcontainer a{
	overflow: hidden !important;
    text-align:center;
}
#navcontainer li a#currentTab {
	background: #333;
    color: aquamarine;

}
#navcontainer li a:hover {
    transition: .2s linear;
	background-color: turquoise;
    
}
#navcontainer a:link, #navcontainer a:visited {
    background: aquamarine;
    border-bottom: 2px solid aquamarine;
    margin: auto;
    width: 100%;
    color: #333;
    font-size: 12pt;
    font-weight: lighter;
    padding: 10px 5px;
    text-align: center;
}
#navcontainer {
	width: 100%;
}
#navsubbar {
	position: absolute;
    top:0;
    background: aquamarine;
  
}
#navsubbar p {
	margin:0;
}
#navsubbar a {
    color: #333 !important;
    width:100%;
}
#navbar {
	background: none;
 	margin: auto;
    width: 80%;
    height: 60px;
    margin-bottom: 30px;

}
#navcontainer {
    margin-top: 40px;
    display: flex;
    margin-bottom: 40px;
}
#head h1 {
	width: 35%;
    margin-left: 300px;
    margin-top: 50px !important;
    float: none;
    position: absolute;

  
}
#head h1 a.logo {
	width: 100%;
    margin-left: -60px;
    padding: 0 60px;

}
#head h3 {
	margin-top: 200px;
    margin-right: 245px;
    text-align: center;
}

#head h3 #topHolderBox {
    box-shadow: 4px 10px 20px 0px rgba(46,61,73,0.35);
    border-top: 3px solid aquamarine;
	padding: 20px;
    font-size: 14pt;
    width: 100% !important;
    font-weight: lighter;
    display: block;
    
}
#head #topHolderBox img {
	display: none;
}
#head #topHolderBox span {
	color: white !important;
}
#head #topHolderBox a {
	font-size: 14pt;
    color: aquamarine;
}

#head .boxDropDown {
	padding: 10px;
    width: 200px !important;
    background: inherit;
    border:none;
  	font-size: 14pt;
    font-weight: lighter;
}
#head .boxDropDown small {
	color: aquamarine !important;
}
#menu_box{
	position: absolute;
    top: 55px !important;
    left: 200px !important;
    width: 200px;
    color: #FFF;
    padding: 10px 20px;
    background: #333;
    font-family: Arial;
    font-size: 13px;
    z-index: 100;
    text-align: left;
    border-top: 3px solid aquamarine;
    border-left: 1px solid #333;
    border-right: 1px solid #333;
    
}
#menu_box a {
    color: #fff;
    display: inline-block;
    background: #333;
 	padding: 10px 10px;
    font-size: 12pt;
    line-height: 30px;
    text-decoration: none;
    border-bottom: 1px solid #333;
}
#menu_box a:hover {
	transition: .2s linear;
    margin-left: 4px;
    background: none;
}
#head h3:first-child {

}



/* ------------Seach Section------------- */
#search, #formSearch {
    width: inherit;
    
}

#formSearch .text {
	border-radius: 5px;
    padding: 12px 30px!important;
    background: #111;
    border: 2px solid #333;
}
#formSearch .text:focus , #selectSearch:focus {
    transition: .2s linear;
	border-color: aquamarine;
}
#selectSearch {
	padding: 10px !important;
    width: 200px !important;
    border-radius: 5px;
}
#formSearch input[type="image"] {
	display: none;
}
#search p {
	margin: 10px;
}
#search p input, #search p select#selectSearch {
 	box-shadow: 4px 10px 20px 0px rgba(46,61,73,0.25);   
    margin-left: 5px;
    margin-right: 10px;
}
#search #result_box {
    padding-top: 15px;
    margin-top: 20px !important;
    margin-left: 10px !important;
    width: 450px;
	min-height: 40px;
    font-size: 13pt;
    box-shadow: 8px 10px 20px 0px rgba(46,61,73,0.55);
}
#search #result_box a {
	padding-right: 10px;
    padding: 10px 2px;
}
#search #result_box a:hover {
	transition: .1s ease-in;
    padding-left: 8px;
    background: #111;
}
#search #loader {
    margin-left: 90px !important;
}

#container .banner .details {
    border-top: 3px solid aquamarine;
    border-bottom: 3px solid aquamarine;
    background: #022;
    width: 99.6%;
}


/* Mainly for Bookmark Page (may actually affect other pages) */
.barTitle {
	padding: 10px;
    color: #333;
    background: aquamarine;
}
.barTitle .scrollable_title, #recently-nav .navi {
	color: #333 !important;
}
a.prev, a.prev:hover,
a.next, a.next:hover{
	background: none;
    color: #333;

}
a.prev:before {
    content: "<";
}
a.next:after {
	content: ">";
}
#recently-nav {

}
.scrollable {
	height: 250px;
}
.arrow-general {
	display: none;
}
.details .bigChar {
	color: aquamarine;
    margin-left: 0px !important;
    font-size: 22pt;
}
.details p , .details a{
	color: aquamarine;
    width: 1000px;
    font-size: 12pt;
    
}
.bigBarContainer , #leftside{
	width: 1250px;
}
    table.listing {
	width: 1200px;
}
table.listing tr, td, th{
	padding: 15px 10px;
}
td {
	color: white !important;
}
/* table.listing tr:hover {
	background: aquamarine !important;
}
table.listing tr.head:hover {
	background: none !important;
} */
 table.listing td a{
	font-size: 12pt;
	min-width: 200px !important;
    font-weight: lighter;
    color: aquamarine !important;
}

#rightside {
    float: left !important;
    margin-left: 15px;
}
.rightBox {
	width: 300px;
    font-size: 12pt;
}

/* Home */
.items div a {
    width: 240px;
	height: auto;
}
.items div a img {
	width: 200px;
    height: 240px;
    margin-bottom: 4px;
    border-bottom: 6px solid transparent;
    background-image: linear-gradient(to right, cyan, aquamarine, turquoise); /* Standard syntax (must be last) */
}
.scrollable{
	min-height: 340px;
/*    border: 2px solid aquamarine; */
    display: flex;
    align-items: center;
    align-self : center;
    justify-content: center;
/*     background: white; */
}

/* Home Page Tab Menu (New & Popular) */
#tabmenucontainer {
	width: 1250px;
}
#tabmenucontainer ul {
	width: 100%;
}
#tabmenucontainer li {

}
#tabmenucontainer a {
	background: aquamarine !important;
    width: 50%;
    padding: 10px 0px;
    font-size: 13pt;
    color: #333;
    font-weight: lighter;
}
#tabmenucontainer .tabactive {
	background: #333 !important;
    color: aquamarine;
}

#subcontent::first-child {
	width: 100% !important;
}

#subcontent div .blue{
	width: 1220px !important;
}

#subcontent div div a {
    color: aquamarine;
    font-size: 11pt;
}

#subcontent div div img[src="../../Content/images/numbers/1.png"],
#subcontent div div img[src="../../Content/images/numbers/2.png"],
#subcontent div div img[src="../../Content/images/numbers/3.png"],
#subcontent div div img[src="../../Content/images/numbers/4.png"],
#subcontent div div img[src="../../Content/images/numbers/5.png"],
#subcontent div div img[src="../../Content/images/numbers/6.png"],
#subcontent div div img[src="../../Content/images/numbers/7.png"],
#subcontent div div img[src="../../Content/images/numbers/8.png"],
#subcontent div div img[src="../../Content/images/numbers/9.png"],
#subcontent div div img[src="../../Content/images/numbers/10.png"] {
    display: none;
}



/* Advance Search Section */

#formAdvanceSearch label {
	font-size: 13pt;
    padding: 6px 10px;
}
#formAdvanceSearch input {
	padding: 10px 20px;
    width: 500px;
    background: #111;
    border-radius: 5px;
    border: 2px solid #333;
}
#formAdvanceSearch input:focus {
	transition: .2s linear;
    border: 2px solid aquamarine;
}

#genres a, a.include, a.exclude {
    background-size: 25px !important;
    padding-left: 40px;
    padding-bottom: 10px;
    min-height: 500px !important;
  	color: mediumaquamarine !important;
}
#genres li label {
    height: 30px;
    width: 250px;
    float: left;
    position: relative;
    font-size: 22px !important;
  	padding: 20px 10px;
}
#genres {
	width: 1200px;
}
select#status {
	padding: 10px !important;
    width: 200px !important;
    border-radius: 5px;
}
#btnSubmit {
	background: none !important;
}

/* Request Manga */
.barContent p {
	font-size: 14pt !important;
    padding: 10px 20px;
}
.barContent a {
    color: aquamarine;
}
.barContent .textDark {
	color: white;
}

/* Manga Description */
.bigChar {
	margin-left: 20px;
    margin-top: 20px !important;
    text-decoration: underline;
}
.barContent div {
	padding-top: 10px;
}

.barContent p {
	line-height: 1.5;
    padding: 10px;
}

.bigBarContainer div div {
	font-size: 14pt;
    line-height: 1.5;
    color: aquamarine !important;
}
.chapterVisited:visited {
	color: #555 !important	;
}

/* Form Register */
#formRegister label{
	font-size: 14pt;
    width: 200px;
    margin-right: 10px;
    margin-top: 5px;
}
#formRegister input {
	border-radius: 5px;
    padding: 12px 30px!important;
    background: #111;
	font-size: 14pt !important;
    border: 2px solid #333;
    width: 300px;
}
#formRegister div div {
	font-size: 14pt !important;
    margin-left: 70px;
}
.g-recaptcha,
.g-recaptcha div{
    margin:0 !important;
    padding: 0;
}
#formRegister #btnSubmit {
	margin-left: 60px !important;
    
}

/* Login */
#formLogin label{
	font-size: 14pt;
    width: 200px;
    margin-right: 10px;
    margin-top: 5px;
}
#formLogin input {
	border-radius: 5px;
    padding: 12px 30px!important;
    background: #111;
    border: 2px solid #333;
    font-size: 14pt !important;
    width: 300px;
}
#formLogin div  a {
	font-size: 14pt !important;
    margin-left: 70px;
}

#formLogin #btnSubmit {
	margin-left: 30px !important;
    width: 100px;
    border: 0;
}


/* Profile */
#formProfile input.txtSignUp {
	border-radius: 5px;
    padding: 12px 30px!important;
    background: #111;
    border: 2px solid #333;
    width: 300px;
    font-size: 12pt;
}
#formProfile #btnSubmit {
	border: 2px solid #333;
    padding: 10px 20px;
    width: 320px !important;
    background: #333 !important;
}
#formProfile #btnSubmit:hover,
#formProfile input.txtSignUp:focus{
    transition: .2s linear;
	border: 2px solid aquamarine;
}

/* Reading Mode */
.lbl {
	font-size: 14pt;
    color: aquamarine;
}
#selectReadType, .selectChapter {
	padding: 10px !important;
    width: 130px !important;
    border-radius: 5px;
}
.selectChapter {
	width: 300px !important;
}
.btnNext , .btnPrevious {
	padding-top: 8px;
    padding-left: 20px;
    
}

/* etc */
 a {
    font-size: 11pt;
}
div#divImage p {
	margin: 0;
}
#divImage img {
	max-width: 1600px !important;
    min-width: 920px;
}

a[href="/MangaList/MostPopular"], 
a[href="/MangaList/Newest"] {
	position: relative;
    margin-left: 1090px;
    background: aquamarine;
    padding: 2px 50px;
    border : 2px solid aquamarine;
    color: #333 !important;
}

a[href="/MangaList/MostPopular"]:hover,
a[href="/MangaList/Newest"]:hover,
a[href="/MangaList/LatestUpdate"]:hover {
    transition: .2s linear;
    border-bottom: 2px solid white;

}

a[href="/MangaList/LatestUpdate"] {
	position: relative;
    margin:0;
    background: aquamarine;
    padding: 2px 50px;
    border : 2px solid aquamarine;
   	color: #333 !important;
}

/* Manga List */
#rightside {
	line-height: 2;
    width: 300px;
    margin-right: 10px;
}
#rightside .barContent a {
	font-size: 12pt;
    padding: 0px 2px;
}
#rightside .barContent a:hover {
	transition: .2s ease-in;
    text-decoration: underline;
}

.pager li{
	background: aquamarine !important;
    border: 2px solid aquamarine !important;
    padding: 0;
    padding: 0px 10px !important;
}
.pager li.current {
    background: #333 !important;
	padding: 4px 15px !important;
    color: white !important;
}
.pager li:hover, .pager li:hover a {
    transition: .2s linear;
	background: #333 !important;
    color: white !important;
}
.pager li a {
    border-top: 1px solid #333 !important;
    border-bottom: 1px solid #333 !important;
}
.pager li a:hover {
	border: 0 !important;
    border-top: 1px solid aquamarine !important;
    border-bottom: 1px solid aquamarine !important;
}
}
