css:
@-moz-document domain("forum.rigsofrods.org"), domain("docs.rigsofrods.org"), domain("multiplayer.rigsofrods.org") {
body {
    background: #141414 !important;
}

.thead {
    background: url("http://www.animos.us/uploads/692608894.png") repeat !important;
}

.pm_alert {
    background: url("http://www.animos.us/uploads/755107046.png") repeat !important;
    border:0px;
}

.w3-striped tbody tr:nth-child(2n) {
  background: #141414 !important;
}

.trow_deleted, .post.deleted_post {
  background: #610202 !important;
}

.spoiler_title {
        background: url("http://www.animos.us/uploads/692608894.png") repeat !important;
}

.highlight {
        background: url("http://www.animos.us/uploads/692608894.png") repeat !important;
}

.spoiler_content {
        background: #333;
}

.spoiler {
    background: #333;
}
 
a {
    color: #fff !important;
}

.author_statistics {
    color: #fff !important;
}
   
a.button:link, a.button:hover, a.button:visited, a.button:active {
    border: 1px solid #111 !important;
}
 
#main {
    background: url("http://i.imgur.com/tw0ILKL.png") repeat !important;
}
 
#main .wrapper {
    background: #1b1b1b !important;
    border-left: 1px solid #303030 !important;
    border-right: 1px solid #303030 !important;
}
 
#container {
    color: #aaa !important;
}
 
#content {
    background: #1b1b1b;
    width: auto !important;
    padding: 20px 10px;
    overflow: hidden;
}
   
#logo {
    background: #1b1b1b url(http://www.animos.us/uploads/211351600.png) top left repeat-x;
    border-bottom: 1px solid #011929;
}
   
#panel .upper {
    border: 0 none !important;
}
 
#panel .lower {
    background: #1b1b1b !important;
    border-bottom: 1px solid #303030 !important;
    border-top: 1px solid #262626 !important;
}
 
header #siteSearch label.searchContainer {
    background: #333 !important;
    border: 1px solid #303030 !important;
}
 
header #siteSearch input.inputText {
    background: #333 !important;
    color: #aaa !important;
}
 
header nav#logoLink a {
    background: url("https://forum.rigsofrods.org/images/logos/rorlogo.png") no-repeat !important;
}
 
.navigation {
    color: #999 !important;
}
 
.navigation .active {
    color: #999 !important;
}
 
.pagination a {
    background: #222 !important;
    border: 1px solid #303030 !important;
    color:#aaa !important;
}
 
.pagination .pagination_current {
    color: #aaa !important;
}
 
table {
    color: #aaa !important;
}
 
.tborder {
    background: #222 !important;
    border: 1px solid #333 !important;
}
 
.trow1 {
    background: #292929 !important;
    border-color: #555 #333 #333 #555 !important;
}
 
.trow2 {
    background: #333 !important;
    border-color: #555 #333 #333 #555 !important;
}
 
.trow_sep {
    background: #222 !important;
    border-bottom: 1px solid #191919 !important;
    color: #aaa !important;
}
 
.post .post_author {
    background: #333 !important;
    border-bottom: 1px solid #222 !important;
    border-top: 1px solid #222 !important;
}
 
.post.classic .post_author {
    border-color: #444 !important;
}
 
.post_controls {
    background: #333 !important;
    border-bottom: 1px solid #444 !important;
}
 
.postbit_buttons > a:link, .postbit_buttons > a:hover, .postbit_buttons > a:visited, .postbit_buttons > a:active {
    background: #2b2b2b !important;
    border: 1px solid #444 !important;
}
 
.post .post_author div.author_avatar img {
    background: #2b2b2b !important;
    border: 1px solid #444 !important;
}
   
.post.classic .post_author div.author_statistics {
    border-top: 1px dotted #444 !important;
}
 
.post .post_head {
    border-bottom: 1px dotted #333 !important;
}
 
.post_content .signature {
    border-top: 1px dotted #333 !important;
}
 
blockquote {
    background: #333 !important;
    border: 1px solid #444 !important;
}
 
blockquote cite {
    border-bottom: 1px solid #555 !important;
}
 
.codeblock {
    background: #333 !important;
    border: 1px solid #444 !important;
}
 
.codeblock .title {
    border-bottom: 1px solid #555 !important;
}
 
code {
    background: #333 !important;
    border: 1px solid #444 !important;
}
 
code:before {
    border-bottom: 1px solid #444 !important;
}
 
.tfoot {
    background: #333 !important;
    border-top: 1px solid #262626 !important;
}
 
textarea {
    background: #111 !important;
    border: 1px solid #303030 !important;
}
 
body, button, input, select, textarea {
    color: #aaa !important;
}
 
html, body, p, code:before, table {
    color: #aaa !important;
}
 
input.button {
    background: #2b2b2b !important;
    border: 1px solid #444 !important;
}
 
input.textbox {
    background: #333 !important;
    border: 1px solid #222 !important;
}
 
footer {
    color: #555 !important;
}
 
#footer .wrapper {
    border: 0 none !important;
}
 
#footer .upper {
    background: #1b1b1b !important;
    border: 1px solid #303030 !important;
}
 
.sceditor-container {
    background: #333 !important;
    border: 1px solid #555 !important;
}
 
div.sceditor-toolbar {
    background: #333 !important;
    border-bottom: 1px solid #555 !important;
}
 
div.sceditor-group {
    background: #555 !important;
    border-bottom: 1px solid #444 !important;
}
 
div.sceditor-dropdown {
    background: #444 !important;
    border: 1px solid #333 !important;
}
 
.sceditor-dropdown a:hover {
    background: #555 !important;
}
 
.sceditor-button:hover, .sceditor-button:active, .sceditor-button.active {
    background: #777 !important;
}
 
select {
    background: #2b2b2b !important;
    border: 1px solid #444 !important;
}
 
.thead a {
    color: #fff !important;
}
 
.drop_go_page {
    background: #444 !important;
    border: 1px solid #222 !important;
}
 
footer {
    background: #444 !important;
    border-top: 1px solid #333 !important;
}
 
fieldset {
    border: 1px solid #444 !important;
}
 
.select2-container-multi .select2-choices {
    background: #292929 !important;
    border: 1px solid #222 !important;
}
 
.select2-drop {
    background: #333 !important;
    color: #aaa !important;
}
 
.select2-results .select2-no-results, .select2-results .select2-searching, .select2-results .select2-selection-limit {
    background: #333 !important;
}
 
.select2-container .select2-choice {
    background: #2b2b2b !important;
    border: 1px solid #444 !important;
}
 
.select2-search input {
    background: url("http://community.mybb.com/jscripts/select2/select2.png") no-repeat scroll 100% -22px, linear-gradient(to bottom, #444 85%, #333 99%) repeat scroll 0 0 rgba(0, 0, 0, 0) !important;
    border: 1px solid #555 !important;
}
 
.blog_updates.release {
    background-color: #fce972 !important;
    border-color: #ffe752 !important;
    color: #555 !important;
}
 
.blog_updates.latest {
    background-color: #cdff5e !important;
    border-color: #c9ff52 !important;
    color: #555 !important;
}
 
.pm_alert {
    background-color: #fce972 !important;
    border-color: #ffe752 !important;
    color: #555 !important;
}
 
.mysupport_staff_highlight {
    background: rgba(255, 255, 255, 0.2) !important;
}
 
div.sceditor-dropdown label {
    color: #aaa !important;
}
 
div.sceditor-dropdown input, div.sceditor-dropdown textarea {
    background: #222 !important;
    border: 1px solid #555 !important;
}
 
.popup_menu {
    background: #222 !important;
    border: 0 none !important;
}
 
.popup_menu .popup_item {
    background: #444 !important;
}
 
button {
    background: #333 !important;
    border: 1px solid #555 !important;
    border-radius: 2px !important;
    margin: 1px !important;
}
 
button:hover {
    background: #444 !important;
}
}
