css:
@-moz-document domain("msreview.net") {
#main_menu {
    height: 230px;}

li.two.odd,li.four.odd,li.six.odd{
margin:0px!important;
}

#main_menu li {
    margin:0px!important;
    float: none;
    width: 100%;
    height: 40px;
}
#main_menu li a {
    display: block;
    width: 100%;
    height: 40px;
}
#main_menu li:hover, #main_menu li.odd:hover {
        width: 290px;
        height: 40px;
        border: 0px #e3e6e7 solid;
            background-color: rgba(0, 0, 0, 0.07);
}
li.one:active, li.two.odd:active,li.three:active,li.four.odd:active,li.five:active,li.six.odd:active {
        transform: scale(0.95);
}
#main_menu li:hover a {
        width: 100%;
        height: 40px;
}
#main_menu li a span {
        position: absolute;
        bottom: 10px;
        left: 50px;
        right: inherit;
        color: #000;
        text-transform: uppercase;
        font-size: 14px;
        text-align: left;
}
#main_menu li.two a,#main_menu li.one a,#main_menu li.three a,#main_menu li.four a,#main_menu li.five a,#main_menu li.six a {
        background-color: rgba(77, 206, 252, 0);
        background-size: 16px;
        background-position:16px center;
    background-repeat:no-repeat;
}

}

@-moz-document url-prefix("https://msreview.net/hardware/") {
#main_menu > li.one > a > span {
        color: #4dcefc;
        font-weight: 700;
}
li.one:after {
        content: "";
        position: absolute;
        width: 4px;
        height: 20px;
        background-color: #4dcefc;
        top: 10px;
}
}

