css:
/* Pdf.js edge style 1.0 
   Created by Gaman
*/
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document regexp(".*\.(p|P)(d|D)(f|F)") {
    /*div{color: #C7EDCC !important;}
    div.textLayer{background-color: #151515 !important; }*/
    body {
      background-color: #242424;
      background-image: none !important; 
    }
    #toolbarSidebar {
       width: 200px;
       height: 32px;
       background-color: #383838;
       background-image: none !important;
    }

    html[dir="ltr"] #toolbarContainer, .findbar, .secondaryToolbar {
      box-shadow: none !important;;
    }
  
    #toolbarContainer, .findbar, .secondaryToolbar { 
      background-color: #383838 !important;
      background-image: none !important;
    }
  
    .pdfViewer .page {
      margin: 1px auto -8px auto;
      position: relative;
      overflow: visible;
      border: 4px solid transparent !important;
      background-clip: content-box;
      border-image: none !important;
      background-color: white;
    }
  
    .toolbarButton:hover, .toolbarButton:focus, .dropdownToolbarButton, .overlayButton, .secondaryToolbarButton:hover, .secondaryToolbarButton:focus {

      border: 0px solid #383838;
      border-color: black;
      box-shadow: none;
    }
  
    html[dir="ltr"] .splitToolbarButtonSeparator {
      display: none;
    } 
  
}
