css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("myanimelist.net") {
    
    .page-common div.updatesBar {
        background-color: #E7CDF5;
    }
    
    .ownlist_style .style-setting .setting-switch input[type="radio"] + label {
        background-color:  #E2C6F2;
        color: #5b0f85;
    } 
    .ownlist_style .style-setting .setting-switch input[type="radio"]:checked + label {
        background-color: #5b0f85;
    }
    
    #dialog td {
       border: 1px solid #5b0f85;
    }
    
     #dialog {
       border: 10px solid #FFE3EB;
    }
    
    .setting-row.locate-right {
        background-color: #FFE3EB;
    }
    
    .page-common .inputButton {
        background-color: #5B0F85;
        border-bottom-color: #72249D;
        border-right-color: #72249D;
        border-left-color: #8342A8;
        border-top-color: #8342A8;
    }
    
    
    .page-common #headerSmall {
         background-color:#fff;
         background-image:url(https://i.imgur.com/rGwt3NS.png);
         background-position:0 8px;
         background-repeat:no-repeat;
         height:36px;
         padding:10px 0 2px;
         position:relative;
    }
    
    .fa-bell{
        color: #5b0f85 !important;
    }
    
     .fa-bell:hover{
        color: #8342a8 !important;
    }
    
    page-common .header-notification-button {
       color: #5b0f85 !important;
       cursor: pointer;
       text-align: center;
    }
    
    page-common .header-notification-button:hover {
       color: #8342a8 !important;
    }
    
    .page-common .header-message-button {
       color: #5b0f85;
    }
    
    .page-common .header-message-button:hover {
      color: #8342a8;
    } 
    
    .page-common .header-message-button.has-unread {
        color: #5b0f85;
    }
    
    .page-common .header-message-button.has-unread:hover {
      color: #8342a8;
    }
    
    .page-common .header-list-button {
        color: #5b0f85;
        cursor: pointer;
    }
    
    .page-common .header-list-button:hover {
        color: #8342a8;
        cursor: pointer;
    }
    
    .icon-top-ranking-page .prev, .icon-top-ranking-page .next {
        background-color: #8342a8;
    }
    
    .page-common a, .page-common a:visited {
        color: #5b0f85;
        text-decoration: none;
    }
    
    .page-common a, .page-common a:hover {
       color: #5b0f85;
       text-decoration: underline;
    }
    
    .top-ranking-table .status .btn-addEdit-large.btn-anime-watch-status.dropped {
        border: #AD0B47 1px solid;
        color: #AD0B47;
    }
    
    .top-ranking-table .status .btn-addEdit-large.btn-anime-watch-status.watching {
        border: #F3B9CD 1px solid;
        color: #F3B9CD;
    }
    
    .top-ranking-table .status .btn-addEdit-large.btn-anime-watch-status.on-hold {
        border: #E8FB69 1px solid;
        color: #E8FB69;
    }
    
    .page-common .pagination .link-blue-box {
         background-color: #8342a8;
    }
    
    .page-common .pagination .link-blue-box:hover{
        background-color: #c598de ;
    }
    
    .top-ranking-table .status .btn-addEdit-large.btn-anime-watch-status.notinmylist {
        background-color: #5b0f85;
        border: #5b0f85 0 solid;
        color: #fff;
        text-decoration: none;
    }
    
    .top-ranking-table .status .btn-addEdit-large.btn-anime-watch-status.notinmylist:hover {
        background-color: #8342a8;
    }
    
    .top-ranking-table .status .btn-addEdit-large.btn-anime-watch-status.completed {
        border: #5b0f85 1px solid;
        color: #5b0f85;
    }
    
    .icon-top-ranking-page .next{
        background-color: #8342a8;
    }
    
    .icon-top-ranking-page .prev:hover,.icon-top-ranking-page .next:hover {
        background-color:#c598de;
        color:#fff;
        text-decoration:none
    }
    
    .top-ranking-table .table-header td{
        background-color: #8342a8;
        border-color: #8342a8;
    }
    
    .page-common #horiznav_nav ul li a{
        color: #5b0f85;
    }
    
    .page-forum .forum_category{
        background-color: #8342a8;
    }
    
    .page-common a, .page-common a:visited {
        color: #5b0f85;
        text-decoration: none;
    }
    
    .page-common #horiznav_nav {
        border-color:#5b0f85;
    }
    
    .page-common #horiznav_nav ul li .horiznav_active {
        background-color: #5B0F85;
        color: #fff;
    }
    
    .page-common #horiznav_nav ul li a:hover {
        background-color:#5b0f85;
        color:#fff;
    }
    
    .user-profile .user-button .btn-profile-submit{
        background-color: #5B0F85;
    }
    
    .user-profile .user-button .btn-profile-submit:hover,.user-profile .user-button .btn-profile-submit:active {
         background-color:#cca1e3;
    }
    
    .page-common #menu{
      background-color: #5B0F85;

    }

    .page-common #nav {
     list-style:none;
     margin:0;
     padding:0
    }
    .page-common #nav li {
     display:block;
     float:left;
     position:relative;
     text-indent:5px;
     width:100px;
     z-index:90
    }
    .page-common #nav li a {
     color:#fff;
     display:block;
     font-size:13px;
     font-weight:700;
     height:24px;
     padding:10px 5px 0;
     text-align:left;
     text-decoration:none
    }
    .page-common #nav li a:hover,.page-common #nav li.hover a {
     background:#E7CDF5;
     color:#000
    }
    .page-common #nav li.hover a.non-link {
     cursor:default
    }
    .page-common #nav ul {
     display:none;
     left:0;
     list-style:none;
     padding:0;
     position:absolute
    }
    .page-common #nav ul li {
     float:left;
     width:105px
    }
    .page-common #nav ul a {
     background:#e1e7f5;
     color:#000;
     display:block;
     font-size:11px;
     font-weight:400;
     height:15px;
     padding:8px 5px
    }
    .page-common #nav ul a:hover {
     background:#5B0F85;
     color:#fff;
     text-decoration:none
    }

    .page-common .h1 {
        background-color: #E7CDF5;
        border-bottom-color: #5B0F85;
        border-left-color: #ebebeb;
        border-right-color: #ebebeb;
        border-style: solid;
        border-top-color: #ebebeb;
        border-width: 1px;
        color: #000;
        font-family: Verdana,Arial;
        font-size: 16px;
        font-weight: 700;
        margin: 0;
        padding: 5px 9px;
        text-align: left;
    }

    .right_top_ad{
        display: none !important;
    }
    
    .btn-adblock-mal-supporter{
        display: none !important;
    }
    
    .page-common a.button_add.button_add.reading, .page-common a.button_add.button_add.watching, .page-common a.button_add.button_edit.reading, .page-common a.button_add.button_edit.watching, .page-common a.button_edit.button_add.reading, .page-common a.button_edit.button_add.watching, .page-common a.button_edit.button_edit.reading, .page-common a.button_edit.button_edit.watching {
    background-color: #F3B9CD;
    color: #fff;
    }
    
    .page-common a.button_add.button_add.completed, .page-common a.button_add.button_edit.completed, .page-common a.button_edit.button_add.completed, .page-common a.button_edit.button_edit.completed {
    background-color: #BA77BF;
    color: #fff;
    }
    
    .page-common a.button_add.button_add.on-hold, .page-common a.button_add.button_edit.on-hold, .page-common a.button_edit.button_add.on-hold, .page-common a.button_edit.button_edit.on-hold {
    background-color: #E8FB69;
    color: #fff;
}
    
    ._unit table{
        display: none !important;
    }
    
    .amazon-ads{
        display: none !important;
    }
    
    #footer-block{
        display: none !important;
    }
    
    .banner-header-anime-straming{
        display: none !important;
    }
    
}
