css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("eldarya.com"),
domain("eldarya.com.br") {
    /*Seven Familiar*/
    /*Images - pictures*/
    
    #pet-headerPortrait-base {
        background: url("https://2.bp.blogspot.com/-mEIIX8GLu9M/WMqnpTmxCBI/AAAAAAAANfs/P6av5q1A0E8IYbaH2z5ZHCpq8cu-ptijQCLcB/s400/Screenshot_2017-03-16_10-58-16.png") -15px top no-repeat !important;
        background-size: auto 125px !important;
    }
    #pet-portrait {
        background: url("https://images.8tracks.com/cover/i/010/254/558/tumblr_of6qeyUtau1uw7zkoo1_500-6452.png?rect=0,0,500,500&q=98&fm=jpg&fit=max") center no-repeat !important;
        background-size: auto 450px !important;
        height: 450px !important;
    }
    #pet-portrait > img[src*="/static/img/pet/icon/"] {
        display: none !important;
    }
    /*Infos*/
    
    #pet-info-name:before {
        content: "Saeyoung Choi" !important;
        font-size: 20px !important;
    }
    #pet-info-name {
        font-size: 0 !important;
    }
    #pet-info-evolve > .on {
        font-size: 0 !important;
    }
    .pet-info-evolve-icon {
        font-size: 13px !important;
    }
    .pet-info-evolve-icon:after {
        content: "Defender of Justice 707 will save the day!";
    }
    #pet-info-racegender:before {
        content: "Hacker" !important;
        font-size: 16px !important;
        line-height: 20px !important;
    }
    #pet-info-racegender {
        font-size: 0 !important;
        margin-top: 5px;
    }
    img[src*="/static/img/f.png"] {
        background: url("http://www.eldarya.fr/static/img/m.png") !important;
        padding-left: 14px !important;
        padding-top: 15px !important;
        width: 0 !important;
        height: 0 !important;
        margin-bottom: 5px !important;
    }
    /*Nourriture - food*/
    
    #pet-info-food > img {
        background: url("http://vignette3.wikia.nocookie.net/mystic-messenger/images/4/43/Common_38.png/revision/latest?cb=20161009040509") !important;
        background-size: 60px 60px !important;
        margin-top: -5px !important;
        padding-left: 60px !important;
        padding-top: 60px !important;
        width: 0 !important;
        height: 0 !important;
    }
    #pet-info-food {
        font-size: 0 !important;
    }
    #pet-info-food:before {
        content: "Honey Buddha Chips" !important;
        font-size: 16px !important;
    }
    #pet-info-food > span {
        font-size: 16px !important;
    }
    /*God Seven has arrived*/
    
    #player-display-pet {
        background: url("http://vignette3.wikia.nocookie.net/mystic-messenger-br/images/b/b2/707_render.png/revision/latest?cb=20161128023449&path-prefix=pt-br") no-repeat;
        background-size: auto 100%;
        height: 540px !important;
        width: 205px !important;
    }
    #player-display-pet > img {
        display: none !important;
    }
}
