css:
@-moz-document domain("www.taobao.com") {
/**
普通隐藏
**/

* {
	display: none;
}
/**
通用
**/

html,
head,
body,
h1,
h2,
h3,
h4,
h5,
ul,
p,
button,
form,
input,
/**
顶部导航
**/

.cover,
.site-nav,
.site-nav div,
.site-nav div div,
.site-nav div div div,
.site-nav div div div div,
.site-nav div div div div div,
.site-nav-menu-bd-panel,
.site-nav-bd,
.site-nav-bd-l,
.site-nav-bd-r,
.site-nav-menu-hd,
.site-nav-menu-bd,
.site-nav-arrow,
.site-nav-icon,
/**
搜索框
**/

.cup,
.cup .top,
.cup .top .top-wrap,
.tbh-logo,
.logo,
.tbh-search,
.search-wrap,
.search-bd,
.search-triggers,
.search-tab-icon,
.search-panel,
.search-button,
.search-panel-fields,
.search-combobox-input-wrap,
.search-combobox,
div.search-imgsearch-panel,
div.search-imgsearch-panel div,
div.search-imgsearch-panel div div,
div.search-imgsearch-panel div div div,
/**
搜索结果推测框
**/
.search-popupmenu,
.search-popupmenu > div,
.search-popupmenu > div > div,
.search-popupmenu > div > div > div,
.search-popupmenu > div > div > div > div {
	display: block;
}

ul > li {
	display: list-item;
}

span,
b,
a{
	display: inline-block;
}

.search-button {
	display: none;
}
}

@-moz-document url("https://www.taobao.com/"), url("http://www.taobao.com/"), url-prefix("https://www.taobao.com/?spm="), url-prefix("http://www.taobao.com/?spm=") {
.top {
	height: 100%;
	top: 36px;
	bottom: 0px;
	left: 0px;
	right: 0px;
	width: 100%;
	z-index: -1;
}
.ta-rect {
	margin-top: 300px;
}
.logo {
	position: fixed;
	top: calc(30% - 80px);
	left: calc(50% - 100px);
	margin: 0px auto;
	width: 250px;
}
.top .ta-rect .rect-wrap,
.search-bd {
	margin-left: 0px;
	margin-right: 0px;
	position: fixed;
	top: calc(40% - 80px);
	left: calc(50% - 300px);
	width: 600px;
}
.ta-extra {
	display: none;
}
.J_FixedTool,
.fixedtool,
.search-hots-fline,
.search-hots-sline,
.search-hots {
	display: none!important;
}
.tb-toolbar-right,
.tbh-super-banner {
	display: none!important;
}
body {
	background-color: rgba(0, 0, 0, 0);
	height: 500px;
}
.logo h1 .logo-bd {
	width: 250px!important;
	background: url(http://img.alicdn.com/tfs/TB1_uT8a5ERMeJjSspiXXbZLFXa-143-59.png) 0 0 no-repeat;
}
/*
.logo{
     background: url(http://o837j3tok.bkt.clouddn.com/taobao_logo_2.png);
    background-size:250px;
}*/

.tbh-ad-inner {
	display: none!important;
}
.search-button .btn-search,
.tbh-nav {
	display: none;
}
.search-bd .search-panel .search-common-panel .tb-icon {
	top: 38px;
}
body,
div.decorations-box,
.top {
	background-color: #fff;
	/* background-color: #f1f1f1;*/
}
.search-bd .search-panel .search-common-panel {
	padding: 3px 0 3px 0px;
	background: none;
}

/*
搜索框上面的 文字
*/

.search-bd .search-triggers .goods-search-tab,
.search-bd .search-triggers .tmall-search-tab,
.search-bd .search-triggers .shop-search-tab {
	color: #777;
	font-size: 15px;
	background: none;
}
.search-bd .search-triggers .ks-switchable-nav .selected:hover {
	background-color: inherit;
	color: #FF5400;
}
.search-bd .search-triggers .ks-switchable-nav li:not(.selected):hover {
	color: #222;
	background-color: inherit;
}
.search-bd .search-triggers .selected {
	color: #FF5400;
	font-weight: bold;
	background-color: inherit;
}
.search-bd .search-triggers .tmall-search-tab.selected {
	background-color: inherit;
}
.search-bd .search-panel div.search-common-panel {
	background-color: inherit!important;
}
/*
输入框
*/

.search-bd .search-panel .search-common-panel #q {
	vertical-align: top;
	overflow-y: visible;
	font-size: 1.1em;
	width: 90%;
	padding-left: 6px;
	height: 32px;
}
.search-bd .search-panel .search-common-panel .search-combobox-input-wrap {
	width: 100%;
	height: 44px;
}

/*
	启动动画
*/

@keyframes fadeInUp {
	0% {
		opacity: 0;
		-webkit-transform: translate3d(0, 100%, 0);
		transform: translate3d(0, 100%, 0);
	}
	100% {
		opacity: 1;
		-webkit-transform: none;
		transform: none;
	}
}

.search-bd {
	-webkit-animation-duration: 1s;
	animation-duration: 1s;
	-webkit-animation-fill-mode: both;
	animation-fill-mode: both;
	animation-name: fadeInUp;
	-webkit-animation-name: fadeInUp;
	animation-delay: 0.3s;
	-webkit-animation-delay: 0.3s;
}

/**
修正传图片图标的BUG
**/
@keyframes show {
	0% {
		opacity: 0;
	}
	100% {
		opacity: 1;
	}
}
/**
修正传图片图标的BUG
**/
.search-imgsearch-panel {
	animation-name: show;
	animation-duration: 1s;
	animation-delay: 3s;
	animation-fill-mode: both;
}

/**
搜索框
**/
.search-bd .search-panel .search-common-panel {
	border: none;
	box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.16), 0 0 0 1px rgba(0, 0, 0, 0.08);
}

/**
淘宝样式bug
**/
#J_Tmsg div.tmsg_panels {
	display: none;
}

/**
 单身节..
**/
.search-panel-fields,
.search-panel-fields:before,
.search-bd .search-panel .search-common-panel:before{
	border:none;
}
}
