css:
@-moz-document domain("skribbl.io") {
body {
    background: #333333;
    background-size: 100%;
    color: white;
}

.loginPanelTitle {
    display: flex;
    align-items: center;
    padding: 4px 8px;
    font-size: 24px;
    background-color: #666666;
    border-radius: 5px 5px 0 0;
    border-bottom: 1px solid #FF9900;
}

.loginPanelContent {
    background-color: #666666;
    border-radius: 0 0 5px 5px;
    padding: 8px;
}

#loginAvatarCustomizeContainer {
    margin-top: 8px;
    background-color: #666666;
    display: flex;
    justify-content: center;
    Border-radius: 5px 5px 0 0;
    border: 1px solid #FF9900;
}

.form-control {
    display: block;
    width: 100%;
    height: 34px;
    padding: 6px 12px;
    font-size: 14px;
    line-height: 1.42857143;
    color: #fff;
    background-color: #666666;
    background-image: none;
    border: 1px solid #FF9900;
    border-radius: 4px;
    -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
    box-shadow: inset 0 1px 1px rgba(0,0,0,.075);
    -webkit-transition: border-color ease-in-out .15s,-webkit-box-shadow ease-in-out .15s;
    -o-transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
    transition: border-color ease-in-out .15s,box-shadow ease-in-out .15s;
    
    
}

.iconPlay {
    background-image: url(http://skribbl.io/res/pen.gif);
}

.btn-success {
    color: white;
    background-color: #FF9900;
    border-color: #e08702;
}

.btn-success:hover {
    color: white;
    background-color: #e08702;
    border-color: #e08702;
}

.form-control:focus {
    border-color: #e08702;
    outline: 0;
    -webkit-box-shadow: inset 0 1px 1px rgba(0,0,0,.075), 0 0 8px rgba(224, 135, 2);
    box-shadow: inset 0 1px 1px rgba(0,0,0,.075), 0 0 8px rgba(224, 135, 2);
}

.moregames, .moregames a:visited {
    width: 100%;
    text-align: center;
    color: #FF9900;
}

.btn-info {
    color: white;
    background-color: #5bc0de;
    border-color: #46b8da;
    padding: 10px;
}

.logoSmall {
    margin-left: 38%
}

.lobbySettings .lobbyName {
    flex: 0 0 auto;
    font-size: 28px;
    text-align: center;
    color: white;
    width: 100%;
    background-color: #fff;
    border-radius: 5px 5px 0 0;
    border: 2px solid #FF9900;
    background-color: #666666;
	border-bottom: none;
}

.lobbySettings .lobbyContent {
    flex: 0 0 auto;
    font-size: 28px;
    text-align: center;
    color: white;
    width: 100%;
    background-color: #fff;
    border-radius: 0px 0px 5px 5px;
    border-top: none;
    border: 2px solid #FF9900;
    background-color: #666666;
}

#invite {
    flex: 1 1 auto;
    height: 32px;
    background-color: #666666;
    border: 1px solid #FF9900;
    border-radius: 5px 0px 0px 5px;
    font-size: 18px;
    line-height: 32px;
    text-align: center;
}

}
