css:
@-moz-document domain("bitchute.com") {
body {
        background: #333;
}
body,
p,
h1,
h2,
h3,
h4,
h5,
h6,
.video-info-bar ul li,
.form-control,
#main-bar .search-box .search-icon i.fa,
.subscription-banner,
.subscription-name,
.subscription-last-video,
.channel-videos-details,
.pagination>.disabled>a, .pagination>.disabled>a:focus, .pagination>.disabled>a:hover, .pagination>.disabled>span, .pagination>.disabled>span:focus, .pagination>.disabled>span:hover,
.pagination>li>a, .pagination>li>span{
    color: #eee !important;
}
a {
    color: #d82727 !important;
}
#main-bar {
border-bottom: none;
}
.video-info-bar .info-ul .sharing-drop,
code,
.video-info-bar .info-ul,
#cssmenu,
#main-navigation,
#footer,
.modal-dialog,
.modal-content,
.video-card,
.subscription-banner,
.pagination>.disabled>a, .pagination>.disabled>a:focus, .pagination>.disabled>a:hover, .pagination>.disabled>span, .pagination>.disabled>span:focus, .pagination>.disabled>span:hover,
.pagination>li>a, .pagination>li>span{
    background: #222;
    border: none;
}
.video-info-bar ul li {
    border-right: solid 1px #666;
}
#page-bar,
.category-list li a {
    border-bottom: solid 1px #666;
}
.carousel-indicators li {
    border-color: #333;
}
.table-hover>tbody>tr:hover,
.hding-bottm-line,
.nav-tabs>li.active>a, 
.nav-tabs>li.active>a:focus, 
.nav-tabs>li.active>a:hover,
.pagination>.active>a, .pagination>.active>a:focus, .pagination>.active>a:hover, .pagination>.active>span, .pagination>.active>span:focus, .pagination>.active>span:hover {
    background: #555;
}
.pagination>.active>a, .pagination>.active>a:focus, .pagination>.active>a:hover, .pagination>.active>span, .pagination>.active>span:focus, .pagination>.active>span:hover {
    border-color: #444;
}
.table-hover>tbody>tr:hover {
    cursor: pointer;
}
#contact-form .form-control,
#hero,
#copyright,
.form-control{
    background: #444;
}
.pagination>li>a:hover {
    color: #333 !important;
}
}
