css:
@-moz-document url-prefix("https://www.shopgoodwill.com/") {
/*Enjoy the style? Leave a thumbs up!*/

body {
    background-color: #282828;
    color: #d1d1d1;
}
.header-top {
    background: #282828;
}
.header-bottom {
    background: #025067;
}
.breadcrumb {
    background:none !important;
}
#productCarousel .item a {
    background:none !important;
}
.product-page .purchase-desktop .purchase {
    background: #3d3d3d;
}
.product-page ul.product-data {
    background: #3d3d3d;
}
.product-page .purchase-desktop .add-to-watch-list a, .product-page .purchase-desktop .contact-seller a, .product-page .purchase-desktop .print-page a {
    color: #337ab7;
}
ul.products li .image {
    background: #3d3d3d;
}
footer.site-footer {
    background: #3d3d3d;
}
footer.site-footer .footer-lower {
    background: #3d3d3d;
}
footer.site-footer .footer-bottom {
    background: #282828;
}
.grid-view ul.products li .data-container {
    color: #337ab7;
}
.modal-content {
    background: #282828;
}
.sign-in {
    background: #282828;
}
.panel-login {
    background: #3d3d3d;
}
.yamm .dropdown.yamm-fw .dropdown-menu {
    background: #282828;
}
.widget {
    background: #282828;
    margin-bottom: 0rem;
}
.widget .tree li a {
    color: #00edff;
}
.widget .widget-title {
    color: #d1d1d1;
}
.widget .tree li a.active {
    color: #d1d1d1;
}
.list-view ul.products li {
    background: #3d3d3d;
}
.list-view ul.products li .product-data div, .list-view ul.products li .product-price div {
    color: #00edff;
}
section.product-grid ul.product-header {
    background:none !important;
}
.table-striped>tbody>tr:nth-of-type(odd) {
    background: #3d3d3d;
}
#result-div {
    background: #272727;
}
.panel-body {
    background-color: #272727;
}
.table>caption+thead>tr:first-child>td, .table>caption+thead>tr:first-child>th, .table>colgroup+thead>tr:first-child>td, .table>colgroup+thead>tr:first-child>th, .table>thead:first-child>tr:first-child>td, .table>thead:first-child>tr:first-child>th {
   background-color:  #272727;
}
.invoice .invoice-heading {
    background:  #3d3d3d;
}
}
