css:
@-moz-document url-prefix(https://www.dropbox.com)
{
/*Hintergrund Kopfleiste
************************/
.maestro .page-header {
    position: fixed;
    z-index: 5;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-pack: justify;
    -ms-flex-pack: justify;
    justify-content: space-between;
    white-space: nowrap;
    left: 240px;
    width: calc(100% - (200px + 2 * 40px));
    min-width: 744px;
    background: #212121;
    padding: 40px 0 0 40px;
    height: 56px;
}
* {
    font-weight: inherit;
}
/*Hintergrund Suchleiste DropDown1
**********************************/
.search-bar__suggestion--is-highlighted {
    background-color: #171717;
}
.search-bar__suggestions a {
    color: #007AF5;
}
/*Hintergrund Suchleiste DropDown2
**********************************/
.search-bar__dropdown {
    margin-top: 10px;
    width: 350px;
    border: 1px solid rgba(27,39,51,0.08);
    -webkit-box-shadow: 0 5px 7px 2px rgba(99,114,130,0.16);
    box-shadow: 0 5px 7px 2px rgba(99,114,130,0.16);
    border-radius: 4px;
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
    visibility: hidden;
    position: absolute;
    height: 0;
    overflow: visible;
    background: #171717;
        background-clip: border-box;
    background-clip: padding-box;
    opacity: 0;
}
/*Hintergrund KÃ¶rper Dateiliste
*******************************/
body {
    background-color: #212121;
    min-height: 100%;
    margin: 0;
    padding: 0;
}
body, input, textarea, select, button, .normal {
    font-family: "Open Sans","lucida grande","Segoe UI",arial,verdana,"lucida sans unicode",tahoma,sans-serif;
    font-size: 13px;
    color: #3d464d;
    font-weight: normal;
}
* {
    font-weight: inherit;
}
/*Hintergrund Ordner Dateien Links
**********************************/
.maestro .maestro-tab-nav .c-tabs__bar {
    position: fixed;
    z-index: 5;
    background: #212121;
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
    width: calc(100% - 560px);
    min-width: 464px;
    height: auto;
    margin-top: 0;
    line-height: 39px;
}
/*Hintergrund Dateine Kopfzeile
*******************************/
.brws-column-header .brws-checkbox-cell {
    background-color: #212121;
    margin-right: 16px;
}
/*Hintergrund Dateiliste
************************/
.mc-table-row {
    border-bottom: 1px solid #2077CE;
    background: #212121;
}
.mc-table, .mc-table-head, .mc-table-body, .mc-table-row, .mc-table-cell {
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
}
/*Textfarbe Dateiliste
**********************/
.mc-table {
    font-family: 'AtlasGrotesk', sans-serif;
    color: #637282;
}
/*Seitenleiste
**************/
.maestro-nav__container {
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    -webkit-box-sizing: border-box;
    box-sizing: border-box;
    width: 240px;
    min-width: 240px;
    height: 100%;
    background: #171717;
    -webkit-box-orient: vertical;
    -webkit-box-direction: normal;
    -ms-flex-direction: column;
    flex-direction: column;
    -webkit-box-pack: justify;
    -ms-flex-pack: justify;
    justify-content: space-between;
    position: absolute;
}
/*Seitenleiste Privat
*********************/
.maestro-nav-switcher-button {
    display: block;
    padding: 0;
    height: 100%;
    background: #171717;
}
* {
    font-weight: inherit;
}
/*Privacy
*********/
#page-footer {
    position: fixed;
    bottom: 0;
    margin-left: -8px;
    padding: 0 13px 10px;
    text-align: left;
    display: inline-block;
    background: #212121;
    border-radius: 3px;
}
}
