css:
@-moz-document domain("blackboard.bsu.edu") {
* {
    background: none !important;
    color: black !important;
    box-shadow: none !important;
    font-family: Gotham, Raleway !important;
    text-shadow: none !important;
}

a {
    color:#BA0C2F !important;
}

#globalNavPageNavArea {
 	background:#BA0C2F url(http://cms.bsu.edu/-/media/www/images/logos/ballstatelogo-sm-white.png) 12px bottom/auto 100% no-repeat !important; 
}

#globalNavPageNavArea * {
    color:white !important;
}

.global-nav-bar-wrap, .actionBar, .brandingImgWrap, [id*=_287_1], [id*=_243_1], .appTabs .hideoff, #courseMenuPalette .actionBarMicro, #copyright, .announcementInfo {
 	display:none !important;   
}

.navPaletteTitle h3:first-of-type {
    display:none;
}

.navPaletteTitle h3 {
 	position:static !important;   
}


.navPaletteTitle h3 a {
    position:static !important;
 	display:block !important;   
}

.navPaletteContent h3 a.submenuLink:before, .navPaletteContent h3 a.submenuLink_active:before {
    font-size:48px !important;
    color:#BA0C2F !important;
    display:block;
    text-align:center;
    width:auto;
    margin:0 !important;
}

.topTabs {
    height:auto !important;
}

.contentBox {
    border:none;
}

.container {
    padding:0 !important;
}

[id*=_628_1] {
    margin:0 !important;
}

.navPaletteContent a.comboLink, .navPaletteContent a.comboLink_active, .listCm .courseMenu a, .buttonCm .courseMenu a {
    padding-left:12px;
}

#contentPanel.contentPane {
    margin-left:250px;
}

#pageHeader, #schemePreviewHead, .pageTitle {
    padding-left:0;
    padding-right:0;
}

.localViewToggle a.active, div.localViewToggle h2 {
    border:none;
}

.localViewToggle a.active::before, div.localViewToggle h2::before, .localViewToggle a.active::after, div.localViewToggle h2::after {
 	content:none;   
}

.contentList, .buildList, .contentListPlain, .announcementList {
    margin:0 !important;
}

.contentList>li, .buildList>li, .contentListPlain>li, .announcementList>li, .contentPageItem {
    padding:0;
}

.coursePath a {
    height:auto !important;
    width:auto !important;
}

.coursePath a::before {
    content:none !important;
}

.item_icon {
 	left:0 !important;   
 	top:0 !important; 
    filter: grayscale(1);
}

.listCm .courseMenu>li:hover a span, .listCm .courseMenu a:hover, .listCm .courseMenu a:focus, .listCm .courseMenu a:hover span, .listCm .courseMenu a:focus span {
 	color:#BA0C2F !important;   
}

.containerPortal {
 	padding:0 !important;
}

#topTab, table.appTabs tbody tr td a, div.paneTabs ul li a, div.paneTabs ul li.external a, div.paneTabs ul li.personal a {
 	border:none !important;   
}
}
