css:
@-moz-document domain("www.douyu.com") {
.pay-task,
#js-stats-and-actions div,
#qrcode-content,
#js-stats-and-actions,
#js-live-room-normal-equal-right,
#js-live-room-normal-equal-left,
#js-recommand
{
	display: none;
}

#js-stats-and-actions .action-list {
	position: fixed;
    top: -100px;
}
#share-content{
	border:none;
}
#header .head-oth .o-search {
    opacity: .7;
}
/*
直播房顶部
*/
.room-mes {
	height: 100px;
}
.anchor-pic {
	background: rgba(0, 0, 0, .04) !important;
    border: none !important;
	padding: 4px !important;
    transition-property: box-shadow;
	border-radius: 2px;
}
/*
#js-live-room-normal-right,
	opacity: 0;
	position: fixed;
	top: -10000px
右侧弹幕栏*/
#js-chat-cont
{
	opacity: 0;
	top: -11007px;
    position: relative;
	max-height: 0;
}
#js-live-room-normal-right .chat {
	overflow: inherit;
}

#js-live-room-normal-right{
	float: none;
}
#js-fans-rank,
.fishop-anchor-recommands-box.expanded
{
	display: none;
}
.chat-feat textarea.cs-textarea {
	width: 80%;
}
.chat-feat .b-btn {
	width: 10%;
}
#js-live-room-normal-right .chat {
	top: -10px;
}
#js-chat-speak {
	box-shadow: rgba(0, 0, 0, .1) 0px 0px 3px 3px;
	border-radius: 2px;
}
/*
左侧视频
*/
div#js-live-room-normal-left {
	float: none;
	width: 100%;
}
.room-mes .headline {
	min-width: 100%;
}
#js-room-video {
	box-shadow: rgba(0, 0, 0, .2) 0px 0px 3px 3px;
	border-radius: 2px;
} 
#anchor-info {
	box-shadow: rgba(0, 0, 0, .1) 0px 0px 3px 3px;
}

}
