css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("fanyi.youdao.com") {
/*输入栏 */
  ::-moz-placeholder
  {font-size: 0px !important;}
  /*登录，人工翻译，右侧边，安装手机版，下侧产品项目*/
  .fanyi__nav__list,
  #transMan,
  .side__nav,
  .download__area--fanyiguan,
  .inside__products
  {display: none !important;}
  /*框内元素*/
  #targetStar,
  #updateResult,
  .resource,
  .suggest__update__con
  {display: none !important;} 
  .input__original__area,
  .input__target__text
  {font-size: 20px !important;}
  .input__target__text--small
  { line-height: 23px !important;}
}

@-moz-document domain("dict.youdao.com"), domain("www.youdao.com") {
/*输入栏 */
  ::-moz-placeholder
  {font-size: 0px !important;} 
  /*顶栏，下载，精选，分割线*/ 
  #nav,
  #margin3,
  #doc2,
  #kf-my-tips
  {display: none !important;}
  /*词典首页调整*/    
  body
   {width: 100% !important;
    min-height: 1px !important; 
    overflow-y: auto !important; }  
  #footer, #footer.set-bottom
   {position: fixed;
    bottom: 0;
    background-color: #f6f6f9;
    padding: 30px 0;
    font-size: 12px;
    color: #929297;} 
     
   /*词典页面
    登录，顶栏，右侧推荐，右侧产品项目，左侧帮助，其他秒懂，遮挡页面，单词本*/
  .hd,
  .bd,
  .dict-votebar,
  .ugc-rank,
  .c-topbar.c-subtopbar,
  .dialog-guide-download.win-download,
  #wordbook   
  {display: none !important;}
  /*词典布局调整*/
  .c-topbar-wrapper 
  {height: 91px !important;
   position: fixed;
   top: 0;} 
  #scontainer 
  {margin-top: 100px !important;
   padding-top: 0 !important;} 
  .result_navigator 
  {margin-top: 0 !important;}
}
