css:
@-moz-document domain(trashbox.ru) {
header #div_top_logo_header .div_cell:not(.cell2){
    position: relative;
}

header #div_top_logo_header .div_cell:not(.cell2):after{
    content: '';

    display: block;

    width: 100%;
    height: 100%;

    position: absolute;
    top: 0;
    left: 0;

    background-color: rgb(245, 121, 35);
    background-image: url('http://toplogos.ru/images/logo-xiaomi.png');
    background-size: cover;
    background-position: center;
    background-repeat: no-repeat;
}

/* links */

a, a:visited{
    color: #f57923;
}

a:hover{
    color: #fff;
    background-color: #f57923;
}

/* primary color */

#div_top_logo .cell2,
#div_top_menu_text a.current,
.div_top_black_buttons,
#div_top_menu_text a:hover,
.div_top_news_highlight,
#div_add_push_notify,
.div_topic_votes_buttons tr > td:last-child,
.div_topic_votes_bar tbody tr td,
#div_user_menu a:hover,
/* ui begin */
.div_panel_caption,
.black a:hover, a.black:hover,
/* ui end */
/*index begin*/
.zero_space_padding2,
.div_index_topics_more,
/*index end*/
/* forum begin */
.div_votes_control tr .div_comment_votes_buttons:last-child td
/* forum end */{
    background: #f57923 !important;
}

.div_panel_digest_top,
.div_gallery_pointer,
#div_menu_page:after,
.div_panel_bordered_50,
/* buttns */
.div_auth_input,
.div_auth_button_submit,
/*index begin*/
.div_topic_cover_caption,
.div_panel_bordered_50,
.div_panel_bordered,
.div_most_commented_item
/*index end*/{
    border-color: #f57923 !important;
}

/* index begin */
.zero_space_padding2
/* index end */{
    box-shadow: 0px 0px 0px 1px #f57923 !important;
    -webkit-box-shadow: 0px 0px 0px 1px #f57923 !important;
}

/* other */

.div_panel_more a,
.div_right_rec_text .div_topic_cover_caption,
.span_good_medals{
    color: #f57923 !important;
}

.div_panel_more a:hover,
.div_topic_day:hover span,
.div_most_commented_item a:hover span,
#div_user_menu a:hover .dark,
/* buttons begin */
.div_sumbenu_item,
.blue a, a .blue, a:visited .blue, .blue a:visited,
.div_auth_button_submit,
/* buttons end */
/* forum begin */
.a_forum_answer, .a_forum_answer:visited,
.div_forum_tags a, .div_forum_tags a:visited,
.div_tags_menu a:hover,
.div_tags_menu_top .a
/* forum end */{
    color: #fff !important;
    background-color: #f57923;
}

.td_rank_pos > div{
    color: #fff !important;
    background-color: #f57923 !important;
}

.td_rank_pos a{
    color: #fff !important;
    background: transparent;
}

.td_rank_pos a:hover{
    background: #ff6800 !important;
}

/* very primary azazaz (forum) */

.a_forum_answer:hover, .div_forum_tags a:hover,
/* buttons begin */
.div_sumbenu_item_active,
.blue a:hover, a:hover .blue, a.blue:hover
/* buttons end */{
    background-color: #ff6800 !important;
}

/* no-primary */

.div_index_topics_more,
.div_comment.highlighted_comment{
    border-color: #ffcca9 !important;
}

.div_comment.highlighted_comment:hover{
    border-color: #f57923 !important;
}

/* remove (kebab) */

.div_most_commented_item a{
    background: transparent !important;
}

.span_new_comment_button {
    color: #f57923;
}

}
